-- ----------------------------
-- Table structure for `ni_about`
-- ----------------------------
DROP TABLE IF EXISTS `ni_about`;
CREATE TABLE `ni_about` (
  `about_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `about_name` varchar(60) NOT NULL COMMENT '姓名',
  `about_net_name` varchar(60) NOT NULL COMMENT '网名',
  `about_birthday` date NOT NULL COMMENT '生日',
  `about_native_place_province` varchar(30) NOT NULL COMMENT '籍贯省',
  `about_native_place_city` varchar(30) NOT NULL COMMENT '籍贯市',
  `about_native_place_dist` varchar(30) NOT NULL COMMENT '籍贯区',
  `about_present_address_province` varchar(30) NOT NULL COMMENT '现居省',
  `about_present_address_city` varchar(30) NOT NULL COMMENT '现居市',
  `about_present_address_dist` varchar(30) NOT NULL COMMENT '现居区',
  `about_job` varchar(30) NOT NULL COMMENT '职业',
  `about_favorite_book` varchar(90) NOT NULL COMMENT '喜欢的书',
  `about_favorite_music` varchar(90) NOT NULL COMMENT '喜欢的音乐',
  `about_favorite_star` varchar(90) NOT NULL,
  `about_domain_name` varchar(90) NOT NULL COMMENT '域名',
  `about_server` varchar(60) NOT NULL COMMENT '服务器',
  `about_record` varchar(60) NOT NULL COMMENT '备案号',
  `about_procedure` varchar(60) NOT NULL COMMENT '程序',
  `about_content` text NOT NULL COMMENT '内容',
  PRIMARY KEY (`about_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ni_about
-- ----------------------------

