-- Table structure for `ni_admin`
-- ----------------------------
DROP TABLE IF EXISTS `ni_admin`;
CREATE TABLE `ni_admin` (
  `admin_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `admin_account` varchar(64) NOT NULL COMMENT '管理员帐号',
  `admin_name` varchar(64) NOT NULL COMMENT '管理员名称',
  `admin_pwd` varchar(64) NOT NULL COMMENT '管理员密码',
  `last_login_time` datetime NOT NULL COMMENT '最后登录时间',
  `role_id` int(2) NOT NULL COMMENT '对应的角色id',
  `admin_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '启用：0 禁用：1',
  PRIMARY KEY (`admin_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

