-- ----------------------------
-- Table structure for `ni_article`
-- ----------------------------
DROP TABLE IF EXISTS `ni_article`;
CREATE TABLE `ni_article` (
  `article_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `article_title` varchar(128) NOT NULL COMMENT '文章标题',
  `article_origin` varchar(30) NOT NULL COMMENT '文章来源',
  `article_programa` smallint(5) NOT NULL COMMENT '文章栏目',
  `article_hit` int(10) NOT NULL DEFAULT '0' COMMENT '点击量',
  `article_pic` varchar(255) NOT NULL COMMENT '文章封面图',
  `article_datetime` datetime NOT NULL DEFAULT '2000-01-01 00:00:00' COMMENT '文章发表时间',
  `article_intro` varchar(360) NOT NULL COMMENT '文章简介',
  `article_content` longtext NOT NULL COMMENT '文章内容',
  `article_top` int(10) NOT NULL DEFAULT '0' COMMENT '是否置顶 0：否 其他值：是',
  PRIMARY KEY (`article_id`),
  KEY `article_programa_rank` (`article_programa`)
) ENGINE=InnoDB AUTO_INCREMENT=194 DEFAULT CHARSET=utf8;

