-- Table structure for `ni_auth`
-- ----------------------------
DROP TABLE IF EXISTS `ni_auth`;
CREATE TABLE `ni_auth` (
  `auth_id` smallint(6) unsigned NOT NULL AUTO_INCREMENT COMMENT '栏目id',
  `auth_name` varchar(60) NOT NULL COMMENT '权限名称',
  `auth_pid` smallint(6) NOT NULL COMMENT '父id',
  `auth_c` varchar(80) NOT NULL DEFAULT '' COMMENT '控制器',
  `auth_a` varchar(80) NOT NULL DEFAULT '' COMMENT '操作方法action',
  `auth_ico` varchar(30) NOT NULL COMMENT '导航图标。一级导航才有；',
  `auth_path` varchar(32) NOT NULL COMMENT '全路径',
  `auth_level` tinyint(3) NOT NULL DEFAULT '0' COMMENT '级别',
  `auth_rank` int(5) NOT NULL DEFAULT '0' COMMENT '权限排序，越大越后面',
  PRIMARY KEY (`auth_id`)
) ENGINE=InnoDB AUTO_INCREMENT=196 DEFAULT CHARSET=utf8;

