  `column_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主页专栏id',
  `column_name` varchar(60) NOT NULL COMMENT '栏目名称',
  `column_pid` int(11) NOT NULL COMMENT '栏目父id',
  `column_c` varchar(30) NOT NULL DEFAULT '' COMMENT '控制器',
  `column_a` varchar(30) NOT NULL DEFAULT '' COMMENT '操作方法',
  `column_path` varchar(32) NOT NULL COMMENT '全路径',
  `column_intro` text NOT NULL COMMENT '栏目简介',
