  `column_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:开启 1:禁用',
  `column_rank` int(5) DEFAULT '0' COMMENT '主页栏目的排序，越大越后面',
  PRIMARY KEY (`column_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

