p://www.catswhocode.com/blog&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;r&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;while&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(!&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;feof&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$fp&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) ){ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$page&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;.= &lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;fgets&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$fp&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, 4096); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$titre&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;eregi&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;title&amp;gt;(.*)&amp;lt;/title&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$page&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$regs&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$regs&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[1]; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;fclose(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$fp&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;解释 Apache 日志&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;大多数网站使用的都是著名的Apache服务器，如果你的网站也是，那么使用PHP正则表达式解析 apache 服务器日志 怎么样？&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//Logs: Apache web server &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//Successful hits to HTML files only. Useful for counting the number of page views. &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;^((?#client IP or domain name)S+)s+((?#basic authentication)S+s+S+)s+[((?#date and time)[^]]+)]s+&amp;quot;(?:GET|POST|HEAD) ((?#file)/[^ ?&amp;quot;]+?.html?)??((?#parameters)[^ ?&amp;quot;]+)? HTTP/[0-9.]+&amp;quot;s+(?#status code)200s+((?#bytes transferred)[-0-9]+)s+&amp;quot;((?#referrer)[^&amp;quot;]*)&amp;quot;s+&amp;quot;((?#user agent)[^&amp;quot;]*)&amp;quot;$&amp;#39;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//Logs: Apache web server &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//404 errors only &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;^((?#client IP or domain name)S+)s+((?#basic authentication)S+s+S+)s+[((?#date and time)[^]]+)]s+&amp;quot;(?:GET|POST|HEAD) ((?#file)[^ ?&amp;quot;]+)??((?#parameters)[^ ?&amp;quot;]+)? HTTP/[0-9.]+&amp;quot;s+(?#status code)404s+((?#bytes transferred)[-0-9]+)s+&amp;quot;((?#referrer)[^&amp;quot;]*)&amp;quot;s+&amp;quot;((?#user agent)[^&amp;quot;]*)&amp;quot;$&amp;#39;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;使用智能引号代替双引号&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;如果你是一个印刷爱好者，你将喜欢这个允许用智能引号代替双引号的正则表达式，这个正则被WORDPRESS在其内容上使用&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;preg_replace(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;B&amp;quot;b([^&amp;quot;x84x93x94rn]+)b&amp;quot;B&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;?1?&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$text&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;检验密码的复杂度&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;这个正则表达式将检测输入的内容是否包含6个或更多字母，数字，下划线和连字符. 输入必须包含至少一个大写字母，一个小写字母和一个数字&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;A(?=[-_a-zA-Z0-9]*?[A-Z])(?=[-_a-zA-Z0-9]*?[a-z])(?=[-_a-zA-Z0-9]*?[0-9])[-_a-zA-Z0-9]{6,}z&amp;#39;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;WordPress: 使用正则获得帖子上的图片&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我知道很多人是WORDPRESS的使用者，你可能会喜欢并且愿意使用 那些从帖子的内容检索下来的图像代码。使用这个代码在你的BLOG只需要复制下面代码到你的某个文件里&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;13&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;14&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;15&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;16&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;17&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;18&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;19&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;20&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;21&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;22&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;23&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;24&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(have_posts()) : ?&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;while&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(have_posts()) : the_post(); ?&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$szPostContent&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$post&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;-&amp;gt;post_content; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$szSearchPattern&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;~&amp;lt;img [^&amp;gt;]* /&amp;gt;~&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// Run preg_match_all to grab all the images and save the results in $aPics &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;preg_match_all( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$szSearchPattern&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$szPostContent&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$aPics&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// Check to see if we have at least 1 image &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$iNumberOfPics&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;count&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$aPics&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[0]); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$iNumberOfPics&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;&amp;gt; 0 ) { &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;// Now here you would do whatever you need to do with the images &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;// For this example the images are just displayed &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;for&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=0; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;&amp;lt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$iNumberOfPics&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;++ ) { &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$aPics&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[0][&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;]; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;}; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;endwhile&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;endif&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;自动生成笑脸图案&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;被WordPress使用的另一个方法, 这段代码可使你把图像自动更换一个笑脸符号&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$texte&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;A text with a smiley &amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php functions&quot;&gt;str_replace&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;:-)&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;&amp;lt;img src=&amp;quot;smileys/souriant.png&amp;quot;&amp;gt;&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$texte&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;移除图片的链接&lt;/strong&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;meta http-equiv=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Content-Type&amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;content=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;text/html; charset=utf-8&amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;/&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$str&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &amp;#39; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;a href=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;http://www.jobbole.com/&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;gt;jobbole&amp;lt;/a&amp;gt;其他字符 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;a href=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;http://www.sohu.com/&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;gt;sohu&amp;lt;/a&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;a href=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;http://www.sohu.com/&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;gt;&amp;lt;img src=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;http://www.fashion-press.net/img/news/3176/mot_06.jpg&amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;/&amp;gt;&amp;lt;/a&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;#39;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;//echo preg_replace(&amp;quot;/(&amp;lt;a.*?&amp;gt;)(&amp;lt;img.*?&amp;gt;)(&amp;lt;\\/a&amp;gt;)/&amp;quot;, &amp;#39;$2&amp;#39;, $str);&amp;nbsp; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;preg_replace(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;/(&amp;lt;a.*?&amp;gt;)(&amp;lt;img.*?&amp;gt;)(&amp;lt;\\/a&amp;gt;)/&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;\\2&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$str&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&amp;nbsp; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;以上就是15个超实用的php正则表达式，希望对大家的学习有所帮助。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('65', '程序出错后，程序员给测试人员的20条高频回复', '伯乐在线', '1', '59', '/Upload/article/56d6aa7b682cc.jpg', '2016-03-02 16:55:23', '编者按：程序员和软件测试员之间的关系无须多言。这些经典回复是国外程序员总结分享的，“全球通用”。', '&lt;p&gt;编者按：程序员和软件测试员之间的关系无须多言。这些经典回复是国外程序员总结分享的，“全球通用”。&lt;/p&gt;&lt;p&gt;20. “That’s weird…” 很奇怪……&lt;/p&gt;&lt;p&gt;19. “It’s never done that before.” 以前没这样过的。&lt;/p&gt;&lt;p&gt;18. “It worked yesterday.” 昨天还好好的。&lt;/p&gt;&lt;p&gt;17. “How is that possible?” 那怎么可能？（怎么会出问题？）&lt;/p&gt;&lt;p&gt;16. “It must be a hardware problem.” 这一定是硬件问题。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;15. “What did you type in wrong to get it to crash?” 你输入什么东西后才崩溃的？&lt;/p&gt;&lt;p&gt;14. “There is something funky in your data.” 你的数据有问题。&lt;/p&gt;&lt;p&gt;13. “I haven’t touched that module in weeks!” 我好几个礼拜没动那个程序了！&lt;/p&gt;&lt;p&gt;12. “You must have the wrong version.” 你一定在用错误的版本。&lt;/p&gt;&lt;p&gt;11. “It’s just some unlucky coincidence.” 这只是凑巧。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;10. “I can’t test everything!” 我无法测试所有东西。（我的机器环境下，无法测试所有的可能情况。）&lt;/p&gt;&lt;p&gt;09. “THIS can’t be the source of THAT.” “这”不可能是问题的原因。&lt;/p&gt;&lt;p&gt;08. “It works, but it hasn’t been tested.” 程序能用，不过还没有测试。&lt;/p&gt;&lt;p&gt;07. “Somebody must have changed my code.” 一定有人改了我的代码。&lt;/p&gt;&lt;p&gt;06. “Did you check for a virus on your system?” 你的电脑扫描病毒了么？&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;05. “Even though it doesn’t work, how does it feel? 即便程序不行了，（你觉得）程序写得如何？&lt;/p&gt;&lt;p&gt;04. “You can’t use that version on your system.” 你不能在你系统上使用那个版本的程序。（程序版本和系统有冲突。）&lt;/p&gt;&lt;p&gt;03. “Why do you want to do it that way?” 你怎么会想着那样操作啊？&lt;/p&gt;&lt;p&gt;02. “Where were you when the program blew up?” 程序崩溃时，你在做什么呢？（做了哪些操作？）&lt;/p&gt;&lt;p&gt;第1条会是什么？猜猜看吧！:)&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;_&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;01. “It works on my machine” &amp;nbsp; 在我机器上好好的！！！（潜台词：怎么在你那就出问题了呢！！！）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;编者后话&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;虽然已经远离编程，但经常还是会用到第20、19条，当然也不会“错过”第1条。各位看过之后，不知你都用上了哪几条哦~&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('66', '有关“非计算机专业如何转行做程序员”的一点思考', '伯乐在线', '17', '66', '/Upload/article/56d6aafc31af9.jpg', '2016-03-02 16:57:32', 'XXXX：员员好，我是12届的大学生，读了一所还可以的大学，但所学专业我很不喜 欢，我因为性格较内向同时又感叹编程的神奇，所以很想进入这一行，但 我去面试几家后发现就算是实习岗位也需要一些基础，虽说大学学过C语言但感觉也基本不会用，那些面试单位是建议我去学习这方面知识再去找他', '&lt;p&gt;今天老夫半夜爬起来等着看世界杯预选赛法国vs西班牙的比赛，在新浪微博里看到&lt;a href=&quot;http://weibo.com/u/2093492691&quot; target=&quot;_blank&quot;&gt;@程序员的那些事&lt;/a&gt; 发的&lt;a href=&quot;http://weibo.com/2093492691/zph0amB2b&quot; target=&quot;_blank&quot;&gt;一封粉丝来信&lt;/a&gt;如下：&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #888888&quot;&gt;XXXX：员员好，我是12届的大学生，读了一所还可以的大学，但所学专业我很不喜\r\n欢，我因为性格较内向同时又感叹编程的神奇，所以很想进入这一行，但 \r\n我去面试几家后发现就算是实习岗位也需要一些基础，虽说大学学过C语言但感觉也基本不会用，那些面试单位是建议我去学习这方面知识再去找他，但我又对这些\r\n \r\n培训机构不太放心，怕花费了一大笔学费结果却不好，现在我有些迷茫不知道该去找找其它工作（人才市场很多都是些销售和工厂的一些岗位）还是去学习，希望广\r\n 大的员员们给我些指导，非常感谢。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;看完还是有一些感触的。作为一个科班计算机系毕业，第一份工作就是软件开发，然后又在咨询和销售领域工作了很多年，再回归到开发领域的老码农，老夫\r\n对于该同学的纠结心情是非常理解的。但是，要把一份工作持续地做下去，光有热情是不够的，如果你总是不能把事情做得比你身边的人更好，你的热情就会慢慢地\r\n消退，会开始怀疑自己的决定是否正确，自己的坚持是否有意义。所以，我的建议是给自己一段时间打好基础，也借此过程验证自己的决定是否明智，就算最后的答\r\n案是否定的，学会一些编程的技术也会对培养自己分析问题的思路有所帮助。&lt;/p&gt;&lt;p&gt;好了，说到正题，非计算机专业的同学如何为成为一个程序员打基础呢？我建议走如下的步骤：&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;1. 学会一门编程语言。随便什么语言都可以，其实python很适合初学者，有本书叫《与孩子一起学编程》用的就是它。不过考虑到步骤2，还是选个流行的教学语言吧，比如C++或者Java。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;2. 学好离散数学，重点是高等代数、数理逻辑、集合论、图论，最好再学会概率论。计算机科学的本质是离散数学，不掌握这些知识你如何能真正理解矢量、逻辑运算、最短路径、涂色算法这些概念呢？&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;3. 非常非常非常非常非常非常认真地学习《数据结构》这门课。你作为程序员功力几何，很大程度上取决于你在这方面的造诣。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;4. 自己做一个小项目，尽可能把你学会的东西都用上。这不仅能向招聘者展示你的功力，也是一个把所学知识融会贯通的过程，只有做了一个项目，你学的知识才真正变成为了你掌握的技术。&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;有人看到这里可能要说了，码农对编程语言熟悉就行了，学那么多不相干的又何必呢？&lt;/p&gt;&lt;p&gt;老夫打个不太恰当的比方，离散数学和数据结构就像一个武林高手的内功，编程语言则是招式。内功不行，就算会很多招式，可以吓唬吓唬武功一般的人，但\r\n是碰到打硬仗这些花拳绣腿就不管用，会死得很惨。可惜现在很多人不重视内功，不少计算机专业毕业生连堆栈和队列是怎么回事都不知道，更别提二叉树、链表、\r\n图结构了。不管碰到什么需求，上来就定义几个大数组，你能指望他编出多高效的代码？比如现在互联网热门的大数据，算法效率是核心问题，如果谁整出个\r\nO(n^2)的算法，信不信老板分分钟砍死他？&lt;/p&gt;&lt;p&gt;再说远一点，学会一门编程语言容易，精通很难，难就难在理解它的精髓。有很多开发者热衷于最新潮的技术，今天玩玩NoSQL，明天搞搞Html \r\n5，说起来头头是道，但是都深入不下去，从来没有真正没有理解这些技术的精髓，也就做不出一个能发挥出它们真正优势的产品来。这往往就是因为内功不行而心\r\n态又很浮躁。（补充一篇文章：《&lt;a href=&quot;http://blog.jobbole.com/499/&quot; target=&quot;_blank&quot;&gt;叶正盛：再次写给我们这些浮躁的程序员&lt;/a&gt;》）\r\n但是程序员是靠代码说话的，只是善于对某个技术侃侃而谈没有任何价值。Linux和Git之父Linus Torvalds说过：“Talk is \r\ncheap. Show me the \r\ncode.”（空谈无价值。拿你的代码来看看。）所以老夫要说，作为一个程序员，不先练好内功就写不出优雅的代码，这样是走不远的。&lt;/p&gt;&lt;p&gt;怎么学呢？这些不需要花很多钱去培训机构，而且从我面试过的很多培训学校学员的表现来看，绝大部分人学完了连MVC的概念都没搞明白，花半年时间就\r\n学会了if…else，何必去糟蹋这钱呢？现在一些大学有旁听的学分课程，花几百块钱就可以听一门课，还可以交作业做实验（比\r\n如：http://zhidao.baidu.com/question/245256206.html）。建议你尽可能去选最好的学校，不仅仅是老师水\r\n平高，关键是同学的高水平会促使你对自己有更高要求，这和培训学校的氛围是完全不同的。&lt;/p&gt;&lt;p&gt;如果你在以上4步都做得很好，那么你应该可以在码农这个行业获得一些机会，剩下的就看你在工作中的表现和积累了。码农的修行是一个长期过程，计算机\r\n领域每年都有新技术出现。做了码农，就得活到老学到老，注定一辈子苦兮兮的。如果你喜欢这种感觉，那么你的选择可能就是正确的。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('67', '趣文：表白后女生发给我一串五层加密的密码', '伯乐在线', '17', '67', '/Upload/article/56d6abbaa6a7b.jpg', '2016-03-02 17:00:42', '最近和一个心仪的女生告白， 谁知道她给了一个摩斯密码给我，说解出来了才答应和我约会。 可是我用尽了所有方法都解不开这个密码。。好郁闷阿。只能求教你们了', '&lt;p&gt;最近和一个心仪的女生告白， 谁知道她给了一个摩斯密码给我，说解出来了才答应和我约会。 可是我用尽了所有方法都解不开这个密码。。好郁闷阿。只能求教你们了。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color: #ff0000&quot;&gt;****-/*—-/—-*/****-/****-/*—-/—**/*—-/****-/*—-/-****/***–/****-/*—-/—-*/**—/-****/**—/**—/***–/–***/****-/&lt;/span&gt; &lt;/strong&gt;&lt;/p&gt;&lt;p&gt;她唯一给我的提示就是这个是5层加密的密码……也就是说要破解5层密码才是答案……好郁闷阿.. 救救我吧..&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://tieba.baidu.com/i/19924404?st_mod=pb&amp;fr=tb0_forum&amp;st_type=uname&quot; target=&quot;_blank&quot;&gt;HighnessC&lt;/a&gt;&amp;nbsp;：&lt;/p&gt;&lt;p&gt;补充一下，我记得她还说过最终语言是英语。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;6楼 PorscheL ：&lt;/p&gt;&lt;p&gt;首先要把摩斯密码给解开来。应该是：&lt;span style=&quot;color: #ff0000&quot;&gt;&lt;strong&gt;4194418141634192622374&lt;/strong&gt;&lt;/span&gt; 然后呢? 然后我再想想。数字的话应该下一步是代入?或者是那个啥&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://tieba.baidu.com/i/19924404?st_mod=pb&amp;fr=tb0_forum&amp;st_type=uname&quot; target=&quot;_blank&quot;&gt;HighnessC&lt;/a&gt; ：&lt;/p&gt;&lt;p&gt;经过昨天一晚的奋斗，我还是破解不了。 不过今天我死磨她，叫她给提示的后，她说途中有一个步骤是”替代密码”而密码表则是我们人类每天都可能用到的东西。我会再套多点讯息的……希望大大帮忙解答阿……毕竟我也希望不要她亲口说出来这个密码的答案..&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;28楼 巨蟹座的传说 ：&lt;/p&gt;&lt;p&gt;4194418141634192622374&lt;br/&gt;毫无规律……&lt;br/&gt;daiddahadafcdaibfbbcgd&lt;br/&gt;仍然不对……&lt;br/&gt;d4/a1/i9/d4/d4/……&lt;br/&gt;……不对&lt;br/&gt;对了，先来考虑考虑替换密码。楼主顺便看看能不能套出每一层密码的提示。这个很重要。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;38楼：幻之皮卡丘&lt;/p&gt;&lt;p&gt;数字有偶数个，且注意到41组合出现数次。&lt;br/&gt;于是分组：&lt;span style=&quot;color: #0000ff&quot;&gt;&lt;strong&gt;41 94 41 81 41 63 41 92 62 23 74&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;注意个位数总是1~4&lt;br/&gt;于是颠倒：14 49 14 18 14 36 14 29 26 32 47&lt;br/&gt;翻成英文字母，前26大写后26小写：NwNRNjNcZfu&lt;br/&gt;然后卡住了……按大小写分别穷举换位也没看出结果&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;83楼：片翌天使&lt;/p&gt;&lt;p&gt;38楼给我的提示。还是让我想到了手机。你们看解码出来分组后的数字分别是：&lt;span style=&quot;color: #0000ff&quot;&gt;&lt;strong&gt;41 94 41 81 41 63 41 92 62 23 74&lt;/strong&gt; &lt;/span&gt;。并且每个组合个位数都不超过4。特别是除了十位数是7和9这两个数字后面有4以外其他的都没有4。看图。&lt;/p&gt;&lt;p&gt;手机的键盘上7和9的字母也是4个的，其他的都不超过4个字母。那么，按照上面的组合解码可得：&lt;strong&gt;G Z G T G O G X N C S&lt;/strong&gt;&lt;br/&gt;解码后我们发现每一组数字都能解释出来。目前我只能解码到这里的。虽然很不通顺，但我相信这里应该是个突破点。&lt;/p&gt;&lt;p&gt;LZ还是快点去套多点提示给我们吧&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;93楼：巨蟹座的传说&lt;/p&gt;&lt;p&gt;4194418141634192622374&lt;br/&gt;rqorrqiqrqyerqowywweur？&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;115楼：ybaba&lt;/p&gt;&lt;p&gt;4194418141634192622374&lt;/p&gt;&lt;p&gt;大家注意一下这组数字 最大的是数字的范围是1-9。手机上一共有10个数字按键 其中0和1不对应任何字母，所以不存在0 \r\n而为什么会有1呢？大家在仔细看一下，如果将数字从左到右编序号，12345直到22。有没有发现奇数编号对应的数字没有1，所以奇数编号的数字应该是对\r\n应有字母的手机按键。而所有偶数编号对应的数字都是1234。&lt;/p&gt;&lt;p&gt;为什么只有1234？大家再看看手机，所有有字母的按键 \r\n最多只有4个字母，所以偶数编号的字母对应的是按键上的第几个字母。按照这个方法我们来看下这串数字 \r\nxx94xxxxxxx74。这是仅有的两个偶数编号的4 相对应9和7。你们再看看手机 只有按键9和7才对应4个字母，其它的都只对应3个。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;（重点来了！）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;207楼：片翌天使&lt;/p&gt;&lt;p&gt;我重新整合了这个解密步骤。如下：&lt;/p&gt;&lt;p&gt;38楼给我的提示。还是让我想到了手机。你们看解码出来分组后的数字分别是：&lt;strong&gt;41 94 41 81 41 63 41 92 62 23 74&lt;/strong&gt; 。并且每个组合个位数都不超过4。特别是除了十位数是7和9这两个数字后面有4以外其他的都没有4。看图。&lt;/p&gt;&lt;p&gt;手机的键盘上7和9的字母也是4个的，其他的都不超过4个字母。那么，按照上面的组合解码可得：G Z G T G O G X N C S 。解码后我们发现每一组数字都能解释出来。目前我只能解码到这里的。虽然很不通顺，但我相信这里应该是个突破点。&lt;/p&gt;&lt;p&gt;93楼的键盘代入也给了我很大的提示。包括楼主的“她说途中有一个步骤是”替代密码”而密码表则是我们人类每天都可能用到的东西”。那么很可能就是电脑键盘。因为QWE的格式是被世人所认可的，也就有可能成为密码的码表。码表 QWE=ABC依次类推。&lt;/p&gt;&lt;p&gt;那么解码：G Z G T G O G X N C S&lt;br/&gt;就是G=O Z=T G=O T=E G=O O=I G=O X=U N=Y C=V S=L&lt;br/&gt;翻译过来就是 O T O E O I O U Y V L&lt;/p&gt;&lt;p&gt;其实到这里就已经大概知道答案是什么了。按照逻辑来整理，只要你重组这些字母的时候你就看到了。虽然重组有很多种例如，LV IOU ET OOO 。可是这些没有语言性质。因为楼主说过最终答案是英语。&lt;/p&gt;&lt;p&gt;所以唯一一个符合逻辑的答案应该是I LOVE YOU TOO。可是我还不确定。因为这里我才解了第3层就已经看出答案了。&lt;/p&gt;&lt;p&gt;所以我决定用O T O E O I O U Y V L。进行再次解码。我用了很多种解码方法。凯撒，乘法等等……最后我发现了只有栅栏才能读得通。分成两排：&lt;br/&gt;O T O E O I&lt;br/&gt;O U Y V L&lt;br/&gt;重组后：OOTUOYEVOLI。&lt;br/&gt;然后倒序：ILOVEYOUTOO.&lt;br/&gt;I LOVE YOU TOO 就自然而然的出来了。&lt;br/&gt;最后我才肯定密码就是&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #ff0000&quot;&gt;&lt;strong&gt;I LOVE YOU TOO.&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;最后的最后，我敢肯定楼主有一个非触摸屏的手机，并且键盘一定是标准格式的。而且应该有一台笔记本或者你经常接触电脑。另外楼主和楼主的女人关系应\r\n该是认识很久的那种，而不是一见钟情。而且楼主告白的时候应该有用到I LOVE YOU 或者我爱你之类的字眼。对不对阿？呼.好累啊..&lt;/p&gt;&lt;p&gt;祝楼主幸福咯。因为她是爱你的！！&lt;/p&gt;&lt;p&gt;/******************分割线******************/&lt;/p&gt;&lt;p&gt;总结：&lt;/p&gt;&lt;p&gt;她应该是一个聪明又含蓄到极点的MM吧！真的很可爱！数十个网友帮他解这道谜题，花费16个小时。总算最后都答案是大家都乐意看到都最好的结果！所\r\n有的辛苦和努力都没有白费。原来只需要一些脑细胞，一句被世人说烂了的话，也能被演绎的如此鬼斧神工！真是浪漫到了极点！祝他们能白头偕老！&lt;/p&gt;&lt;p&gt;/******************分割线******************/&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('68', '画图解释 SQL join 语句', '伯乐在线', '21', '46', '/Upload/article/56d6ac1e5ee89.png', '2016-03-02 17:02:22', '我认为 Ligaya Turmelle 的关于SQL联合（join）语句的帖子对于新手开发者来说是份很好的材料。SQL 联合语句好像是基于集合的，用韦恩图来解释咋一看是很自然而然的。不过正如在她的帖子的回复中所说的，在测试中我发现韦恩图并不是十分的匹配SQL联合语 法。', '&lt;p&gt;我认为 Ligaya Turmelle 的关于SQL联合（join）语句的帖子对于新手开发者来说是份很好的材料。SQL \n联合语句好像是基于集合的，用韦恩图来解释咋一看是很自然而然的。不过正如在她的帖子的回复中所说的，在测试中我发现韦恩图并不是十分的匹配SQL联合语\n法。&lt;/p&gt;&lt;p&gt;不过我还是喜欢这个观点，所以我们来看看能不能用上韦恩图。假设我们有下面两张表。表A在左边，表B在右边。我们给它们各四条记录。&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;-- ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;1&amp;nbsp; Pirate&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1&amp;nbsp;&amp;nbsp; Rutabaga&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;2&amp;nbsp; Monkey&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2&amp;nbsp;&amp;nbsp; Pirate&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;3&amp;nbsp; Ninja&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 3&amp;nbsp;&amp;nbsp; Darth Vader&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;4&amp;nbsp; Spaghetti&amp;nbsp; 4&amp;nbsp;&amp;nbsp; Ninja&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;我们用过name字段用几种不同方式把这些表联合起来，看能否得到和那些漂亮的韦恩图在概念上的匹配。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;SELECT * FROM TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;INNER JOIN TableB&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;ON TableA.name = TableB.name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id&amp;nbsp; name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp;&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;--&amp;nbsp; ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp;&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;1&amp;nbsp;&amp;nbsp; Pirate&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2&amp;nbsp;&amp;nbsp;&amp;nbsp; Pirate&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;3&amp;nbsp;&amp;nbsp; Ninja&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 4&amp;nbsp;&amp;nbsp;&amp;nbsp; Ninja&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;内联合（inner join）只生成同时匹配表A和表B的记录集。（如下图）&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/iinner-join.png&quot;&gt;&lt;img class=&quot;size-full wp-image-40453 aligncenter&quot; title=&quot;inner join&quot; alt=&quot;inner join&quot; src=&quot;/Upload/ueditor/img/1456909318673342.png&quot; height=&quot;329&quot; width=&quot;502&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;——————————————————————————-&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;SELECT * FROM TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;FULL OUTER JOIN TableB&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;ON TableA.name = TableB.name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id&amp;nbsp;&amp;nbsp;&amp;nbsp; name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp;&amp;nbsp;&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;--&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;1&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Pirate&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Pirate&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Monkey&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;3&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Ninja&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 4&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Ninja&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;4&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Spaghetti&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;null&amp;nbsp; null&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Rutabaga&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;null&amp;nbsp; null&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 3&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Darth Vader&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;全外联合（full outer join）生成表A和表B里的记录全集，包括两边都匹配的记录。如果有一边没有匹配的，缺失的这一边为null。（如下图）&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/Full-outer-join.png&quot;&gt;&lt;img class=&quot;size-full wp-image-40454 aligncenter&quot; title=&quot;Full outer join&quot; alt=&quot;Full outer join&quot; src=&quot;/Upload/ueditor/img/1456909318593472.png&quot; height=&quot;329&quot; width=&quot;502&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;——————————————————————————-&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;SELECT * FROM TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;LEFT OUTER JOIN TableB&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;ON TableA.name = TableB.name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id&amp;nbsp; name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp;&amp;nbsp;&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;--&amp;nbsp; ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;1&amp;nbsp;&amp;nbsp; Pirate&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Pirate&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;2&amp;nbsp;&amp;nbsp; Monkey&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;3&amp;nbsp;&amp;nbsp; Ninja&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 4&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Ninja&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;4&amp;nbsp;&amp;nbsp; Spaghetti&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;左外联合（left outer join）生成表A的所有记录，包括在表B里匹配的记录。如果没有匹配的，右边将是null。（如下图）&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/Left-outer-join.png&quot;&gt;&lt;img class=&quot;wp-image-40455 aligncenter&quot; title=&quot;Left outer join&quot; alt=&quot;Left outer join&quot; src=&quot;/Upload/ueditor/img/1456909318139088.png&quot; height=&quot;329&quot; width=&quot;502&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;——————————————————————————-&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;SELECT * FROM TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;LEFT OUTER JOIN TableB&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;ON TableA.name = TableB.name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;WHERE TableB.id IS null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id&amp;nbsp; name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;--&amp;nbsp; ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;2&amp;nbsp;&amp;nbsp; Monkey&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; null&amp;nbsp;&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;4&amp;nbsp;&amp;nbsp; Spaghetti&amp;nbsp; null&amp;nbsp;&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;为了生成只在表A里而不在表B里的记录集，我们用同样的左外联合，然后用where语句排除我们不想要的记录。（如下图）&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/WHERE-TableB.id-IS-nul.png&quot;&gt;&lt;img class=&quot;wp-image-40456 aligncenter&quot; title=&quot;WHERE TableB.id IS nul&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909318107737.png&quot; height=&quot;329&quot; width=&quot;502&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;——————————————————————————-&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;SELECT * FROM TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;FULL OUTER JOIN TableB&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;ON TableA.name = TableB.name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;WHERE TableA.id IS null &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;OR TableB.id IS null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;id&amp;nbsp;&amp;nbsp;&amp;nbsp; name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; id&amp;nbsp;&amp;nbsp;&amp;nbsp; name&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;--&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; --&amp;nbsp;&amp;nbsp;&amp;nbsp; ----&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Monkey&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;4&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Spaghetti&amp;nbsp; null&amp;nbsp; null&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;null&amp;nbsp; null&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Rutabaga&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;text plain&quot;&gt;null&amp;nbsp; null&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 3&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Darth Vader&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;为了生成对于表A和表B唯一的记录集，我们用同样的全外联合，然后用where语句排除两边都不想要的记录。（如下图）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/WHERE-TableA.id-IS-null.png&quot;&gt;&lt;img class=&quot;size-full wp-image-40457 aligncenter&quot; title=&quot;WHERE TableA.id IS null&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909318202049.png&quot; height=&quot;329&quot; width=&quot;502&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;———————————————————–&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;还有一种笛卡尔积或者&lt;strong&gt;交叉联合（cross join）&lt;/strong&gt;，据我所知不能用韦恩图表示：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;sql keyword&quot;&gt;SELECT&lt;/code&gt; &lt;code class=&quot;sql plain&quot;&gt;* &lt;/code&gt;&lt;code class=&quot;sql keyword&quot;&gt;FROM&lt;/code&gt; &lt;code class=&quot;sql plain&quot;&gt;TableA&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;sql color1&quot;&gt;CROSS&lt;/code&gt; &lt;code class=&quot;sql color1&quot;&gt;JOIN&lt;/code&gt; &lt;code class=&quot;sql plain&quot;&gt;TableB&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;这个把“所有”联接到“所有”，产生4乘4=16行，远多于原始的集合。如果你学过数学，你便知道为什么这个联合遇上大型的表很危险。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【2013-06-17 更新】下图由 Moffatt 在 2008 年制作（点击可查看大图）。PS：Jeff Atwood 的文章写于 2007 年。&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2013/05/SQL-Joins.jpg&quot;&gt;&lt;img class=&quot;wp-image-41506 aligncenter&quot; title=&quot;SQL Joins&quot; alt=&quot;SQL Joins | 一张图看懂 SQL 的各种 join 用法&quot; src=&quot;/Upload/ueditor/img/1456909319368804.jpg&quot; height=&quot;456&quot; width=&quot;580&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('69', '我是一个垃圾程序员', '伯乐在线', '16', '49', '/Upload/article/56d6acc539772.jpg', '2016-03-02 17:05:09', '在过去的4个月里，我深刻地反思了我自己。就在7个月前，我还在从事一份虽不是十分讨厌，但却也感觉不到任何快乐的工作。我还不能确切地描述为什么 我会有这样的感觉，直到我的一个朋友再次找到我，说有一份工作虽然干的活一样，但有机会参与开源软件项目开发，然后我就跳槽了。这一切让我知道了：', '&lt;p&gt;在过去的4个月里，我深刻地反思了我自己。就在7个月前，我还在从事一份虽不是十分讨厌，但却也感觉不到任何快乐的工作。我还不能确切地描述为什么\r\n我会有这样的感觉，直到我的一个朋友再次找到我，说有一份工作虽然干的活一样，但有机会参与开源软件项目开发，然后我就跳槽了。这一切让我知道了：&lt;/p&gt;&lt;p&gt;&lt;em&gt;&lt;strong&gt;我是一个垃圾程序员。&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;&lt;p&gt;3个月后我选择离开了这家公司。当时我签的是临时合同，在还没有完全投入工作前，却先感受到了公司内部的派系斗争之风。这份工作，我没感觉到任何快\r\n乐，但也没有特别的不开心。只是我还没找到一些特别具有挑战性的，并能引起我兴趣的事情。所以我决定先休息一段时间，总结下人生。&lt;/p&gt;&lt;p&gt;为了不至于影响家庭的收入，我仔细地安排了休假计划。在偿还完银行账单后，剩余的钱可足够保证我4到5个月不上班都没问题。做了张现金开销和预算表之后，我决定先休假2个月，然后用1个月的时间进行面试，最后第4个月作为缓冲期，以防万一面试的时间超出了我的预期。&lt;/p&gt;&lt;p&gt;现在我真的在缓冲期里了。到目前为止，我已经面试了近10家公司，却没有一家公司考虑录用我。甚至在多数公司中，我都没有通过技术面试。我是一个程\r\n序员，并且一直以来我都自我感觉良好，觉得我是一个还不错的程序员。但是在这样一个通过海选进行招聘的行业里，我发现很难再认为自己是一个优秀的程序员\r\n了。&lt;/p&gt;&lt;p&gt;我的一些好友和同事，我认为他们中的大多数都比我更有经验并且IQ比我高。当我向他们描述我的忧虑状况时，得到的反馈可总结为“理想很丰满，现实很\r\n骨感”。在上一份工作中，同事们熟悉我的做事方式，与我一起工作都很愉快。并且觉得我的工作背景、敬业程度和技术能力都非常不错。所以不能说我是一个没能\r\n力的程序员，只是我还不善于在面试中表现自己。&lt;/p&gt;&lt;p&gt;但如果说只有少数几个公司拒绝了我，我还能信他们。可是截止到目前我是颗粒无收，并且仅有两次我成功地通过了第一轮的技术面试。如果我已经手握一到\r\n两个OFFER，我还能承认我的能力，但你能说所有公司都犯了同样的错，一致地忽略了我这个虽然面试表现不好但却很优秀的程序员？换句话说，难道有那种可\r\n能，像我这样的垃圾程序员总是能从胜任工作的人的手中抢到职位？&lt;/p&gt;&lt;p&gt;从以往的历史来看，我应该不是那种光说不练的人。基本每天开始工作前，我都会练习写一些代码。同时仅是基于乐趣，我会使用一些奇特的编程语言来解决\r\n一些经典的场景。时常我也会向开源社区做些贡献。有时我甚至会去尝试与他人攀谈，学习如何在交谈中尽量的展现我自己。到后来我发现，像与人交往这种曾对我\r\n最具挑战的事也不是那么困难了。这段故事告诉了我，除非你去尝试，否则你不知道自己到底能不能做到。&lt;/p&gt;&lt;p&gt;问题是还没等我做成什么，我就已经崩溃了。这个月我还能付得起房租和信用卡，但是我还没拿到任何OFFER，下个月估计就得欠债度日了。我甚至不得\r\n不考虑从现在的公寓搬家到那种老旧的筒子楼里。但更重要的是，看起来我好像已经没有能力继续在这个行业工作了，并且恐怕我也不知道除了写程序我还能干什\r\n么。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;那么，到底一个垃圾程序员是什么样？&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我书架上塞满了各种经典图书：《SICP：计算机程序的构造和解释》、《Lisp编程》、《TAOCP：计算机程序设计艺术》、《C专家编程》、\r\n《Effective \r\nC++》和一些算法、分布式计算、安全、图形、数学等各种教科书，并且大部分我都已经读过了（TAOCP这本我还在不断的研磨）。我也经常读一些ACM、\r\nIEEE上的文章和博士论文。如果仅从我的阅读列表来判断，你可能认为我是科班出身，但实际上，我只是喜欢编程，并且想尽我所能来掌握更多的知识。&lt;/p&gt;&lt;p&gt;我为很多开源软件项目做过贡献，编程语言涵盖C++、Perl、Python等，甚至还用过一些类Lisp语言。这些项目中，有些我曾使用过但缺少一些功能，有些是新的项目需要解决BUG或增加功能，有些则是我发现是我需要的类库或程序。&lt;/p&gt;&lt;p&gt;我一直在用心地不断提高我的能力。前面我提到了以前我会写一些练习代码。在我工作过的部门里我会推崇自动化测试。我读过大量的书籍、论文和技术文章，同时我也会写一些文章来尝试着帮助他人。&lt;/p&gt;&lt;p&gt;我非常的喜欢数学。在读完信息论，弄清香农熵后，我又开始深入研究序理论。我真的很喜欢Joint-Semi-Lattices、集合论、关系代\r\n数、Sentential-Database。一些内容现在我已经记不清了，比如我知道对于每种递归的形式都有一个迭代的方法，如果我需要详细了解我可以\r\n迅速的查阅杂志或教科书。&lt;/p&gt;&lt;p&gt;尽管如此，我还是在编写一个要求判断是否序列A是序列B的子集并返布尔值的函数时出现了失误。当被问及我刚编写的函数的的复杂度时我还是记不起来了\r\n（擦，显然列表排列的复杂度为N方，但当时就像对我能否回想起这些细节的审问一样，头脑一片空白）。我的每一丝信心都已经被这些错误、失误和拒绝一点一点\r\n地打败了。&lt;/p&gt;&lt;p&gt;到最后，我发现我越来越沮丧、绝望，并十分害怕自己会让家人失望。以后我的女儿会问我我是做什么的，我可能不直接地回答她，或者告诉她我是给电脑写\r\n程序的，然后赶紧回避开女儿进一步的追问。等她长大了我也尽量避免告诉她，为了混上饭吃我可能什么都做。我从来没有成功过，甚至也没有做过什么让自己值得\r\n骄傲的事儿。我只是尽我所能，即使整个世界都觉得我不够好。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;我是一个垃圾程序员，这就是我的人生。&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('70', '老码农：如何写出让自己满意的代码', '伯乐在线', '1', '64', '/Upload/article/56d6ad1cc1a86.jpg', '2016-03-02 17:06:36', '我在回复里刚写了几句，就意识到140个字很难把我的想法说清楚，本着知无不言言无不尽的好为人师精神，我决定把我的回答写成一篇博文。\n\n首先要说明的是，我写这篇博客并不代表本人觉得自己的代码写得有多好。', '&lt;p&gt;今天有位朋友在微博上问我这样一个问题：&lt;/p&gt;&lt;blockquote&gt;&lt;p&gt;“@老码农的自留地 ，最近出于学习目的写一个管理系统，越到后边，越觉得自己前边的代码写得烂。前辈，我想让代码写得更好一点，能不能谈谈你的经验，给我指点一下！”&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;我在回复里刚写了几句，就意识到140个字很难把我的想法说清楚，本着知无不言言无不尽的好为人师精神，我决定把我的回答写成一篇博文。&lt;/p&gt;&lt;p&gt;首先要说明的是，我写这篇博客并不代表本人觉得自己的代码写得有多好。事实上我很清楚自己的水平，作为一个做应用系统的程序员，和那些做框架做系统\n的大牛根本就不在一个层次。而且即使在应用层次，我的水平大概也只能算二三流，只是因为热爱编程所以一直在努力而已，但不管怎么说，能做自己喜欢的工作我\n已经很满足了。所以我稍微篡改了一下问题，针对&lt;strong&gt;“觉得自己前边的代码写得烂”&lt;/strong&gt;这个重点，把这位朋友问的如何&lt;strong&gt;“让代码写得更好一点”&lt;/strong&gt;改成了&lt;strong&gt;“如何写出让自己满意的代码”&lt;/strong&gt;。&lt;/p&gt;&lt;p&gt;言归正传，我自己的体会是写代码很像写作文，开始写之前的构思过程是最关键的。记得高中的时候，有位语文老师给我传授的经验是，至少花三分之一的时\n间来构思，反复斟酌中心思想、各个段落的大意，文章的脉络，主要的修辞手法，等等。把这些要素都想清楚了，写起来就可以一气呵成。&lt;/p&gt;&lt;p&gt;我觉得写代码也是一样，思路是最关键的。假定采用的技术平台、框架、工具等已经确定了，那么在开始动手写之前，花三分之一以上的开发时间去把所有的\n数据结构及其相互关系考虑清楚。例如需要定义几个类，类和类之间的关系是怎样的，每个类里都有什么属性，每个类提供一些什么样的方法，等等，这些是最核心\n的。这些数据结构要考虑得尽可能细，比如功能实现可能没问题，但是性能上不理想，这就说明你的数据结构设计还需要改进。这些细节要反复考虑，交叉检验，直\n到自己觉得很周到了为止。在此基础上，再注意实现的细节、测试用例、代码可读性，就应该可以写出让自己满意的代码。具体说明如下：&lt;/p&gt;&lt;h3&gt;1. 数据结构和核心算法&lt;/h3&gt;&lt;p&gt;关于数据结构的重要性，大神Linus Torvalds讲过这样的话，我觉得非常赞同：”Bad programmers worry \nabout the code. Good programmers worry about data structures and their \nrelationships.” （低水平程序员总在考虑代码，高水平程序员总在考虑数据结构及其之间的关系）&lt;/p&gt;&lt;p&gt;数据结构考虑清楚了，核心的算法自然就出来了，这就是关于每个类的每个方法如何实现的问题。比如需要实现一个中位数查询方法，如果你前面确定了数据\n保存的格式是一个列表，那么你可以考虑采用插入排序法；如果数据格式是自平衡二叉排序树（AVL），则只需直接返回根节点就可以了。&lt;/p&gt;&lt;p&gt;数据结构决定算法，所以你在考虑数据结构的时候，一定要尽可能地使数据的结构和它的自然属性相匹配，不然后面的实现就会是一场噩梦。比如，你把一个\n多层级的结构定义成二维数组，看上去也靠谱，相当于在一个表格里维护一个组织结构图，可是当你做到部门增减的时候，本层级的数组元素移动自不必说，下面各\n个层级的元素移动就很容易乱套，而且性能很差，可能你写了2000行代码还有很多边界条件会出错。相反，如果用一个孩子兄弟链表来表示这个树型结构，操作\n起来就非常容易，可能100行都足够了。&lt;/p&gt;&lt;h3&gt;2. 功能实现&lt;/h3&gt;&lt;p&gt;思路确定后，实现过程也需要大量的构思活动。碰到你比较熟悉有经验的领域，你自然可以轻车熟路，但难免会有一些你不太熟悉的技术需要尝试。有的同学\n比较排斥这种领域，比如我好不容易才掌握了Struts \n2，领导又让我去学习Grails框架，我就会觉得很不爽，大概看了看就挑出它的一堆问题，然后能躲多远就躲多远。可是我要说，这样的心态会阻碍自己不断\n提高技术水平。作为一个程序员，最大的挑战也是最大的乐趣所在，就是不断学习新的技术，没有这样的心态，很快就会落后。&lt;/p&gt;&lt;p&gt;好，那么遇到不熟悉的技术怎么办？我的体会是，先不要急着实现项目中的代码，自己另外维护一个测试项目，在里边边查文档边学习，边做一个小功能，把\n所有需要在项目中实现的功能先在测试项目里跑通，然后再写项目里的代码。这样做的好处是把单个技术问题和其他潜在的bug隔离开来，便于快速学习新技术。\n否则，你直接在项目里写代码出错以后，要判断问题的源头都要多费好几倍的精力。&lt;/p&gt;&lt;h3&gt;3. 测试&lt;/h3&gt;&lt;p&gt;测试很重要，设计测试用例就像开发时设计数据结构一样，也是很关键的。在设计测试用例的时候，要把当时自己设计数据结构的思路全部忘掉，或者找别人\n来设计测试用例，不然会不由自主地测试那些你已经考虑到了的地方。这样测试是跑通了，用户一用起来可能各种边界条件会到处出问题。&lt;/p&gt;&lt;p&gt;有人会推崇TDD的方法，先设计好测试用例，然后在开发过程中确保所有测试通过。我个人不喜欢这种方法，虽然承认从开发质量管理和长期维护的角度来\n说TDD是很有必要的，但我个人尝试的结果是，设计完测试用例后，想到开发的目标不是实现功能，而是为了跑通测试，就感到毫无乐趣可言。这一点我自己也觉\n得很矛盾。&lt;/p&gt;&lt;p&gt;写到这里我又想到大神Linus说过的另一句话：”Regression testing” What’s that If it \ncompiles, it is good; if it boots up, it is perfect. \n（“回归测试”？这是什么东西？如果代码能编译就是好的，如果它启动了，那就是完美的。）&lt;br/&gt;当然了，大神水平摆在那里，他有资本目空一切，咱确实没资格仿效。但是我还是觉得TDD也有TDD的问题，测试是很重要，但把它摆到驱动开发的高度，就有点本末倒置了。这个是我自己的一点看法，本人对TDD了解得不深入，如果有谬误之处，请多多指教。&lt;/p&gt;&lt;h3&gt;4. 代码可读性&lt;/h3&gt;&lt;p&gt;要想自己满意，代码的可读性一定要好。要做到一年后甚至几年后你拿到自己写的代码，还能很容易看明白当时的思路和实现。这就涉及到命名和注释的问题。&lt;/p&gt;&lt;p&gt;命名就像超市里的商品标签一样，要让看得人一目了然就知道这是个什么东西，比如你的员工类里有两个属性分别是到岗日期和离职日期，把它们定义成date1和date2就没有多少可读性，而定义成dateOnBoard和dateQuit就比较清晰。&lt;/p&gt;&lt;p&gt;注释也是很重要的，它可以用来说明一段代码的作用，算法的设计思想，或者是方法调用的参数格式要求等。有人觉得命名就是注释，代码本身就为自己代言\n了。我觉得这种说法用来强调命名规范的重要性是很好的，但是因此说不需要注释则有失偏颇。试想，如果Dijkstra首次发明最短路径算法的时候，他给出\n的代码里没有一行注释，即使所有的变量命名都定义得准确而严谨，又有几个人能看懂他的算法呢？所以，在重要或者复杂的地方，都需要详细地写一些注释，便于\n看代码的人清晰地了解你的思路。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;最后总结一下：&lt;/strong&gt;要想写出自己满意的代码，首先不要急于动手，要先仔细想清楚思路性的东西，尤其是数据结构，然后\n在实现过程中大胆尝试小心验证，设计好测试用例，确保代码的可读性，就可以在代码中表现出自己的最高水平。但毕竟各人水平是有差异的，自己满意并不等于其\n他人欣赏。我对此的看法是，不求尽如人意，但求无愧我心，足矣。最后再啰嗦一句，技术水平是可以慢慢提高的，但是好的编程习惯需要从一开始就养成，它会让\n你在前进的道路上事半功倍，受益终生。&lt;/p&gt;&lt;p&gt;&lt;em&gt;针对评论中提到的需求不确定导致设计和实现困难的问题，我又写了一篇有关需求分析的文章：《关于需求分析的几点体会》，欢迎批评指正。&lt;/em&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('71', '31部黑客电影，你看过哪几部？', '伯乐在线', '17', '43', '/Upload/article/56d6adeaa37bb.jpg', '2016-03-02 17:10:02', '《社交网络》是2010年电影作品，由大卫·芬奇执导，改编自2009年畅销书籍《Facebook：性爱与金钱、天才与背叛交织的秘辛》，描述Facebook创办人马克·扎克伯格以一个哈佛学生的身分，创办了改变全球网络通信的社交网站脸书，及背后背叛秘辛故事。', '&lt;h2 id=&quot;like-title-1&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909773263194.jpg&quot;/&gt;&lt;/h2&gt;&lt;h2&gt;1、《社交网络The Social Network》(2010)&lt;/h2&gt;&lt;p&gt;《社交网络》是2010年电影作品，由大卫·芬奇执导，改编自2009年畅销书籍《Facebook：性爱与金钱、天才与背叛交织的秘辛》，描述Facebook创办人马克·扎克伯格以一个哈佛学生的身分，创办了改变全球网络通信的社交网站脸书，及背后背叛秘辛故事。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909773136752.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-2&quot;&gt;2、《硅谷传奇(Pirates of Silicon Valley)》(1999)&lt;/h2&gt;&lt;p&gt;1999年电影，是部描述苹果公司与微软的，非官方授权的传记式电影。电影改编自保罗·弗列柏格与米迦勒·史文 所撰写的《硅谷之火》。电影由马汀·伯克编导，于1999年在 透纳电视网播出。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774649272.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-3&quot;&gt;3、《大买卖(The Score)》(2001)&lt;/h2&gt;&lt;p&gt;尼克·威尔斯是满誉江湖的大盗，不过他早已金盆洗手了，和女朋友戴安一起经营着他们的合法营生：蒙特利尔的一家爵士乐俱乐部。可是，尼克的老朋友兼销赃人马克斯却不愿看到他就这样退出江湖，在马克斯的威逼利诱下，尼克迫不得已重拾老..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774114775.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-4&quot;&gt;4、《碟中谍4(Mission: Impossible – Ghost Protocol)》(2011)&lt;/h2&gt;&lt;p&gt;当特工伊桑·亨特在莫斯科执行一个秘密的间谍活动的时候，一个恐怖分子安置的炸弹炸掉了俄罗斯的标志性建筑：克林姆林宫。俄罗斯政府随即宣布，这起袭击已经超越了一般的恐怖袭击的范畴，是完完全全的“战争的挑衅”。俄罗斯政府的..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774111560.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-5&quot;&gt;5、《反托拉斯行动(Antitrust)》(2001)&lt;/h2&gt;&lt;p&gt;电脑高手米罗（莱恩·菲利浦饰）是有个当画家的漂亮女友（克莱尔·弗拉妮饰），年轻的他有着大好的前程。正当他着手和朋友泰迪创办自己的公司时，却意外地被硅谷一间拥有数亿美金资产的大公司——NURV相中，这间公司的持有者，正是电脑..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774109581.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-6&quot;&gt;6、《操作系统革命(Revolution OS)》(2001)&lt;/h2&gt;&lt;p&gt;《操作系统革命》是一部2001年由J·T·S·摩尔导演的纪录片电影，该电影追述了GNU、Linux、自由软件运动以及开放源代码运动长达二十余年的历史。该片的主演有理查德·斯托曼、林纳斯·托瓦兹、布鲁斯·斐伦斯、拉里·奥古斯丁与埃里克·雷蒙等。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774953547.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-7&quot;&gt;7、《偷天换日The Italian Job》(2003)&lt;/h2&gt;&lt;p&gt;一个完美无缺的计画、一项天衣无缝的行动、一次干净俐落的逃亡，但是窃盗集团首脑柯查理却没料到他的左右手竟然背叛他。查理和他的伙伴－犯案高手史提夫、电脑天才小赖、飙悍驾驶帅哥罗柏、爆破专家左耳和开锁老手布约翰－在威尼斯..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909774542845.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-8&quot;&gt;8、《异次元骇客The Thirteenth Floor》(1999)&lt;/h2&gt;&lt;p&gt;在一栋公共大楼的十三楼，想象力丰富的道格拉斯霍尔（克莱格比科）和汉农富勒（阿明穆勒斯太尔）将虚拟现实发挥到了极限，他们在计算机上模拟了1937年的洛杉矶。霍尔在迷惘中醒来，发现富勒被谋杀，而他的房子里有一件带血的衬衫。..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775310020.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-9&quot;&gt;9、《黑客帝国(The Matrix)》(1999)&lt;/h2&gt;&lt;p&gt;《黑客帝国》是一部1999年好莱坞科幻电影，由沃卓斯基兄弟执导，基努·里维斯、劳伦斯·菲什伯恩、凯莉·安摩丝及雨果·威文等人主演，并由香港\n电影界的袁和平担任动作指导。这部影片在中国大陆也是1999年中影引进的，上海电影译制厂译制，当时个人计算机和互联网正在大陆普及，门户网站也在兴起\n中。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775703744.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-10&quot;&gt;10、《龙纹身的女孩(The Girl with the Dragon Tattoo)》2011&lt;/h2&gt;&lt;p&gt;《龙纹身的女孩》（英语：The Girl with the Dragon Tattoo）是一部英语惊悚电影，根据瑞典作家史迪格·拉森于2005年出版的同名小说改编而成。导演是大卫·芬奇，丹尼尔·克雷格和鲁妮·玛拉主演。北美于2011年12月20日上映。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775878574.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-11&quot;&gt;11、《天地逃生Gamer》(2009)&lt;/h2&gt;&lt;p&gt;在并不遥远的未来世界里，网络游戏风靡全球，不过玩法跟如今动动鼠标键盘的操作方式不太一样，而是将玩家的神经末梢接入，可以直接操作游戏角色，而被控制的这些游戏角色，都是活生生的真人。游戏的开发商是亿万富翁肯•卡索，他利..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775184616.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-12&quot;&gt;12、《天才除草人The Lawnmower Man》(1992)&lt;/h2&gt;&lt;p&gt;到千年之交时，一种名为“虚拟现实”的技术将得到广泛应用，它能使人进入一个由电脑创造出来的、如同想象力般无限丰富的虚幻世界，它的创造者预测这种技术将为人类社会的发展发挥无数积极的作用，而有些人则担心它会被一些人利用，..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775957825.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-13&quot;&gt;13、《地心抢险记The Core》(2003)&lt;/h2&gt;&lt;p&gt;地心毁灭是美国一部科幻灾难电影，于2003年上映。电影讲述地球的核心因不明原因停止转动，以致电磁场减弱。在失去地磁的保护下，地球会遭到致命\n的高能宇宙微波辐射袭击，造成灾难。此时有6人组成团队，驾驶一艘名为“维尔吉号”的潜地艇，钻进炽热地幔，放置并引爆核弹，使地核再次转动，重生地磁，\n最后成功。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775121780.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-14&quot;&gt;14、《时空悍将Virtuosity》(1995)&lt;/h2&gt;&lt;p&gt;在完全计算机化的时代，一名邪恶的科学家研制出一个强大的杀人程序：里面融合了超过150个连环杀手的变态心理和杀人手法。而这个程序的职能使他挣脱了科学家的控制独立行凶。他拥有人的外形，并能任意修复身体的损伤，他的名字叫Sid 6.7..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909775120772.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-15&quot;&gt;15、《剑鱼行动Swordfish》(2001)&lt;/h2&gt;&lt;p&gt;加布利尔(约翰·特拉沃尔塔饰)是个在江湖上闯荡多年的间谍特工，他决定窃取一笔高达95亿美金的非法政府基金，找到了世界上最顶尖的电脑黑客之一的斯坦利(休·杰克曼饰)。当年他将FBI引以为傲的高级计算机监视系统搞了个天翻地覆，..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909776960926.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-16&quot;&gt;16、《感官游戏eXistenZ》(1999)&lt;/h2&gt;&lt;p&gt;在科技发展到尖端的未来，爱丽拉·盖勒这位美丽性感的设计师有若电玩界的女王，她所设计的[X接触]远远超越时代，它非常真实引人入胜，在试玩大会上，各方玩家齐聚一堂，体验爱丽拉的最新杰作，想不到半途杀进一群狂热者企图刺杀..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909776908620.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-17&quot;&gt;17、《神鬼妙计Foolproof》(2003)&lt;/h2&gt;&lt;p&gt;凯文，珊曼莎及罗博三人原本生活的安全无虑，唯一的梦想便是能够创出一番丰功伟业；他们的秘密基地是经由精密科技所打造而成的，为了达成三人的梦想，他们开始着手一件史上最大宗，也是最不可能成功的抢案。 　　原因就在于，他..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909776133905.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-18&quot;&gt;18、《创：战纪Tron: Legacy》(2010)&lt;/h2&gt;&lt;p&gt;影片以一对父子的冒险故事为线索层层展开，将电子网络中的与现实中的探险交相呼应。影片的主角萨姆·弗林（加莱特·赫德兰饰）是一个27岁的叛逆青年，他的父亲凯文·弗林由曾获得奥斯卡奖和金球奖的杰夫·布里奇斯扮演，影片中男主..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909776324216.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-19&quot;&gt;19、《骇客追缉令Takedown》(2000)&lt;/h2&gt;&lt;p&gt;凯文密尼克可称为有史以来最厉害的电脑骇客，他侵入家电信公司，入侵联邦调查局的电脑，在网路世界中成为最难以捉摸的骇客之王，然而他却希望能遇上一个真正的挑战，当他开始入侵著名物理学家兼电脑天才下村勤的电脑系统后，他的愿..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909776423364.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-20&quot;&gt;20、《对话The Conversation》(1974)&lt;/h2&gt;&lt;p&gt;由美国科波拉影片公司出品，弗朗西斯．福特．科波拉编剧兼导演。本片是科波拉的批判现实主义杰作，被誉为70年代最优秀的电影之一。编导的倾向性非\n常明显，主题是谴责丧失隐私的现代社会使人更加孤独、相互猜忌，加速了人际关系的紧张和人的异化。主角拆毁屋子以寻找窃听器的场景已成为经典。该片获\n1974年戛纳国际电影节金棕榈奖；美国奥斯卡奖三项提名；全美影评家联合会最佳导演奖。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777496813.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-21&quot;&gt;21、《黑客Hackers》(1995)&lt;/h2&gt;&lt;p&gt;除了Sandra Bullock主演的《The \nNet》外，05年另外一部以未来网络世界为主题的科幻犯罪片《Hackers》也相当精彩。从今天的眼光看来，本片Cast堪称强大——虽然在当时他们\n都只是新晋演员：Johnny Lee Miller和Angelina \nJolie联袂演出，但也许是对黑客的身份过度想象的缘故，两人的演出过于板着面孔。看着影片中所幻想的一一变为现实，而影片的演员亦成为好莱坞当家花\n旦，确是赏心乐事。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777825977.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-22&quot;&gt;22、《电子世界争霸战Tron》(1982)&lt;/h2&gt;&lt;p&gt;《电子世界争霸战》是一部1982年迪士尼科幻电影。由Steven Lisberger执导、杰夫·布里吉斯等主演，及台湾动画公司宏广负责了绝大部分的手绘上色工程。本片另于2010年推出续集－－《创：战纪》。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777314202.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-23&quot;&gt;23、《战争游戏War Games》(1983)&lt;/h2&gt;&lt;p&gt;皆大欢喜的娱乐性科幻片《战争游戏》准确地把握住大多数观众的“厌战”心理，以主人公戴维和其女友詹尼弗的爱情贯穿始终，十分巧妙地处理了关于核武器和战争的场面，使影片避免了所谓的“悲惨结局”，从而赢得了观众的欢迎和喜爱。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777874047.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-24&quot;&gt;24、《大盗Sneakers》(1992)&lt;/h2&gt;&lt;p&gt;这部影片表现电脑的现代化及让人大开眼界。利用电脑进行犯罪活动的案例不时见诸报端，但像本片那样透过细节描绘所表现出来的触目惊心的事件，使人强\n烈地感受到人类对科技未来发展的潜在所可能带来的危机。故事的引人之处是缜密而细致的描写那副“黑盒”的可怕功能，以及以罗伯特·雷德福为首的几个主要角\n色的智慧。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777128393.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-25&quot;&gt;25、《虎胆龙威4 Live Free or Die Hard》(2007)&lt;/h2&gt;&lt;p&gt;这是《虎胆龙威》系列特别营造的一个惯例：约翰·迈克莱恩总是在错误的时间出现在错误的地方，然后被迫卷进一系列的麻烦之中。话说不知道是哪一年的\n7月4日，恰好是一个周末，虽说纽约警察约翰·迈克莱恩正在度假中，可是他还来不及和他那已经上大学的女儿露西道别，就被总部的电话叫走了，他接到一个任\n务：逮捕黑客马特·法莱尔，然后送到FBI那里接受审讯……怎么看这都是一件稀松平常的案子，可是你不要忘了约翰那天生吸引麻烦的体质。约翰刚刚抵达马特\n的公寓，就像触动了什么机关似的，各种各样的混乱接踵而来。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909777646302.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-26&quot;&gt;26、《007之黄金眼Golden Eye》(1995)&lt;/h2&gt;&lt;p&gt;作为第17部007电影，《黄金眼》的问世可谓历尽艰辛。由于1989年的《杀人执照》成为票房业绩最差的007电影，所以新作的前景令人堪忧。同\n年，澳大利亚广电集团Qintex收购了米高梅／联美电影公司，后者面临着与Pathé电影公司合并的命运，而EON \nProductions的母公司Danjaq又将米高梅／联美电影公司告上法庭，因为后者未经Danjaq的许可就准备授权Pathé电影公司在多国电视\n台播放007电影。这些错综复杂的法律纠纷让新片的拍摄搁置了数年。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909778189474.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-27&quot;&gt;27、《觅踪Untraceable》(2008)&lt;/h2&gt;&lt;p&gt;当制片人汤姆·罗森伯格(Tom Rosenberg)和加里·卢切西(Gary Lucchesi)读到一个由罗伯特·菲佛伦特(Robert \nFyvolent)和马克·布里克(Mark \nBrinker)共同创造的名叫《觅踪》的剧本时，一个如此合时宜的故事瞬间就突破了他们的防线，卢切西说：“FBI中的网络犯罪调查组并没有成立很长的\n时间，也就只有6、7年的历史。而《觅踪》却对焦在了一个非常与众不同的案件上，因为它和侵犯儿童以及信用卡偷窃完全不搭边，讲述的是一个正在实行谋杀的\n罪犯，只是在利用网络使一切更容易进展而已–网民们都关注于此，越多的人登陆，牺牲品也就死得越快。”&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909778124664.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-28&quot;&gt;28、《时空线索Deja Vu》(2006)&lt;/h2&gt;&lt;p&gt;《时空线索》，美国科幻悬疑电影，于2006年11月22日首度上映。片名标题在法语中，为“时光倒流七十年”的意思。本片剧情认为有平行宇宙的存在，当人类出现时光倒流七十年的情形时就是在平行宇宙的另一个自己已死亡而出现意识结合。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909778105334.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-29&quot;&gt;29、《神秘幻影(One Point O) 》2004&lt;/h2&gt;&lt;p&gt;Simon 是一个计算机程序师,一天早晨他醒来发现他的公寓里有一个简单的褐色包裹。 \n他打开它，但是里面什么也没有。　尽管他尝试锁好他的公寓,神秘的包裹却继续出现。从安全摄像镜头观察他的行踪,Simon \n渐渐变得神经质，开始怀疑公寓里的每一个人。当他们的其中之一忽然死亡的时候, Simon \n　　被推到了危险的边缘。在这种精神折磨中,他疯狂地寻找改变了他的生活的神秘力量到底意味着什么。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909778543438.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-30&quot;&gt;30、《叛逆性骚扰Disclosure》(1994)&lt;/h2&gt;&lt;p&gt;故事场景是西雅图的一家尖端高科技公司，迈克·道格拉斯是未来副总裁的最佳人选，不料公司与大企业合并后，夺得此一高位的人竟是他十年前的同居女友黛咪·摩尔。摩尔以老朋友的身份邀道格拉斯在下班后到她办公室叙旧，不料却主动勾引他..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909779829494.jpg&quot;/&gt;&lt;/p&gt;&lt;h2 id=&quot;like-title-31&quot;&gt;31、《侏罗纪公园3 Jurassic Park 3》(2001)&lt;/h2&gt;&lt;p&gt;本片时间设定在恐龙大闹圣地亚哥事件四年后。一名小男孩玩滑翔翼不幸失踪在索诺岛上。他的父母富商保罗与前妻阿曼达请求恐龙专家葛兰博士前往搭救。葛兰正拟进行一项史前动物研究，但苦于缺乏经费。在动之以利的情况..&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1456909779911860.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('72', '测试是浪费时间，我的程序肯定没问题', '伯乐', '1', '59', '/Upload/article/56d9204866508.jpg', '2016-03-04 13:42:32', '    太费时间。\r\n    不值得。\r\n    我很懒。\r\n    我不知道如何做。\r\n    我知道我的程序好用，我运行过一次，没出问题。\r\n    我是超人，我从来不犯错误。', '&lt;p&gt;尽管关于测试驱动开发(TDD)的书和文章有成百上千之多，仍然有很多人从未感受过测试的强大力量。&lt;/p&gt;&lt;p&gt;之所以不愿意去写测试程序不外乎有以下几个理由：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;太费时间。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;不值得。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;我很懒。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;我不知道如何做。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;我知道我的程序好用，我运行过一次，没出问题。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;我是超人，我从来不犯错误。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;除非你的答案是6.(如果是这样，我很羡慕你)，否则，你应该继续读下去。&lt;span id=&quot;more-13115&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;h3&gt;让我们从一个简单的例子开始。就说你要写一个Email地址校验程序。&lt;/h3&gt;&lt;p&gt;你也许会使用正则表达式，或手工实现每个字符的对比，来完成这个任务。&lt;/p&gt;&lt;p&gt;现在你想验证一下你的代码是否有效。你会怎么做？你可以手工输入不同的值来验证它是否符合你的要求，结果你就测试了像下面的这些东西：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;c plain&quot;&gt;foo&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;c plain&quot;&gt;foo@bar&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;c plain&quot;&gt;foo@bar.baz&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;c plain&quot;&gt;...&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;如果你要改变/调整校验算法会发生什么？你需要把之前所有的过程全部重复一遍，把每个值都再输入一遍，看看校验的结果。如果让计算机自动的帮你校验这些是不是会更好些呢？&lt;/p&gt;&lt;p&gt;通过把测试过程自动化，你可以在任何时候对程序进行任何遍次的测试。这不仅仅在以后会节省你大量的时间，而且会增加你的自信心，因为每次当你感觉到程序可能出错时，只要运行一下你的测试程序，看看测试结果就行了。&lt;/p&gt;&lt;h3&gt;现在设想一下你正在编写一个更加复杂的程序，比如XML解析器。&lt;/h3&gt;&lt;p&gt;你不可能编写出解析器后不去测试它，问题是，你如何测试它？通常，你会创建一个XML样板文件，把它输入解析器里，手工检查解析的结果。当你看到解析器按照要求输出了你期望的结果后，你确信你完成了任务，解析器没问题。&lt;/p&gt;&lt;p&gt;问题就在于如果你发现了一个Bug。那你就需要去修改你的代码，再验证一次它是否好用。可是我们程序员都是一群懒人，我们会只测试我们遇到的这个Bug，几乎从来不会去测试解析器的其它部分。这会产生什么问题呢？你能确保没有把什么东西改坏？&lt;/p&gt;&lt;h3&gt;当你写了单元测试程序后，这个工作流程会变的怎样？&lt;/h3&gt;&lt;p&gt;首先，你已经对程序进行过测试，之前你必须对每个函数进行手工的测试，手工的检查输出结果。如果这种事情你只需要做一次，那也没什么了，但当有东西出错时，你不想一遍一遍的重复做这个事情，没有人喜欢来回重复的做相同的事情，特别是无聊的事情。&lt;/p&gt;&lt;p&gt;另一种情况，你为你的解析器里的每个功能都写了自动测试程序。在这个例子中，你已经测试过你的程序，对这个过程你并不陌生。你需要做的是把手工的检查改为assertions，它们会为你自动测试程序。&lt;/p&gt;&lt;h3&gt;当Bug出现时，巨大的变化发生了。&lt;/h3&gt;&lt;p&gt;如果没有自动化测试，你必须手工的检查所有的东西来确保你在修正一个Bug时不会引入其它的错误。当有数个Bug出现时，你会变得极不耐烦，你开始\r\n不再检查其它部分的代码，只关注你的这个Bug。但假如你为程序写好了自动化测试，你唯一要做的就是点击一次按钮，让所有的测试程序运行起来，你马上就能\r\n看到测试结果，几乎不需要在其它任何事情。&lt;/p&gt;&lt;h3&gt;当有了测试程序后，修改Bug是怎样进行的？&lt;/h3&gt;&lt;p&gt;首先，你需要重现这个Bug。无论如何，在你修改前，你必须要认定这个Bug是否真实的存在。如何做到这些？你要写出一个测试用例来重现这个bug，这个必须要在你做任何的修改这个bug的工作之前完成。&lt;/p&gt;&lt;p&gt;如果这个bug不能重现，问题就可能出自其它地方，如果不能用自动化测试来重现这个bug，你可能在错误的地方修改了这个bug，很可能改坏了你的程序。当你写出测试程序，你必须先运行它确保它会失败。如果测试没有失败，什么地方可能出了问题，你没能正确的重现它。&lt;/p&gt;&lt;p&gt;当你修改了这个Bug，你也有了针对它的测试程序，你可以立即发现这个bug是否还会出现。&lt;/p&gt;&lt;h3&gt;当你这时又发现另外一个bug时该怎么做？&lt;/h3&gt;&lt;p&gt;相同的做法。写一个测试程序，重现这个bug。即使你没有时间来立即修正这个bug或者这不是个致命的bug，你也应该有个能够让它重现的测试程序，当日后你回来解决这个问题时，你就能知道该做什么了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('73', '为什么很少见工资高的程序员炫富？', '伯乐', '1', '49', '/Upload/article/56d921399a9b3.jpg', '2016-03-04 13:46:33', '反观网上炫富的没有一个是程序员，哪怕是平时聊天吹牛的也没见程序员晒', '&lt;blockquote&gt;&lt;p&gt;反观网上炫富的没有一个是程序员，哪怕是平时聊天吹牛的也没见程序员晒。&lt;/p&gt;&lt;/blockquote&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img class=&quot;alignnone&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1457070352781529.jpg&quot; height=&quot;363&quot; width=&quot;483&quot;/&gt;&lt;br/&gt;&lt;em&gt; （网络图）&lt;/em&gt;&lt;/p&gt;&lt;h3&gt;鼹鼠的土豆&amp;nbsp;的回答，&lt;/h3&gt;&lt;p&gt;我不是程序员，我嫁给了程序员，我从一个程序员的老婆的角度回答这个问题啊！或者说从一个IT男老婆的角度回答这个问题。&lt;/p&gt;&lt;p&gt;首先是太宅，没什么花销，或者说没机会炫富，或者说他们的消费项也达不到炫富的程度，拿我爱人来说啊！不抽烟，出去应酬才喝酒，每天早上在家吃完早\n饭才去上班，车停单位免费停车场，午饭在公司食堂吃，基本上每月饭补发饭卡里都会花不完，请人吃饭开发票回公司报销，晚上回家吃饭，每周公司组织运动公司\n付费。每天固定的爱好，玩个魔兽，还是用金就换点卡了，嗨！而且魔兽这东西，充一千块钱能玩一辈子……每周跟初中、小学的朋友们在家附近打两次球，还是谁\n输谁请客，但是他基本上一年也不输一次。每周末带父母去看个电影，吃个饭，都是我订票，我选地方，刷我卡。&lt;/p&gt;&lt;p&gt;还有IT男也不知道自己有多少钱，据我观察我爱人和他们部门的人，基本上有老婆的钱都交给老婆，没老婆的就干脆不清楚自己有多少钱。我们家一般是每\n个月我给他一些零花钱，每到月底重新给的时候，一般他会看看钱包说没花完，不要了。以前我还觉得他可能知道家里有多少钱，假装不知道。后来有一次去看车，\n他问我预算是多少，我说多少多少，他很惊讶的说咱们有这么多钱啊！！！&lt;/p&gt;&lt;p&gt;对了，有几位我觉得很有潜力的大哥，都是很推崇刘墉的话“老婆是男人最好的首饰”，不管自己穿的多破，只要带着老婆逛街的时候说“喜欢什么就买吧！”不用炫富也高大上了。&lt;/p&gt;&lt;p&gt;大家问我生活趣事，我会陆续更新的。&lt;/p&gt;&lt;p&gt;豆瓣有阵有个人发钓鱼的帖子，很多男的上当。我说你要是受到诱惑是不是也会把持不住，他说不知道！然后下楼做饭去了，我在楼上就注册了个马甲给他发豆油！等了一会儿，他豆油回复到“媳妇，你用咱家网发，我能不知道是你吗？！！！”&lt;/p&gt;&lt;p&gt;我跟我男朋友（现在是我爱人了）都玩魔兽，我喜欢做任务，有次做任务被守尸，就是那种刚一复活，马上又被杀。怎么都跑不了，也打不过。他却在打本，\n不能过来帮我！被守尸了四个小时以后，突然就崩溃了。大哭大闹，然后自己从他住的地方走回了我住的地方，一边走一边给他打电话一边哭。&lt;/p&gt;&lt;p&gt;到家一边写稿一边哭，写的那个伤心，记得写了一句“男人对于女人，就像降落伞，在我需要你的时候，你不在，以后就不用在了。”然后他打电话我也不接，敲门我也不开，果断的说要跟他分手。第二天早上醒了，觉得自己简直是神经病！&lt;/p&gt;&lt;p&gt;我爱人到现在都心有余悸的把那次吵架叫“一次游戏引发的血案”。作为魔兽某服某公会会长的他更是发了通知给大家，见到那个守我的号大家谁守了都可以得到奖励。&lt;/p&gt;&lt;p&gt;结婚以后还是经常有类似的事发生，有孩子以后还是经常有类似的事发生，前天看电视上野猪正在吃山竹，吃的那个香，然后我说你给我也买点，他不给我买！我就说我太可怜了，野猪都吃上了，我都吃不上呀！然后他就下楼给我买了！山竹真好吃呀！！&lt;/p&gt;&lt;p&gt;玩WOW的时候，有个大哥一直带我做任务，我就跟他说，你放心打本去吧！有个大哥人特别好，每天带我做任务。他有天跟我说，有个本很难，我带你们\n吧！我说好呀！然后我们一起去了，本打完了。大哥说兄弟谢谢你呀！IT男贱贱的说“不用谢呀！你每天带我媳妇做任务，辛苦了！”后来大哥就不带我做任务，\n不理我了！！！&lt;/p&gt;&lt;p&gt;还是玩wow，IT男是公会会长，打本的时候，我经常会去蹭本，捡东西，我在公会的外号叫“破烂王”！大家都不知道我们的关系。后来有次去打黄龙，\n我说那个龙还挺好看的，他说那我带你去打一个吧！但是还差一个高手，他就叫了他发小，他发小当然知道我们的关系了！打之前大家都知道黄龙是给破烂王\n的，YY里就没怎么聊。打完了，我就低头捡钱。他发小怕别人把黄龙捡了，就在YY里喊“***快让你媳妇儿捡黄龙！**快让你媳妇儿捡黄龙！”然后我的屏\n幕角落里就刷屏的显示着“特大爆料：破烂王是妹子！破烂王是会长媳妇儿！”反复都是这几句！谁说男人不三八呀！！！&lt;/p&gt;&lt;h3&gt;mu mu&amp;nbsp;的回答，&lt;/h3&gt;&lt;p&gt;比如说，你买了个名表，在程序员团队里炫了个把月，没人有反应，只好拉着一个程序员说，&lt;br/&gt;“你看看这个表，江诗丹顿…”&lt;/p&gt;&lt;p&gt;程序员哥们没听明白什么丹顿，但也不愿怂了，从抽屉里拿出个电子表，&lt;/p&gt;&lt;p&gt;“你看看我这个，卡西欧，防水，防摔，太阳能，电波对时，秒表闹钟，都用了七八年了…”&lt;/p&gt;&lt;p&gt;程序员天生喜欢简单，耐用，皮实的东西，牛仔裤，冲锋衣，包不用酷炫，但一定要能安安稳稳的放下笔记本电脑，背着要舒服，不坏不用换。这类产品，一般没的炫。&lt;/p&gt;&lt;p&gt;程序员也不懂怎么炫。炫富也要知识，奢饰品那么多，品牌眼花缭乱，比framework还难选，哪里是说炫就能炫的，没有知识，有钱也不知道买什么。&lt;/p&gt;&lt;p&gt;程序员炫了也没人看。除了电脑，程序员周围都是程序员，穿个几万块的西装，戴个几十万的表，电脑前坐一天，还没穿T恤的舒服，不但没人能感受到你的富，还有人会觉得你穿的奇怪。&lt;/p&gt;&lt;p&gt;炫富的人炫耀的东西，程序员不觉厉害；程序员觉的厉害的东西，炫富的人又不懂。&lt;/p&gt;&lt;p&gt;再说，程序员也不富。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;萌神&amp;nbsp;推荐了一张趣图：&lt;/h3&gt;&lt;p&gt;&lt;img class=&quot;alignnone&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1457070353103181.jpg&quot; height=&quot;1488&quot; width=&quot;375&quot;/&gt;&lt;/p&gt;&lt;h3&gt;张翼德&amp;nbsp;的看法：&lt;/h3&gt;&lt;blockquote&gt;&lt;p&gt;乐意炫富的人，不是因为有钱，而是因为付出的少而得到的多。程序员赚的都是辛苦钱，炫富给谁看？&lt;/p&gt;&lt;/blockquote&gt;&lt;h3&gt;靳超&amp;nbsp;的看法：&lt;/h3&gt;&lt;blockquote&gt;&lt;p&gt;其实绝大数程序员还没到可以炫富的程度，即使到了，为啥要炫啊？钱对于吃喝嫖赌样样都不会的程序猿来说只不过是一串数字而已，除了买geek设备外实在不知道有什么用…&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('74', '趣文：如果编程语言是女人（新编版）', '伯乐', '1', '283', '/Upload/article/56d921c3df35b.jpg', '2016-03-04 13:48:51', 'PHP是一个很平常的女孩，能真心的和你交谈。她超重，不是很聪明，皮肤不好，虽然化妆也不是很漂亮，每有流行事情她都在几年后才去追随，安于现状。你最终会勉强接受她。', '&lt;p&gt;Ruby \n是一个略显丰满的女孩，但其令人窒息的甜蜜面容绝对会成为你一生的梦想，然而，当你最终得到了她，她却开始迅速发胖，你开始认识到了她华丽外表下所有恶劣\n的性格缺陷。你坚持认为事情会有好转，但事实却不是这样，你最终不得不苦恼的把所有时间都花在寻找新的能让她保持高兴的事情上。&lt;/p&gt;&lt;p&gt;Java 是一个过于精于计算的生意场上的女人，她会盲目的遵循各种习惯风俗——不论它们是如何的相互矛盾和荒谬。虽然相貌平平，但你和她出门或做任何事情前都必须经过各种的礼节、复杂的装饰、规定的仪式。她通过各种方法、没完没了的自我宣传来保持一个好的名声。&lt;/p&gt;&lt;p&gt;Python是大众情人，她体贴周到，魅力迷人，是一位好听众，更是伶牙俐齿，思想有见地，有时她会进入野外自我探索的旅途，在那里，她能彻底的自我再造，当她会来时，你看到的已经是完全另外一个人了。&lt;/p&gt;&lt;p&gt;JavaScript是精力充沛的狂热信徒，她愿意去寻找出任何荒谬的办法来跑的更快——即使会因此变得体积太庞大而无法干任何其它的事情。然而，你无法忽视她，因为她无处不在，你总能遇到她。幸运的是，当她变的有点失控时，她的胖朋友们会为你提醒她。&lt;/p&gt;&lt;p&gt;Lisp将会成为你事业上的导师，能在任何事情上为您提供理论方案，让你佩服的五体投地，但是，按她的方法做出的锤子不能直直钉入一个钉子。永远都\n是玄虚的思想，宏大的计划，跟着她，你会发现自已永远都只能住在桥洞里饿的瑟瑟发抖，直到她发现另外一个愿意为她花钱的傻瓜再为她可笑的追逐50年的风\n车。&lt;/p&gt;&lt;p&gt;PHP是一个很平常的女孩，能真心的和你交谈。她超重，不是很聪明，皮肤不好，虽然化妆也不是很漂亮，每有流行事情她都在几年后才去追随，安于现状。你最终会勉强接受她。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('75', '8个最佳PHP库', '伯乐', '20', '48', '/Upload/article/56d92253679b3.png', '2016-03-04 13:51:15', 'PHP标准库 (SPL)的目的就是提供一组接口，让开发者在PHP5中充分利用面向对象编程。因此本文我们搜集了8个最好的，能辅助开发者简化他们的工作，为他们的开发任务服务的PHP库。', '&lt;p&gt;PHP标准库 (SPL)的目的就是提供一组接口，让开发者在PHP5中充分利用面向对象编程。因此本文我们搜集了8个最好的，能辅助开发者简化他们的工作，为他们的开发任务服务的PHP库。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1. &lt;a href=&quot;http://filp.github.io/whoops/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Whoops &lt;/a&gt;: 更好的php错误报告库&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070627416434.jpg&quot; alt=&quot;whoops&quot; height=&quot;311&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Whoops是一个易于处理和调试错误的PHP库 。它提供基于堆栈的的错误处理和好看的错误界面。它有个简单的API来处理异常，跟踪帧和数据，并能和任何框架整合（随时可用的集成端和Silex）。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2. &lt;a href=&quot;http://www.phpfastcache.com/&quot; target=&quot;_blank&quot;&gt;PhpFastCache&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070628862768.jpg&quot; alt=&quot;phpfastcache&quot; height=&quot;342&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;phpFastCache 是一个开源的 PHP 缓存库，只提供一个简单的 PHP \n文件，可方便集成到已有项目，支持多种缓存方法，包括：apc, memcache, memcached, wincache, files, pdo\n and mpdo。可通过简单的 API 来定义缓存的有效时间。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3. &lt;a href=&quot;http://www.eden-php.com/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Eden&lt;/a&gt; : 功能强大的 PHP 库&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070628547467.jpg&quot; alt=&quot;eden-php-library&quot; height=&quot;321&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Eden是一个开源且免费的PHP快速开发类库。它包含很多组件用来自动加载、事件驱动、文档系统、缓存、模板、国际化、数据库、web服务、支付\n网关、装载和云服务技术。为了给我们最好的选择，他已经将现有的函数实现了与谷歌服务（Youtube, Drive, Contacts, \nAnalytics, Checkout, Maps），Facebook（脸谱）, Twitter（推特）, Tumblr（轻博客）, \nPayPal（贝宝）, Authorize.net, FedEx（联邦快递）, UPS（联合包裹服务公司）, Amazon + \nRackspace Clouds（亚马逊+Rackspace 云）等服务的交互。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4. &lt;a href=&quot;http://phperror.net/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Php Error&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070628308761.jpg&quot; alt=&quot;php-error&quot; height=&quot;321&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;PHP Error 是一个开源的 PHP 库，用于转换标准的 PHP 错误信息，主要用于开发过程中的调试。PHP Error 紧密集成到 PHP 环境中，显示带语法高亮的错误提示。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5. &lt;a href=&quot;http://detector.dmolsen.com/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Detector&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070629356085.jpg&quot; alt=&quot;detector&quot; height=&quot;321&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Detector是一个开源的PHP类库用于检测关于用户的浏览器环境的许多东西。它可以获得浏览器的使用和浏览器的html5 \ncss3功能,分析是否移动电话、平板电脑、桌面或网页爬虫和其他项如：颜色深度, \n视口尺寸、cookie等支持。类库可以自动适应新的浏览器、版本和设备对每一个浏览器使用独特的用户代理字符。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6. &lt;a href=&quot;http://opauth.org/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Opauth&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070629910185.jpg&quot; alt=&quot;opauth&quot; height=&quot;470&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Opauth 是一个开源的 PHP 库，提供了 OAuth 认证的支持，让你无需关注不同 Provider \n之间的差别，提供统一标准的访问方法。目前支持 Google、Twitter 和 Facebook，其他的 Provider \n支持也将陆续提供。同时也支持处理任何 OAuth 认证提供方。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7.&lt;a href=&quot;http://pchart.sourceforge.net&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;pChart&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070629898600.jpg&quot; alt=&quot;pchart&quot; height=&quot;219&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;pChart 是一个 PHP 类库用来创建各种图表，可从 SQL 查询、CSV 文件以及手工提供图表的数据来源。强项是图表的渲染质量，该项目开发活跃，每周都有新特性和 bug 修复。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8. &lt;a href=&quot;http://phpthumb.sourceforge.net/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;Php Thumb&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457070629377688.jpg&quot; alt=&quot;phpthumb&quot; height=&quot;240&quot; width=&quot;625&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;PHP Thumb 是一个 PHP 的类用来生成图片的缩略图。 只需数行代码即可，支持多种图片来源，包括文件系统或者数据库，支持大多数图片格式。并可对图片进行旋转、剪切、加水印等等。可执行定义缩略图的质量，提供内建的缓存以降低服务器的负载。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('76', '史上最糟糕的两个变量名', '伯乐', '1', '36', '/Upload/article/56d92293df54a.jpg', '2016-03-04 13:52:19', '作为一个程序员，“起名字”是他们工作中非常重要的一部分。Phil  Karlton就说过：“在计算机科学领域，有两大难题，如何验证缓存和如何给各种东西命名。”虽然很难，但是每次在写代码的时候，给事物起名字又是不可 回避的工作。无论是程序变量名还是数据库表名或者是表里的列名，甚至是文件系统中的文件名，', '&lt;p&gt;作为一个程序员，“起名字”是他们工作中非常重要的一部分。Phil &amp;nbsp;Karlton就说过：“在计算机科学领域，有两大难题，如何验证缓存和如何给各种东西命名。”虽然很难，但是每次在写代码的时候，给事物起名字又是不可 回避的工作。无论是程序变量名还是数据库表名或者是表里的列名，甚至是文件系统中的文件名，以及你的项目名称、产品名称，给这些东西起名字可不是个轻松活 儿。&lt;/p&gt;&lt;p&gt;糟糕的命名方式随处可见。你会发现，有的变量名字起得太短，根本没法提供足够的描述信息。或许有这个问题的人都做过 TRS-80 &amp;nbsp;BASIC程序员，在这种BASIC语言里，无论你起多长的变量名，只有名字的头两个字母有效，所以那个时候的程序员不得不在键盘边儿上放个笔记本，以便 将很短的变量名称和他们的对应含义记录下来，这样才能不搞混淆。&lt;/p&gt;&lt;p&gt;有的时候，你会发现这样的命名方法：直接将变量名称中的原音字母省略，以此来缩短变量的长度。这种方法被用来替代常用的 “截断法”，也就是简单地把字母截断来缩短长度。比如你可以用$cstmr(原音省略)来代替$cust(直接截断). &amp;nbsp;但是，对于customers（顾客）和costumers（服装提供商）这两个单词来说，原音省略法就会造成混淆(customers和 costumers采用原音省略法，其结果都是cstmr)。更糟糕的是，$cstmr缺乏原音字母，打字的时候会更加别扭，而且从读音的角度来说，也很 难对其进行发音。&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img class=&quot;size-full wp-image-18320   &quot; title=&quot;tsr80&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1457070712655702.jpg&quot; height=&quot;300&quot; width=&quot;300&quot;/&gt;&lt;/a&gt; &lt;/p&gt;&lt;p&gt;TSR80计算机，只支持4位的变量名&lt;/p&gt;&lt;p&gt;还有一种人为的特殊命名方式，有的时候程序的作者只是为了小幽一默，所以起了些有趣的名字。我就曾经见到过有人把循环变量命名为$crap &lt;span style=&quot;color: #888888&quot;&gt;(crap在英语里是轻微的咒骂，跟damm,shit这种词语差不多——译者注)&lt;/span&gt;，我的一个同事告诉我，他在给一段代码做整理的时候，看到过有个函数被命名为:THE_LONE_RANGER_RIDES_AGAIN()。虽然这样的命名方式非常特殊，但是他们并不属于我说的“糟糕”的命名的范畴。&lt;/p&gt;&lt;p&gt;虽然我很清楚，对于命名规范这种事情，大家是公说公有理，婆说婆有理，但是，我还是非常自信地宣布，我认为，史上最糟糕的命名是：&lt;span style=&quot;color: #ff0000&quot;&gt;&lt;strong&gt;$data!&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;是的！必须是$data! 这个命名完全是循环定义，实际上就是一句废话。就仿佛你把你们家所有的东西都扔到一个行李箱里，然后在行李箱上面贴个条，赫然写着：“东西”&lt;/p&gt;&lt;p&gt;正确变量命名应该写清楚变量的数据类型。因此在命名是考虑数据类型是一个很好的提升命名质量的办法。我有一次在看一段读数据库表记录的代码时，看到了$data这个名字，大概像下面这样：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;123&lt;/td&gt;&lt;td&gt;&lt;code class=&quot;php variable&quot;&gt;$data&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= read_record();&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php functions&quot;&gt;print&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;ID = &amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$data&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;CUSTOMER_ID&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;];&lt;/code&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;如果这时候问问：“$data是什么数据类型呢？”,然后你就很想给它换个名字了。把名字改成$record是一个好的开始。进一步改成$custormer_record，就更好一些了。&lt;/p&gt;&lt;p&gt;模糊的命名相当糟糕，糟糕程度紧随其后的就是，长得几乎一样，无法分辨的变量名。因此，有史以来第二糟糕的变量名就是:&lt;strong&gt;&lt;span style=&quot;color: #ff0000&quot;&gt; $data2&lt;/span&gt;&lt;/strong&gt;.&lt;/p&gt;&lt;p&gt;总的来说，任何仅仅依靠数字编号来区分的变量名都应该被重构。马上举个例子给你看，你就明白了：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;12345678910111213&lt;/td&gt;&lt;td&gt;&lt;code class=&quot;php variable&quot;&gt;$total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$price&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;* &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$qty&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$total2&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;- &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$discount&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$total2&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;+= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total2&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;* &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$taxrate&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$total3&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$purchase_order_value&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;+ &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$available_credit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total2&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;&amp;lt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total3&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;print&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;You can&amp;#39;t afford this order.&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;你可以发现，要读懂这个代码就跟读甲骨文一样痛苦。很明显，这个程序的目的就是要计算订单的总花费，$total. &amp;nbsp;如果程序的逻辑没有问题，那么$total这个变量名也算是恰如其分。但是，偏偏有人修改了这个程序，给添加了计算折扣和税率的功能，然后他还在变量命名 上偷懒，直接起了个$total2, 更可恨的是，还有其他人在这个程序里计算了用户的可用账户金额，然后直接起名叫$total3!&lt;/p&gt;&lt;p&gt;真正倒霉催的是下面这行代码：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;1&lt;/td&gt;&lt;td&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total2&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;&amp;lt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$total3&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;如果你不回头看之前的代码，要想知道这句代码的意义是完全不可能的。所以你必须往回阅读，看看哪个变量究竟是什么意思。&lt;/p&gt;&lt;p&gt;如果你看到了类似像$total2这样的变量，那就应该把这个名字改得更加具体一些。花5分钟的时间让这些变量名称变得更加合理。这个层面上的软件重构是最简单，代价最小，也是最安全的，尤其是在你要修改的变量是个局部变量的时候。&lt;/p&gt;&lt;p&gt;让我们用最简单的“搜索-替换”功能来解决我们之前发现的问题&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;12345678910111213&lt;/td&gt;&lt;td&gt;&lt;code class=&quot;php variable&quot;&gt;$order_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$price&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;* &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$qty&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$payable_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$order_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;- &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$discount&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$payable_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;+= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$payable_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;* &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$taxrate&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php variable&quot;&gt;$available_funds&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$purchase_order_value&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;+ &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$available_credit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;( &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$payable_total&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;&amp;lt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$available_funds&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;print&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;You can&amp;#39;t afford this order.&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;经过修改后，唯一变化的就是变量名，而且代码变得简单易懂了。现在对于每个_total，就不存在二义性的问题了。看看我们发现了什么：原先if语句中的两个比较变量的位置写反了。有效的命名方法让我们能够更快地发现错误。&lt;/p&gt;&lt;p&gt;通常，我们都认为用数字作为变量的结尾是不好的命名方法，但是有一个例外。如果变量描述的实体本身就是以数字结尾的，那变量名最好也是以数字结尾。比如，如果我们要定义一个SHA-1哈希实体，那干脆就把它命名为$sha1,这样就很好，你完全没有必要把它搞成$sha_one，然后来避免在变量名中使用数字。&lt;/p&gt;&lt;p&gt;在我完成了对本文的第一版后，我创立了自己的命名规则，并使用Perl::Critic包来检测上述提到的两种命名问题。我制作的插件&amp;nbsp;Perl::Critic::Bangs&amp;nbsp;可以检测出这两种问题：ProhibitVagueNames&amp;nbsp;和&amp;nbsp;ProhibitNumberNames.&lt;/p&gt;&lt;p&gt;还有哪些其他的糟糕的命名方法逼得你发疯？你自己有去做些什么纠正这些错误么？&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('77', '对代码命名的一点思考和理解', '伯乐', '1', '49', '/Upload/article/56d923b7e3127.jpg', '2016-03-04 13:57:11', '一个软件最后都会落实到代码。而代码，其背后的架构设计或设计思想或模式固然重要，但我觉得更重要的东西则是良好的命名。混乱或错误的命名不仅让我 们对代码难以理解，更糟糕的是，会误导我们的思维，导致对代码的理解完全错误', '&lt;p&gt;一个软件最后都会落实到代码。而代码，其背后的架构设计或设计思想或模式固然重要，但我觉得更重要的东西则是良好的命名。混乱或错误的命名不仅让我\n们对代码难以理解，更糟糕的是，会误导我们的思维，导致对代码的理解完全错误。相反，良好的命名，则可以让我们的代码非常容易读懂，也能向读者正确表达事\n物以及逻辑的本质，从而使得代码的可维护性就大大增强，读命名好的文章是非常流畅的，会有一种享受的感觉。&lt;/p&gt;&lt;p&gt;另外一点也许大家还没感受到，那就是良好的命名，以及良好的命名习惯，由于我们总是对每个概念的名称要求非常苛刻，我们会思考这个名称所表达的概念\n是否正确，该名称是否正确表达了事物的本质或正确反映了某个行为的逻辑。所以，这种对命名的良好思考习惯，可以反过来帮助我们纠正之前的一些错误设计和代\n码实现；比如，你之前有一个地方可能命名不太准确，然后你发现后面有另一个地方需要用这个名字，且更合理。所以你会发现这个名字对前面的地方就不适合了，\n从而你会去思考前面的地方可能需要用其他的名字，或者你会发现前面的地方的设计根本就是有问题的。这种就是名字可以促使你思考你的设计是否正确的例子。&lt;/p&gt;&lt;h2&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;代码命名&lt;/a&gt;混乱或错误的主要原因：&lt;/h2&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;没理解事物的本质；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;理解了事物的本质，但不知道命名的重要性或者根本不屑于做好命名；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;理解了事物的本质，也知道命名的重要性，但没能力命名好事物；&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h2&gt;养成良好的命名习惯的一些想法：&lt;/h2&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;对自己的严格自律，自己写代码时要有一种希望把每个名称都命名好的强烈意识和严格的自律意识；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;要努力分析和思考当前被你命名的事物或逻辑的本质；这点非常关键，思考不深入，就会导致最后对这个事物的命名错误，因为你还没想清楚被你命名的事物是个什么东西；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;在有自律意识和一定的分析能力基础之上，注意命名的方法技巧；要知道何时用动词，何时用名词；以及形容词放哪里，动词放哪里，名词放哪里；也就是小学时的主谓宾要会用；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;你的任何一个属性的名字都要和其实际所代表的含义一致；你的任何一个方法所做的事情都要和该方法的名字的含义一致；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;从代码的命名可以看出写代码的人编程时思路是否清晰，如果你对一个名字的命名不准确，很可能体现出你还没有理解这个名字背后的东西；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;要让你的程序的每个相似的地方的命名风格总是一致的。不要一会儿大写，一会儿小写；一会儿全称一会儿简写；一会儿Pascal命名法，一会儿camel命名法或匈牙利命名法；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;不要出现重复的命名；因为通常名称都有嵌套关系，比如类在命名空间里，方法在类里，所有如果一个概念在命名空间里表达了，那就不必再类上再表达一次；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;对于属性或类名，应该总是名词在最后\n面，名词决定了这个属性代表什么，前面的部分都是用于修饰这个名词；比如，假如现在你有一个服务，然后又是一个关于订单的服务，那就可以命名为\nOrderService，这样命名就是告诉我们这是一个服务，然后是一个订单服务；再比如CancelOrderCommand，看到这个我们就知道这\n是一个Command，即命令，然后是什么命令呢？就是一个取消订单的命令，CancelOrder表示取消订单；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;对于方法，应该总是动词开头，名词结尾；比如Order.AddItem(orderItem);这个，表示订单类有一个添加订单项的方法，Add是动词，表示添加，Item是名词表示订单项；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;在C#中，我们一般用camel以及\nPascal命名法，而不是匈牙利命名法。我觉得主要是两个原因：1）VS强大的智能感知提示的存在，我们没有必要突出变量的类型了，但这个我觉得只是一\n个次要原因；2）真正的原因，我上面有提到，一个变量，名词是放在最后的，这个名词决定了这个变量代表什么。比如有个变量叫totalCount，我们一\n看就知道这是一个count，然后count一定是一个int或者long，所以就不需要在强调它的类型了。再比如，remotingRequest, \nhttpRequest，这种，我们也一看就知道他们是请求，一个是remoting的请求，一个是http的请求。remoting,http是用来修\n饰request的。request决定了这个变量是什么（同时就意味着我们知道了他的类型了），然后remoting,http这种是进一步说明该\nrequest的业务含义或当前上下文。就像disabledButton，我们一看就知道这是一个button，然后是一个什么Button呢，就是一\n个已禁用的button。所以，好的名称，本身就会让我们很容易知道该名称是什么东西，它的类型是什么，具有什么业务含义，所以没有必要再加类型缩写作为\n前缀；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;color: #333333; font-style: normal;&quot;&gt;多学习英文，多看国外优秀开源项目中的命名技巧，会对我们命名有很大帮助；&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;通过一些不太好的代码命名来分析一些简单的命名问题&lt;/h2&gt;&lt;p&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2014/09/ccf1b698600936fa8ad5948ac90c4169.png&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-77052&quot; alt=&quot;132318412159759&quot; src=&quot;/Upload/ueditor/img/1457071010762867.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;以上代码中，有很多问题，我们来一一分析：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;方法的参数，第一个字母，一会儿大写的P，一会儿小写的p，不一致；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;第二个参数后面出现多余的空格，不应该；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;_paramsTable这个参数为什么要出现下划线，而其他参数没有下划线，不一致；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;publishRequest属于camel命名法，而iSignCounter, sStageIsOK这种属于另一种命名法，这种命名c++中用的多，不一致；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;foreach循环中，参数名叫instParam，但是后面的集合叫arrParams4SignActions，更对称一点的，应该叫arrInstParam；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;方法的最后两行，出现多余的空格，导致代码格式排版混乱；&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;从上面的代码我们可以知道，仅仅是通过这些细节，就能发现很多问题。我们写代码时，只要多细心点，多注意点排版是否美观一致、命名是否统一，那代码写出来就会漂亮很多了。下面我们再看看其他的代码：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2014/09/f441e31af8717f54475cd9785fd17a4d.png&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-77053&quot; alt=&quot;132332351682183&quot; src=&quot;/Upload/ueditor/img/1457071010123820.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;上面的代码中，两个参数的命名也不一致，projectid中，i是小写，但是publishId参数，i却是大写，应该都统一为大写；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;ViewData中的key，一会儿是全部大写的UPDATE，一会儿是另一种命名，不一致；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;上面的两个红框标出来的if，虽然都是只有一行代码，但是一个有括号，一个没有括号，不一致；且第二个if里出现了多余的空行，格式混乱；&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2014/09/855ef6528f38578eae1c3176dc50bc3b.png&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-77055&quot; alt=&quot;132340191685294&quot; src=&quot;/Upload/ueditor/img/1457071011121253.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;上面的代码中，函数中，一会儿用IList，是一个接口，一会儿用Dictionary，非接口，不一致；应该都用接口，或者都不用接口；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;listOriginal和receiverList命名不一致，要么全部list开头，要么全部List结尾；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;foreach循环中，变量的类型叫TDMSOriginalRequirement，但是变量名却叫originalItem，而集合名称又叫\nlistOriginal，应该三者统一；比如foreach (Assembly assembly in assemblies)&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;+“…”这个地方没有用空格，加号两边应该要空格，这属于格式混乱，不严谨；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;createUser这个变量取的很不理想，create是动词，createUser合起来就是创建用户的意思，而他这里要表达的意思是创建人的意思，所以应该叫createdUser或者creator；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;为何originalItemFormat和originalItem的意思可以等价，不合理，如果等价，一开始就要命名为originalItemFormat；而且format是一个东西，动词放在最后，算个啥？&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2014/09/1a1af2a919f36d33deebb331f5db6fc8.png&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-77056&quot; alt=&quot;132358057626942 (1)&quot; src=&quot;/Upload/ueditor/img/1457071011126448.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;上面这个类的几个私有字段中，有些带命名空间，有些不带，要么都不带，要么都带；一般命名空间都是在上面声明，后续不需要出现；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;ILog logger；这一句有两个问题：1）logger为何没有下划线，不统一；2）为何类名叫ILog，而变量名叫logger，要统一，要么类名叫ILogger，要么变量名叫_log；&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;a href=&quot;http://jbcdn2.b0.upaiyun.com/2014/09/4bccb8daa3b21e3454f6d86897ac2e04.png&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-77057&quot; alt=&quot;140002070901351 (1)&quot; src=&quot;/Upload/ueditor/img/1457071011974511.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;上面这两个私有方法，一个是大写开头，一个是小写开头，不一致，混乱；应该要一致；&lt;/p&gt;&lt;h2&gt;总结&lt;/h2&gt;&lt;p&gt;通过上面的一些例子，我们知道，在我们不经意间，多写了一个空格或者一个空行，或者一个字母的大小写不一致了，都会导致命名的不一致；如果自己没有\n养成这种平时注重代码命名各种一致性的习惯，那写出来的代码很可能就是像上面那样。我觉得是非常糟糕的。上面我举的例子都只是简单的命名方面的，更深层次\n的命名问题，比如如何做到名称和其背后的实现内容一致，这个是需要我们平时不断修炼的。不是短时间内就可以做到那个程度。&lt;/p&gt;&lt;p&gt;我觉得，要做好命名，归根结底：&lt;/p&gt;&lt;p&gt;1）先要意识到命名的重要性；2）要端正态度，要认真的写代码；3）要努力推敲每个名称和其实际做的事情是否一致，也就是命名的准确性；4）要时刻注意命名的各种一致性；&lt;/p&gt;&lt;p&gt;养成良好的命名习惯不是为了别人，不是为了公司，而是为了提高自己的编程修养，提高自己认识事物的能力。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('78', '年轻程序员最需要学什么？自律！', '伯乐', '1', '65', '/Upload/article/56d92441aaaa4.jpg', '2016-03-04 13:59:29', '在过去的七年半中，我在Ronimo游戏公司指导过十几个程序员实习生，审阅了数百份简历。我发现他们中的大多数都需要学习一件事情。你可能以为这 是某一技术、算法、数学，或其它形式的某方面知识。', '&lt;p&gt;在过去的七年半中，我在Ronimo游戏公司指导过十几个程序员实习生，审阅了数百份简历。我发现他们中的大多数都需要学习一件事情。你可能以为这\r\n是某一技术、算法、数学，或其它形式的某方面知识。当然，他们的确需要弥补这些知识，但是在我看来，这些都不是最重要的。他们要去学习的最重要的一件事\r\n是：自律。这种自律体现在：编写尽可能清晰的代码；重构代码以消除因后续开发中的变化所造成的混乱；移除从未用过的代码并且添加注释。&lt;/p&gt;&lt;p&gt;我指导实习生的大部分时间不是在高级技术或引擎细节的解释上，而是让他们写出更好的代码。我总是会问实习申请者：要成为一名优秀的程序员，你们认为\r\n哪些是重要的？他们的回答通常是：代码要清晰，易懂，便于维护。这当然是我想听到的，但是很少有年轻的程序员能从一而终地去实践。&lt;/p&gt;&lt;p&gt;做到这些需要自律，因为这意味着代码不能停留于“实现了功能”。假设所有的变量都被随意地命名，代码依然能够完美运行，但是阅读性很差。从短期看，\r\n从“功能型代码”到“清晰型代码”带来的回报很少：代码原本就可以运行，对其清理之后代码仍然可以运行。这就是为什么需要自律来完成这一步，这也是为什么\r\n参加实习会很有帮助：一个好的导师会非常注重代码的质量（尽管不同的人对“好的代码”有不同的定义），从而要求实习生进一步改进完善，走到下一个阶段。&lt;/p&gt;&lt;p&gt;下面给出几个例子，这些是我在新手程序员所写的代码里经常看到的问题：&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;名不副实的函数/变量/类&lt;/h3&gt;&lt;p&gt;这些函数、变量、类所做的事情并不是他们名字所暗示的那样，这些名字具有欺骗性。显然名字应该反映真实的内容，但让我吃惊的是，名不副实这种情况常常出现。&lt;/p&gt;&lt;p&gt;举个例子，我最近偶然看到以前一个实习生写的两个类：EditorGUI 和 \r\nEditorObjectCreatorGUI，这代码本是用来处理编辑器里的界面。令我吃惊的是，创建新对象的按钮的代码放在了 EditorGUI \r\n里面，而EditorObjectCreatorGUI则是处理不同对象间的操作，这都跟名字所暗示的完全相反！尽管代码比较简单，但我花了好大一会儿才\r\n弄明白，因为我基于类名称作出了完全错误的假设。这个案例的解决办法很简单：重命名为 EditorObjectCreatorGUI和 \r\nEditorObjectNavigationGUI，仅仅做一小步就可以大大提高阅读性。&lt;/p&gt;&lt;p&gt;命名不准确这种情况我见到很多。之所以频繁发生，是由于代码在不断地演变。最初选择那个命名时可能是正确的，但一到代码完成之后，命名可能就变得不\r\n准确甚至错误的了。这个陷阱提醒我们应该始终把命名记在心上，在你添加一段代码的时候就要弄清楚，这与函数或类的名称是否相称。&lt;/p&gt;&lt;p&gt;推荐阅读：《程序员最头疼的事：命名》&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;混淆不清的类&lt;/h3&gt;&lt;p&gt;另一个问题是混淆不清的类，即一个类做了很多不相关的事情。当你长时间专注于同一块代码时，就可能这个问题。新功能用最简单的方法实现，到了某种程\r\n度，类就会变得臃肿，做了很多不相关的事情。有时候类变得臃肿不在于代码规模的大小：一个类可能只有几百行，但它却包含了不属于本类功能的代码。&lt;/p&gt;&lt;p&gt;举个例子，如果一个GUI类需要“分析哪些纹理可供使用”（设想有个按钮用于选择纹理），如果GUI类是唯一一个需要这种分析结果的类，那么在\r\nGUI类里实现它是很合理的。但是，这时一个完全不相关的gameplay类也需要这种分析结果的信息，因此你将GUI类传递给gameplay类来查询\r\n纹理信息。这个时候GUI类就多出一种东西了：它是GUI类，同时也是TextureAnalyser类。这个案例的解决方案很简单：从\r\nTextureAnalyser类分割出一个独立的类，这个类可同时被GUI类和gameplay类使用。&lt;/p&gt;&lt;p&gt;避免这种问题的最好方法是在每次写代码前三思：我在这里添加的功能跟类的名称符合吗？如果不符合，那么就要对类重命名，或者将其分割成独立的类，或者把这段代码放到其他的类中。&lt;/p&gt;&lt;p&gt;如果想不出来一个跟类非常匹配的名字，这通常是代码异味（Bad Smell）。如果找不到合适的名字描述这个类，可能因为它所做的事情太混杂了。这时可以将它分割成几个部分，并且每个部分用一个恰当的名字来描述。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;体积庞大的类&lt;/h3&gt;&lt;p&gt;这问题跟上面所说混淆不清的类很相似：随着时间的推移，越来越多的代码被添加到一个类里，使得其变得臃肿。在这种情况下尽管放在一个类是很合理的，\r\n但是类的体积变得很大。超大的类处理起来是很麻烦的，当很多代码对同一个私有成员变量进行操作时，bug就很容易出现，并且人也很容易忽略很多细节。&lt;/p&gt;&lt;p&gt;分割一个超大的类是件相当无聊的工作。当代码高度交错时，这也具有很大的挑战性。分隔代码需要高度的自律，因为这只是对已有的代码进行增加或修改而保持原有的功能不变。&lt;/p&gt;&lt;p&gt;Ronimo公司有一个规定，保持类的代码在500行以下，函数的代码在50行以下。有时候这是不可行也不合理的，但是通常来说，不管哪一个类或函\r\n数超出了这个规定，我们都会寻找办法将其重构或者分割成更小的，更易于管理的片段。（这让我很好奇：你觉得这个限制应该是多少行？可以在评论中留言。）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;代码注释&lt;/h3&gt;&lt;p&gt;实习申请人给我们发过来的样本代码几乎都有一些被注释的代码块，但并没有说明为什么会做这个注释。是代码存在错误需要修改吗？还是代码过旧需要更\r\n新？注释掉的代码为什么会在这里？当我们问起申请人时，他们对这些被注释的代码也显得很疑惑，但是奇怪的是，总会有一些原因不明的被注释的代码。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h3&gt;代码重复&lt;/h3&gt;&lt;p&gt;另一个我经常看到的问题是有相似功能的代码重复出现。&lt;/p&gt;&lt;p&gt;举个例子，从纹理名字也许可以看出这东西的用途，如TreeBackground.dds。为了知道这个纹理是否可以用于一棵树，我们检查以Tree开头的文件名。也许当使用SDK后我们能很快找到，使用&lt;em&gt;beginsWith(”Tree”)&lt;/em&gt;就\r\n行了。这个代码很短，如果需要用到它，直接粘贴到那儿就可以了。这就是代码重复，并且人人都知道代码重复是应该避免的，如果重复的代码很短，那么最吸引人\r\n的做法是直接复制粘贴。在这儿的问题很明显：以后如果要检查这个纹理是否适用于别的东西，我们就要进行散弹式修正，一个地方一个地方修正了。&lt;/p&gt;&lt;p&gt;通常比较好的做法是，如果代码功能特殊，不要去复制，而是把它放到一个函数里。尽管代码很短很短，并且调用一个函数比粘贴需要写更多的代码，但是你要学会这么做，这也需要高度的自律。&lt;/p&gt;&lt;p&gt;本文所讨论的主题很浅显，大多数人在上大学一年级的时候已经学过了。难就难在从知道这些东西到实际花时间遵循它们，再到把它们记在心里。这就是为什么所有在Ronimo实习过的人学到的最重要的东西不是知识，而是自律。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('79', '有教育意义的小故事', '网络', '17', '360', '/Upload/article/56dd412cc521c.jpg', '2016-03-07 16:51:56', '有一道测试题，从下面3个候选人中选出一位能造福人类的，你会选谁?第一个，他信 巫医和占，有两个情妇，有多年的吸烟史，而且嗜酒如命；第二个，曾经两次被赶出办公室', '&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 很有教育意义的故事&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;教育故事之一&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp; 有一道测试题，从下面3个候选人中选出一位能造福人类的，你会选谁?第一个，他信\n巫医和占，有两个情妇，有多年的吸烟史，而且嗜酒如命；第二个，曾经两次被赶出办公室，每天要到中午才肯起来，读大学时曾经吸过毒，每晚都要喝许多白兰\n地；第三个，曾是国家的战斗英雄，保持着素食习惯，从不吸烟，也不喝酒，年轻时没做过什么违法的事情。大多数人会毫不犹豫地选择第三个，但没想到却是错\n的。有史料记载:第一个是富兰克林.罗斯福，第二个是丘吉尔，第三个是希特勒。这不禁引起了人们的思索，没有想到，罗斯福和丘吉尔这样的大好人，身上还有\n这样的缺点；而希特勒，这个杀人的魔鬼，他的身竟还有如此多的闪光点。由此我们想到了孩子，如果我们相信每一个孩子都是未来的成功者，如后来的罗斯福和丘\n吉尔，他们所谓的一些缺点怎么也比不过早先的罗丘两人吧。所以孩子一时错误，不代表会什么，我们对孩子要增加信任和期待，教育孩子的全部秘密就在于相信孩\n子。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之二教育从尊重开始&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;一位从德国回来的女士谈及亲身经历的一件事，对于我们教育孩子颇有启示。一个星\n期天，她领5岁的儿子到公园玩。儿子用一只&amp;quot;纸飞机&amp;quot;换回了德国小朋友的一辆电动&amp;quot;小汽车&amp;quot;，这件事使母亲大吃一惊。因为那只纸飞机充其量只值5 \n美分，而这辆小汽车至少也要值20多美元。开始她以为儿子说谎，当找到小汽车的主人和德国小孩的妈妈时，这位德国母说:&amp;quot;小汽车是属于孩子的，该由孩子做\n主。&amp;quot;她还说:&amp;quot;你儿子喜欢，小汽车就归他了。过会儿，我会领着孩子上玩具商店，让他知道这辆汽车值多少钱，能买多少个纸飞机，这样他就不会第二次做这样\n的蠢事了。&amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;尊重孩子的选择，不干预、不阻止，也不单纯批评孩子的做法，而是采取有效措施，及时对孩子进行循循善诱的教育。我们的教育中正是缺少了对孩子的这种尊重。\n尊重孩子的选权，并不能保证孩子的每一次选择都是正确的，但他们毕竟还小呀!在这个过程中孩子肯定会摔几个跟头，走一段弯路，但孩子的选择能力却在一次次\n尝试中得以提高。让我们在教育中少一分盲目和粗野，多一份理智和科学，把爱变得更深沉一些、艺术一些。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之三让教育多份选择和智慧&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;著名特级教师袁浩在处理4个放学后贪玩抓虾的&amp;quot;小调皮&amp;quot;时，用了如下方法:袁老师事先对他们捉虾的地点做了了解，发现了那个&amp;quot;清浅的小溪&amp;quot;是没有危险的。\n于是当4个&amp;quot;小调皮&amp;quot;耷拉着脑袋，站在办公室里等着挨批时，袁老师只是笑着对他们说:&amp;quot;你们喜欢虾子，这好啊!大科学家达尔文、法布尔从小不都是昆虫迷\n吗?你们谁能告诉我，虾子喜欢歇在什么地方?你是怎么抓着的?抓虾用什么办法最好?虾子到底是什么模样、什么颜色的，头上有什么?脚有多少只?尾巴\n呢……&amp;quot;小调皮们傻了眼，只顾着抓，谁会注意到底是什么模样?看到孩子们傻了眼，袁老师又拍拍他们的肩膀说:&amp;quot;我本想让你们明天向全班同学介绍介绍的，可\n是你们……这样吧，你们再去抓一次，抓的时候，认真看看，好好想想，回来再讲给我听听，好不好?&amp;quot;孩子们相互望望便撒腿去了。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;孩子放学回家忙着捉虾，这事若落在我们手里可能会把他们的家长传来!左一个&amp;quot;不遵守纪律&amp;quot;，右一个&amp;quot;不注意安全&amp;quot;，通常会把孩子说得眼泪汪汪!因为我们所\n关注、所思考的只是&amp;quot;放学不回家捉虾&amp;quot;这个事情的性质，至于孩子们捉虾的危险大不大，坏事能不能变成好事，我们很少会去关心和注意的。在这一点上，袁老师\n做了有心人。他通过孩子们对虾的特殊&amp;quot;情结&amp;quot;，引导他们进行观察、思考、探索、教孩子们如何玩得更有意义。教育就是这样，有时只要我们的多些了解和思考，\n就可能多份选择和智慧。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之四两种思维的碰撞&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;一天，富有的父亲带着儿子从城里去乡下旅行，想让他见识一下穷人是怎样生活的。\n在农场一户最穷的人家里，他们度过了一天一夜。旅行结束后，父亲问儿子:&amp;quot;旅行怎样?&amp;quot;&amp;quot;好极了!&amp;quot;&amp;quot;这回你应该知道穷人是什么样了吧?&amp;quot;儿子回答:&amp;quot;是\n的，我知道了&amp;quot;。&amp;quot;你能描述一下富人和穷人的区别吗?&amp;quot;儿子想了想说:&amp;quot;我们家里只有一条狗，可是他们家里却有4条狗；咱家仅有一个水池通向花坛中央，可\n他们竟有一条望不到边的小河；夜里我们的花园里只能看见几盏灯，可他们的花园上面却有千万颗星星；还有，我们院子里只能停几辆小汽车，可他们院子里却能容\n得下几百头奶牛。&amp;quot;儿子说完，父亲哑口无言。接着儿子又说:&amp;quot;等我长大了，一定过上和他们一样富裕的生活。&amp;quot;孩子心中富有和成人心中的富有是不同的，正像\n孩子心中的快乐和成人心中的快乐不同一样。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;今天要想真正走进新课程，教师一定要走进学生的心灵，把握学生思维方式。知道学\n生的理解与想象和教师是不同的，了解学生的内心世界。研究学生已有经验、认知能力、心理特征，认识学生思维，学生才能带着教科书走进教师，这样课堂教学中\n两种思维的碰撞才能会如&amp;quot;水&amp;quot;如&amp;quot;潮&amp;quot;，心灵对话才会撞击出生命的浪花。在那流淌轻松活力、润泽生命的课堂里快乐、健康、和谐地成长。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之五 潜能&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;传说苏格兰北部的古城堡和蓦地经常闹鬼。有一个人因为公务繁忙半夜时分才回家，而他又必须经过蓦地。蓦地里有偶尔传来令人毛骨悚然的号叫，蝙蝠幽灵一样无\n声地飞来飞去，枯枝像鬼怪的手爪伸向暗黑的天空。走着走着，他一不小心掉进一个新挖的墓坑里。这个坑太深太大了，人高马大的他几经努力还是爬不出去。他索\n性坐在那里，等天亮再说吧。这时，又有一个人经过这里，也不小心掉了下去，这个人使出吃奶的力气也爬不出去。先掉下来的人实在看不过去，开口说:&amp;quot;别费力\n气了，你是爬不出去的。&amp;quot;那人忽然听到说话声以为是遇到了鬼，吓得魂不附体，手脚并用，没想到三两下竟然爬了上去，然后惨叫着逃得无影无踪。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;每个人的潜能都是深不可测的，我们没有理由给任何人妄下断语-------&amp;quot;你\n是庸者，你是天才&amp;quot;。谁知道一个人的潜能在什么时候爆发出来?值得思考的是，平常的生活里，&amp;quot;平庸&amp;quot;的人很少爆发自己潜能因而显得平庸，&amp;quot;聪明&amp;quot;的人则会\n让自己的潜能像山泉一样长流常新地表现出来，因而不同凡响。其实，我们全部可以做出不同凡响的事，因为我们的潜能就像埋藏起来的石油，一直没有得到开发。\n作为一名教师就是要作这种&amp;quot;潜能&amp;quot;的挖掘者、激发者，使每一名学生身体里的潜能都得到充分的发挥，每一个教师都能做到这一点，那就是整个教育的成功。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之六你不能施舍给我翅膀&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;在蛾子的世界里，有一种蛾子名叫&amp;quot;帝王蛾。&amp;quot;帝王蛾的幼虫时期是在一个洞口极其\n狭小的茧中度过的。当他的生命要发生质的飞跃时，这天定的狭小通道对它来讲无疑成了鬼门关。它娇嫩的身躯必须拼尽全力才可以破茧而出。太多太多的幼虫在往\n外冲杀的时候力竭身亡，不幸成了&amp;quot;飞翔&amp;quot;这个词的悲壮祭品。有人怀了悲悯恻隐之心，企图将那幼虫的生命通道修得宽阔一些。他们拿来剪刀，把茧子的洞口剪\n大。这样一来，茧中的幼虫不必费多大的力气，轻易就从那个牢笼里钻了出来。但是，所有因得到了救助而见到天日的饿子都不是真正的&amp;quot;帝王蛾&amp;quot;-------\n它们无论如何也飞不起来，只能拖着丧失了飞翔功能的累赘的双翅在地上笨拙地前行!原来，那&amp;quot;鬼门关&amp;quot;般的狭小茧洞恰是帮助帝王蛾幼虫两翼成长的关键所在，\n穿越的时刻，通过用力挤压，血液才能顺利送到蛾翼的组织中去；惟有两翼充血，帝王蛾才能振翅飞翔。人为地将茧洞剪大，蛾子的翼翅就失去充血的机会，生出来\n的帝王蛾便永远与飞翔无缘。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;现在的青少年就需要这种磨练，他们惧怕那黑黑的隧道，总是渴望有一双援助的手将\n他们一路护送；现在的家长也多是&amp;quot;怀了爱怜之心&amp;quot;的父母，总是怕孩子吃苦受累，于是把孩子的&amp;quot;生命通道&amp;quot;修得特别宽畅，让孩子&amp;quot;自由快乐&amp;quot;地成长；现在的\n老师也大有&amp;quot;怀了恻隐之心&amp;quot;的，他们怕学生做不出&amp;quot;标准答案&amp;quot;，怕学生的答案与自己的要求相差太远，居然把答案一字不差地读给学生!这样下去，将来的社会\n上，会有多少能够展翅飞翔的&amp;quot;帝王蛾&amp;quot;呢?孟子曰:&amp;quot;天将降大任于斯人也，必先苦其心志，劳其筋骨，饿其体肤，空乏其身，行拂乱其所为……&amp;quot;今天的青少年\n吃点苦不算什么，&amp;quot;不经历风雨怎能见彩虹&amp;quot;?父母和老师一定要为孩子的未来着想，给他们锻炼的机会，给他们锻炼的空间，这样他们才会在未来的社会上凭借自\n己的能力占据一席之地，这样他们才能用坚强的臂膀托起未来中国绚丽的朝阳。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之七头鱼&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;这是一条很不起眼的小鱼儿，很瘦小孤单&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;主人喂食时它总也抢不到，最多只能吃些别的鱼剩下的残渣碎末。平时它常孤零零地在鱼外游荡，似乎谁都可以欺负它。万幸的是，终于有一天主人发现它是如此可\n怜，打那以后喂食时都先在它身边撒些饵料。不想这一些微不足道的举动，竟改变了小鱼的命运，还创造了一个奇迹。因为聪明的鱼儿们渐渐地发现:只要在小鱼的\n周边就一定能得到食物。于是一星期以后一个颇为壮观的场面出现了:一条不起眼的小鱼，领着一个偌大的鱼群在水中游来游去。这条小鱼儿竟然成了&amp;quot;头鱼&amp;quot;。从\n此它再也不会以自卑的神色黯然地徘徊在池边、角落，而是摇头摆尾、神气活现地做着&amp;quot;领头鱼&amp;quot;，俨然开始统帅起整个鱼群。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;主人不是教育学家，他完全可以用这样的方法，把任何一条鱼都培养成&amp;quot;头鱼&amp;quot;&amp;quot;统帅&amp;quot;。同样道理，如果我们对学生多一些关注，哪怕他是一个&amp;quot;差生&amp;quot;，也很有可能让他走出困境、窘界。能否创造出更大奇迹，在孩子本身，更在于你我的关注。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;教育故事之八习惯的力量&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;有兄弟俩，一个出奇的乐观，另一个则非常的悲观。他们的父母想让他们的性格都改\n变一些，于是就把那个乐观的孩子锁进了一间堆满马粪的屋子里，把悲观的孩子锁进放满漂亮玩具的屋子里。过了一个小时，当他们打开悲观孩子的屋门时，发现他\n坐在一个角落里，正在伤心地哭泣，原来他怕不小心弄坏了玩具，受父母责骂。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;当他们打开满是马粪的房间时，发现那乐观的孩子正在兴奋地用一把小铲子挖着马\n粪，并不断地把散乱到门口的马粪铲干净。看到进来的父母，那乐观的孩子像发现奇迹似的嚷到:&amp;quot;爸爸，看!这儿有这么多马粪，附近肯定会有一匹漂亮的小马，\n我要给它清理出一块干净的地方来!&amp;quot;这个乐观的孩子就是后来的美国总统里根，他从报童到好莱坞明星，再到州长直至走上总统的宝座，乐观一直伴随着他。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;著名心理学家维纳第的研究表明，人们对外界事物所抱有的稳定的情感和态度，大多\n由他们从小习惯化的情感归因模式所决定。习惯是人们心理内部的一种顽强而巨大的力量。培根认为&amp;quot;一切天性与诺言都不如习惯更有力量&amp;quot;。学生的各种习惯是在\n有意或无意之中多次重复而产生的，许多有益的好习惯，往往比坏习惯更难形成。我们的教师，要注意培养学生的各种良好习惯，因为形成良好习惯，将使人受益终\n生。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之九 造句与&amp;quot;造人&amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;这是小学一年级的一堂说话练习课，练习用&amp;quot;是&amp;quot;字说话造句。&amp;quot;动物是人类的朋友。&amp;quot;&amp;quot;爸爸是老师&amp;quot;。&amp;quot;大象是陆地上最大的动物。&amp;quot;……孩子们纷纷发表&amp;quot;见\n解&amp;quot;。就在这时，有个叫刘涛的孩子站了起来，他是个有名的捣蛋鬼，班上的女孩子见了他就赶紧躲开。&amp;quot;我是好……&amp;quot;听得出，他想说&amp;quot;我是好孩子&amp;quot;，可是没等\n他说完，同学们已哄堂大笑。不少学生在窃窃私语:&amp;quot;哼，他也能是好孩子?&amp;quot;&amp;quot;他还有脸说!&amp;quot;……看着刘涛同学尴尬的样子，张老师示意他坐下。谁知刘涛却站\n在那儿不愿意坐下。于是，张老师便鼓励他想一个更&amp;quot;合适&amp;quot;的句子。果然，刘涛开口说:&amp;quot;我是个坏……&amp;quot;正当大家等他说自己是个&amp;quot;坏孩子&amp;quot;的时候，他却突然\n停了下来，脸涨通红。很显然，他不甘心承认自己是&amp;quot;坏孩子&amp;quot;。此时，张老师又满怀柔情地鼓励他再来一次。小刘涛从老师的目光中获得了动力，他内心深处发出\n一个响亮的声音说:&amp;quot;我不是一个坏孩子!&amp;quot;这时，张老师情不自禁地带头鼓起掌来。而小刘涛的眼里已噙满了泪水。下课后，张老师把小刘涛叫到办公室跟他谈\n心。张老师还没开口，刘涛却主动地说:&amp;quot;老师，我以后一定做个好孩子。&amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;我们可以想象小刘涛以后的发展变化。人最重要的是自尊，是别人对自己的尊重和信\n赖。即使是后进生，也同样看重自己在别人心目中的位置。培养学生独立和完整的人格，首要的是营造一个平等、友善、祥和的生活环境。在这里，小刘涛&amp;quot;灵活&amp;quot;\n运用&amp;quot;是&amp;quot;字的造句过程，实质上是一个自我价值的体现并逐步完善的过程，在否定之否定中，肯定了自我，这不仅表现了他的勇气，也显示了他的机敏。而张老师\n更是慧眼识珠，通过热情的鼓励、善意的引导使小刘涛最终从&amp;quot;坏孩子&amp;quot;变成一个好孩子。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;一次寻常的造句，演绎成非同寻常的&amp;quot;造人&amp;quot;，这便是师者的爱心和育人的艺术，令人叫好。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十上帝施舍的天堂&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;曾经看过一幅儿童画：一个鱼缸，里面游动着六条漂亮的大尾巴金鱼；在鱼缸的四周，\n围着六只眼睛发亮的小猫，心想：六比六，还省得争抢打架。但是看看那幅儿童画下面歪歪扭扭的题字，居然是：太美了，舍不得吃！不禁为自己精神的庸俗而惭\n愧。一日课间休息时，一个平日比较调皮但很聪明的小男孩，跑到我跟前仰着头好奇地问：＂老师，太阳会生小孩吗？人会生小孩，小猫、小狗会生小孩，那么太阳\n会不会生小孩呢？＂我一时语塞，本想胡乱地搪塞过去，转念一想，不由计上心来，于是对他说：＂老师明天回答你好吗？＂第二天的文体课上，我给他们上了一堂\n别开生面的课，专门介绍太阳。下课后，我问那个小朋友：＂你心中的疑团解开了吗？＂他会心地朝我点了点头。走在熙熙攘攘的大街上，一个坐在妈妈自行车后座\n上的孩子突然指着一幅巨大的广告牌高声喊道：＂谁、找、你！＂那个孩子得不到妈妈回应，拽了一下妈妈衣服，继续不依不饶地指着广告牌说：＂妈妈，你看呀，\n谁、找、你！＂那位妈妈似乎明白了什么，猛地回头捂住孩子的嘴。原来孩子所谓的＂谁找你＂是＂雅戈尔＂的误读。其实那位妈妈真的大可不必，孩子并不是在存\n心丢你的脸，虽说他没有读对一个字，但他那大声读出三个半边字的热情与勇气不是很可贵吗？&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;在这物质的世界里，我们的精神有时会觉得恍惚无依。我们眼看着自己的纯真与热忱、\n敏感与同情一点点被剥蚀、被风化。在＂早熟＂成为全球性流行疾病的今天，让我们悉心守护那弥足珍贵的童真与童趣，并让自己知道，这童真与童趣，是上帝送给\n人类最后的礼物，是上帝通过孩子施舍给我们的美丽天堂。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十一不可能有有答案的&amp;quot;答案&amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;德国著名数字高斯在上中学时，有一次在数学课上打瞌睡，下课铃声响起时，他醒了\n过来，抬头发现黑板上有一道数学题，以为是当天的家庭作业，便匆匆地抄到练习本上。回家后，他埋头演算，就是算不出来，但他不气馁，不妥协，锲而不舍，终\n于求得了答案。他把答案带到课堂，老师见了大吃一惊，瞠目结舌，原来那是一道一直被认为&amp;quot;无解&amp;quot;的数学题。这是一个真实的故事，又是一个耐人寻味的故事，\n从这个故事中我们会感悟到；如果高斯真的知道此题&amp;quot;无解&amp;quot;，他还会去如此&amp;quot;劳神&amp;quot;吗?&amp;quot;答案&amp;quot;恐怕仍为&amp;quot;千古之谜&amp;quot;。一件事，如果在一个人的意识中被认\n为&amp;quot;不可能&amp;quot;，在行动上自然不会去做，&amp;quot;结果&amp;quot;也就真的&amp;quot;不可能&amp;quot;。理论是行动的指南，有什么样的教育理念，自然会产生什么样的教育行为，也就会产生什么\n样的教育结果。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;中小学生正处在发育期和成长期，他们的行为习惯、道德品质、思维意识是完全可塑的，是有充分潜力可挖掘的，关键是我们能否去发现、去挖潜、去设计、去雕琢。我们要牢牢记住，千万不可犯&amp;quot;盲人摸象&amp;quot;这一令世人耻笑的错误。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十二孩子的答案&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;这是一道重在考查三年级小学生思想品德的题:&amp;quot;在书店里买书，营业员阿姨多找你\n10元钱，你会怎么办?&amp;quot;此题的成人格式化的答案是:退回给营业员阿姨!否则，答题的小学生便思想品德有问题。可是孩子的答案却丰富多彩:我退给营业员阿\n姨3元，剩下7元再过买一本书；我回家交给我妈妈，因为妈妈下岗了；我去交给警察叔叔，可警察叔叔自己买糖吃怎么办?我还给营业员阿姨，但阿姨必须给我买\n一支冰淇淋；我用钱去玩儿游戏机……事实上，10元钱检验不出一个孩子的思想与品德。但如果用10元钱来检验一个成人的思想与品德却应该很有效。我想，如\n果让&amp;quot;河北第一秘&amp;quot;李真来回答，他肯定会说&amp;quot;退还给营业员阿姨&amp;quot;，如果让胡长清、张二江之流来答，也必定是&amp;quot;退还给营业员阿姨&amp;quot;。在10元钱的砝码面前，\n伪君子恰恰在&amp;quot;正确&amp;quot;的答案里暴露出自己灵魂的阴影、道德的败坏；而孩子们同样暴露的是童年的天真烂漫、心无杂念。当然了，也许这种对比缺乏说服力。可是\n当我看到孩子&amp;quot;我回家交给妈妈，因为妈妈下岗了&amp;quot;这一答案时，我的心为这个孩子对母亲的关爱而震动!我想，这个孩子的思想品德是没有任何问题的。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;另外一个老掉牙的脑筋急转弯:树上有3只鸟，一枪打死1只，树上还剩几只鸟?所\n有成人及所有被成人误导的孩子们都会哈哈一笑:笨死了，当然一只也没剩!&amp;quot;&amp;quot;树上还剩两只鸟。&amp;quot;这是一个二年级小女孩的答案。面对全班同学的哄堂大笑，这\n位小女孩说:&amp;quot;树上3只鸟分别是鸟爸爸、鸟妈妈和鸟宝宝。鸟爸爸被坏蛋一枪打死了，鸟妈妈吓飞了；鸟宝宝还不知道害怕，所以没有飞，飞走后的鸟妈妈看到鸟\n宝宝没有跟过来，就飞回来救鸟宝宝。所以说，树上还剩下2只鸟，鸟妈妈和鸟宝宝。&amp;quot;值得庆幸的是，小女孩碰到了一个有思想、有品德的老师，在全班同学的哄\n笑中，老师认真地鼓励了小女孩:&amp;quot;你真是有爱心的孩子，你回答得太好了!&amp;quot;。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;在程式化的答案面前，我们都在渐渐适应机器的习惯，而忘记了我们本身，忘记我们\n是一个会思考的真真正正的人。并且在这可怕的循环里，一批批成人正在复制着一批批同样的孩子。当个别孩子用残存的想象力去突破成人钢筋水泥般的思想限制\n时，他们获得的往往是讥讽与嘲笑，而不是认可与鼓励!所以孩子们的想象力在渐渐退化、消失，连同同情与爱心一起，最终变得麻木无知，仅仅剩下一副毫无生气\n的思想品德僵化的躯体。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十三批评，原来可以如此幽默&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;这是一节自习课，学生们都在座位上专心地阅读、写字，我站在讲桌前批改他们的练习\n册，当改到小康的作业时，我不禁皱起了眉头：字又宽又大，格子塞得满满的。于是，便随口说了一句：＂小康啊，你的字该减肥了。＂事情就这样过去了，我丝毫\n没想到的是，下课后，几个平素快嘴的孩子将我围住了：＂老师，你今天批评小康真幽默，我们喜欢你这样说话。＂体育委员帅帅说道：＂老师，我们犯错误时，你\n要能像今天批判小康那样，我们肯定喜欢你了。真的，我们喜欢这样的批评。＂&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;幽默，创设了和谐融洽的教育情境，它让本来难堪的批评变得友好起来，更为重要的\n是，幽默传达了监督对学生的尊长和信任，正如老舍先生所说：＂幽默者的心是热的。＂幽默的老师不仅不认为学生的过错行为是出自恶劣的动机，有时还假设动机\n是善良的，这种＂文过饰非＂的假设，保护了学生的自尊心，更能激发起学生改过向善的强烈愿望。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十四批评与鼓励&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;上小学时，我很淘气、很好动，成绩也不好。记得二年级的时候，有一次老师布置了许多数学作业。放学后，比我高两年级的姐姐为了能多和我玩一会儿，就帮我把\n作业都写好了。第二天交出作业以后，我一直忐忑不安地等着结果。熬到数学课，老师将作业本发到了每一位同学手里，惟独没有发给我和另一位成绩很好的同学。\n只记得当时我脸色通红，觉得肯定被老师发现了。于是，我低着头，紧张地等着老师的&amp;quot;判决&amp;quot;。出乎意料的是，老师当着全班同学的面，将我和那位同学的作业本\n展示出来，并且说:&amp;quot;同学们，这是老师批改时遇到的最工整、最准确的两本作业，希望大家以后多向这两同学学习!&amp;quot;然后说出了我和那位同学的名字。在同学们\n羡慕和怀疑目光的注视下，我真是不知道该怎么办才好。下课后，老师单独将作业本交给我，意味深长地对我说:&amp;quot;你是个聪明的孩子，老师希望以后你能够自己将\n作业完成得像今天这么好。&amp;quot;那天以后，每次写作业时，我都格外认真，甚至超过了姐姐的笔记，成绩也不断得到了提高，甚至其他学科也都有了起色。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;中学时，有一次英语老师点名让我朗读一节课文。那节课文是我花了很大心思预习过的。读后，当我满怀期待地等着老师的肯定与赞扬时，得到的却是这样的一句\n话：“你读的这是什么？是英语还是念经，你的口语太差了，以后要多锻炼……&amp;quot;伤心的我从那次起就不再喜欢英语了，直到现在英语仍然是我最最弱的一项。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;事隔十几年了，对那位曾经鼓励我的数学老师仍心存感激，而那位英语老师的话我仍然记得清清楚楚。如今，我也成为了一名光荣的教育工作者，忆起往事，实在无\n意指责谁，只是想敬告我的同仁们：您的肯定与鼓励，您漫不经心随口而出的一句批评，对您学生产生的作用和后果是多么大癌…&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十五生命中最初的痛&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;那一年他刚刚满7岁，读小学二年级。一天下午，老师组织学生在校园地里采摘还未成熟的青青豆荚，他个子最矮也最卖力。孩子天生好馋，何况又是饥饿年代，劳\n动过程中，全班同学几乎都私下偷偷往嘴里塞了几枚青豆，惟有他，咽回去一次又一次溢出的涎水，任凭饥肠辘辘，任凭胃壁痉挛，始终不曾让自己吞下一枚豆子。\n&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;放学时，老师逐一盘诘这些不谙世事的孩子，每个孩子都胆怯怯地默认了，惟有他挺\n胸、抬头、干脆爽朗答道:&amp;quot;没有，一颗都没有。&amp;quot;老师死死地盯着他，再次厉声责问，他的声音明显低沉了下去。&amp;quot;一点不老实，小小年纪就撒谎。&amp;quot;他被牵着耳\n朵推搡，其他同学回家了，惟有他被留下了，流着泪……&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;诚实的语言需要有信任的耳朵来倾听，更需要量有善良的心来领会。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十六你的眼神它能察觉到&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;春秋时期，一位有名的智者路过一片稻田。他看到一个农夫赶着两头牛奋力地耕田，就随意地问道:你的哪头牛干活更好一些?老农没有答话，等到了田边他给两头\n牛拴好、喂上料，才来到智者身边，背对着牛小声对智者说:就那头正摇尾巴的家伙，干起活来特卖力。智者大为不解:我方才问你，你怎么不说呢?现在又为什么\n这么小声对我讲?难道怕谁听到吗?老农一脸正色道:不错，就怕旁边的那头牛听到我夸别人而冷落它，你的眼神它能察觉到，而后这家伙就会闹情绪、不好好干活\n了，其实牲口和人一样，都愿意被别人夸、被大伙称赞。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;我们在为人师、为人父母时，是否也能像老农那样，考虑到孩子们的感受呢?试想一\n句:&amp;quot;同学们，这次考试平时咱班最差的李某某都考了70多分，看来咱班的确是有了进步&amp;quot;，真能激励他们吗?&amp;quot;说者无意，听者有心&amp;quot;，此刻又在挫伤谁呢?愿\n我们用真心去赞赏，激扬起更多孩子的信心和勇气，而不是在无意中制造一份不必要的伤痛与苦楚。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;教育故事之十七神秘的礼物&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;这是发生在小男孩初中三年时的故事。小男孩的班级刚换了一位新班主任，这对有点\n玩世不恭的他并不感到新鲜。第一次开班会，老师没有说太多，只是反复强调一点:凡是月考成绩好的，或者进步幅度大的，都会得到一分他送的神秘礼物。起初，\n小男孩以为这又是一位爱发神经的班主任在搞什么花样，所以不是很在意。第一次月考结束，老师果然拿出5份包裹严密的礼物，赠给了5名表现优秀者。大家都很\n想知道是什么，就怂恿获奖者打开，可是，被老师阻止了。他要求获奖者必须到家后再打开，且只能一个人看。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;第二天，满以为礼物之谜会立即揭开，可是，不管管怎么问，哪怕是最好的朋友，他们都不说。而且，与以往不同，看上去他们各个精神抖擞，神采飞扬。两次、三\n次，每次月考过后都重复着相同的故事。&amp;quot;神秘的礼物&amp;quot;就这样成为这个班每个人心中一份沉甸甸的渴望。但大家都清楚，要想得到它，必须付出艰辛的努力，取得\n更大的进步。为了得到这份&amp;quot;神秘的礼物&amp;quot;，几乎是名列班级最后的小男孩也在默默地努力着。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;天道酬勤。终于，在中考前一个月的模拟考试上，小男孩力压群芳，位居班级第一。\n拿到了老师那份&amp;quot;神秘的礼物&amp;quot;，他欣喜若狂。回到家，他迫不急待地打开层层包裹，看到 \n一个精致的小瓶，里面有一张小纸条，拿出来一看，上面写着:我知道你很优秀，所以相信会努力改变自己，并能严守&amp;quot;礼物&amp;quot;的秘密，对吗?看完礼物，小男孩会\n心地笑了。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;中考时，带着纸条上的那句话，小男孩顺利地考上了县内重点高中。再后来，他成为一名著名的律师。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体 &quot;&gt;&amp;nbsp;对于任何一件事情，&amp;quot;让他做&amp;quot;和&amp;quot;他要做&amp;quot;，其最终的结果会截然不同。教育者更多的时候应该把精力放在二者的转化上。只有这样，教育的效能才能最大化。 &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('80', '23个发人深省的教育故事', '网络', '16', '326', '/Upload/article/56dd4457317c1.jpg', '2016-03-07 17:05:27', '儿子很不满意自己的工作，他忿忿地对父亲说：“我的上司根本不把我放在眼里， 改天我要对他拍桌子，然后辞职不干。”父亲说：“我建议你好好地把你们公司的一切贸易技巧、商业文书和公司组织完全搞通', '&lt;p&gt;&lt;strong&gt;一、父亲的策略&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　儿子很不满意自己的工作，他忿忿地对父亲说：“我的上司根本不把我放在眼里，\n改天我要对他拍桌子，然后辞职不干。”父亲说：“我建议你好好地把你们公司的一切贸易技巧、商业文书和公司组织完全搞通，甚至连怎样修理影印机的小故障都\n学会，然后再辞职不干。这样你将公司当作免费学习的地方，什么东西都通了之后，再一走了之，不是既出了气，又有许多收获吗？”&lt;/p&gt;&lt;p&gt;　　儿子听了父亲的建议，从此默记偷学，甚至下班之后，还留在办公室研究商业文书的方法。一年之后，父亲问起儿子的工作：“你现在大概多半都学会了，准备拍桌子不干了吧！”&lt;/p&gt;&lt;p&gt;　　“可我发现这半年来，老板对我刮目相看，最近更是委以重任，如今我已经成为公司的红人了！”儿子自豪地说。&lt;/p&gt;&lt;p&gt;　　“这是我早就预料到的，”父亲笑着说，“当初，你的老板不重视你，是因为你的能力不足，却又不努力学习，而后来你痛下苦功，当然会对你刮目相看。只知抱怨上司，却不会反省自己，这是人们常犯的错误。”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;二、这不是理由&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　初三时，第一天，上生理卫生课，老师就在后面的墙上挂了一幅人体解剖图。图上标明了重要的骨骼、肌肉的名称和部位。整个学期那幅图都挂在那里。&lt;/p&gt;&lt;p&gt;　　期末考试时，我们一走进教室，那幅解剖图便被收了起来，整张试卷只有一道试题：“列举人体各主要骨骼的名称和部位。”&lt;/p&gt;&lt;p&gt;　　我们几乎异口同声地提出抗议：“我们从来没有学过。”&lt;/p&gt;&lt;p&gt;　　“这不是理由”老师微笑着说，“那些知识已经挂在墙上好几个月了。”我们勉强回答了一会儿以后，老师便把试卷收起来，然后撕得粉碎。“永远记住。”他忠告我们，“教育不仅仅是学别人告诉你的东西。”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;三、假如你是老师&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　一天早读，去教室辅导，看到后排有一同学趴在桌上，我顺手拿起一本书朝他背上轻轻拍了一下，他猛抬头，摔出一句：“干什么？我没有睡觉！”满脸骄横。当\n时我恨不得立即赶他出去。此时，同学们并不知道我们之间发生了什么，我尽量克制自己，努力保持平静的心态，微笑着说：“那你说，你趴在桌上，老师该怎么\n办？噢，对了，听说你的作文写的不错，你写一篇《假如我是老师》的文章吧。”当时，他没有说话，我继续在教室里巡回辅导。当天午饭后，这位同学到办公室找\n我，说：“老师，我错了，我不该那样没礼貌。”看得出，他是真心悔过，我自然原谅了他。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;四、邮局“不负责任”&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　有位女生，爱上了自己的老师。无法自拔的她忍不住给老师寄去了信。然而，信投进邮箱，女生立即后悔了：老师已经有了一个幸福的家庭，自己还那么小，老师\n看完信后会怎么看待？要是让同学、父母知道了，自己怎么面对？女生越想越怕，原来“炽热”的爱霎时被恐惧吓得无影无踪。为能躲过此劫，她甚至想到了死。可\n是日子一天一天地过去，老师表情一如往常，他照样春风满面地上课。&lt;/p&gt;&lt;p&gt;　　有一天，在和学生聊天时，老师指责邮局“不负责任”，他的信件经常遗失。直至此时，女生才全身释然：自己的那封信被幸运“丢失”了。&lt;/p&gt;&lt;p&gt;　　数年后，当大学毕业的该生去拜访自己曾爱上现在仍很尊敬的这位老师时，老师将保存了多年的这封信交给了她。这时，她才恍然大悟，连忙站起来向老师鞠躬：“谢谢老师！谢谢老师！”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;五、没有负担&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　有这样一个家庭，儿子的成绩很不好而且越来越差，有一次考了全班最后一名。父亲接过儿子的试卷微笑着说：“太好了儿子，以后你没有什么负担了！”儿子大\n吃一惊，忙问：“爸爸，你是不是病了？”父亲说：“没病，你想一想，一个跑在最后的人还有什么负担呢？不用担心再有人超过你，但你只要往前跑，肯定有进\n步。”儿子受到启发，心里就高兴起来。第二次，考了全班第29名，父亲看到了儿子的试卷，兴奋地说：“太好了儿子，你比上回前进了十几名了！”第三次，儿\n子考到了全班第10名，父亲激动地说；“太好了儿子，你真了不起！离第一名只差9名了。”就这样，不管孩子的成绩是好是坏，父亲总是坚持正面激励，从不训斥责骂，而孩子却发生了意想不到的变化。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;六、是你教我的&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　有一天，母亲和孩子到一栋大厦去办事，走进大厅时，电梯正在进人，她们加快脚步想赶上这趟电梯。这时从后面走进一位老婆婆，年轻的母亲本能地向后一让，\n请老婆婆先进了电梯，这时电梯已经满员了，母女俩只好静静地站在电梯门口，等着下一趟。办完事出来又乘电梯，这一回，电梯里只有母女俩和一位老爷爷。电梯\n开到底层，门开了，站在门口的女儿却把身体向后一让，说：“老爷爷，你先请。”老爷爷说：“孩子，你先请，爷爷走得慢。”女儿仍不肯自己先走，坚持让爷爷\n先走出电梯。&lt;/p&gt;&lt;p&gt;　　母亲幸福地抚着女儿的头，问：“你为什么要请爷爷先走出电梯呢？”女儿说：“是你刚才教我的呀！”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;七、这真遗憾&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　美国的斯特娜夫人是一位享誉盛名的早期教育家。有一天，孩子问斯特娜夫人：“我想到同学家里去玩，可以吗？”母亲说：“可以，但必须在12点前回来。”\n可那天孩子比预定的时间晚了20分钟才到家。斯特娜夫人见孩子回来了，什么也没说，只是指了指墙上的钟。孩子知道回来迟了，马上歉疚地说：“是我不对。”\n吃完饭，孩子赶紧换了衣服，因为每到星期二她都要和母亲去看电影。这时，斯特娜夫人又让孩子看看钟，并说：“今天来不及了，戏和电影是看不成了。”孩子难\n过地流下了眼泪。斯特娜夫人并未就此止步，而是说了一句十分惋惜而又耐人寻味的话：“这真遗憾！”&lt;/p&gt;&lt;p&gt;　　面对孩子的过错，尽管斯特娜夫人只说了寥寥几个字，并未采取其它任何处罚手段，但是使孩子明白了一个简单的道理：母亲的要求如果是正确的，那就必须绝对照办，否则你就得为此付出代价。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;八、四块糖果&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　陶行知先生在做校长时，曾经遇到了两个学生打架，当时其中的一个学生正用砖头砸向对方。陶行知先生及时制止了他们，并让拿砖头的学生到他的办公室去。没\n想到陶先生让学生坐下后，微笑着拿出一块水果糖说：“这是奖励你的，因为我让你来，你反而比我先到了。”学生听后一脸困惑。接着，先生又拿出第二块水果糖\n说：“这也是奖励你的，因为我让你停下来，你马上就停下了，表示对我的尊重，我应该感谢你。”学生听后更加不解其意。先生又拿出第三块糖说：“与你打架的\n学生是一个经常欺负女同学的学生，我对你的侠义精神表示敬意。”这时那位学生已经泣不成声了：“先生，不管怎么说，我用砖头打人是不对的……”先生拿出了\n第四块糖说：“你已经认识到自己的错误，咱们的谈话也应该到此结束了……”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;九、随意挑的&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　一所学校成绩总是搞不上去，请一个著名教育专家帮助学校发现人才。专家先是让校长将所有教师的教案拿来，他选出了3名教师，说他们是学校最有发展前途的\n教师。然后他从一年级的考卷中随意抽出50份试卷，说这50个学生是最优秀的，将来前途不可限量，如果由这3位教师来教，学校会创造一个奇迹。&lt;/p&gt;&lt;p&gt;　　果然，三年后，学校一炮打响，这50名学生全部考入了重点高中。在学生毕业后不久，学校收到了教授的来信：3名教师和50名学生都是我随意挑选的。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十、美丽的帽子&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　俊俏、可爱的小女孩苏珊读一年级的时候，小小的身体里竟然长了肿瘤。经过化疗，她的头发全部掉光了。一个亮亮的脑袋对于一个7岁的女孩来说是多么的残\n酷！她的心灵中弥漫着痛苦和悲伤。苏珊即将返校时，她的班主任，一个年轻的女教师郑重地对全班宣布：“从下星期开始，我们要认识各种帽子，所有的同学都要\n戴着自己的帽子来学校上课，越新奇越好！”孩子们并不知道教师的良苦用心。到了星期一，苏珊站在校门口却迟迟没进去，她担心，她犹豫，因为她戴了一顶帽\n子。可让她感到无比意外的是，她的每个同学都戴着帽子。与同学们那五花八门的帽子比起来，她的那顶帽子显得那样普通。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十一、无声胜有声&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　刚接一个班的第一节课，就碰上麻烦事。那天，当我拿着书兴冲冲走进教室时，看到的却是班上两个男生正在打架，此时，同学们的目光一齐射向我，而那两个男\n生也停止了打架。其中一个双手叉腰，昂着头，不屑地看着我，显然不把我放在眼里。其实在此之前，我已了解了他的“底细”---调皮捣蛋，老师都拿他没办\n法。看着他趾高气扬的样子，我知道此时无论说什么他都听不进去的，最好的办法是采取冷处理。于是，我只当什么都没有发生，径直走上讲台，说：“同学们，请\n大家坐好，我们开始上课……”他迷惑地看着我，有点不知所措地坐到座位上。&lt;/p&gt;&lt;p&gt;　　下课后，我对那件事仍然只字未提。直到下午，他见我仍没有批评的意思，便显得有些忐忑不安了，终于，放学后，他敲开了我办公室的门，主动承认了错误，从那以后，他很少再惹事。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十二、利息&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　在一次阶段测验中，一个男生的语文得了59分。他找到我说：“老师，您就再给我的作文加1分吧，就1分。求您了！”我说：“作文绝对不给加分；但是，我\n可以给你把总分改成60分——我借给你1分。不过你可要想好啊，这1分不能白借，是要还利息的，借1还10，下次考试我要扣掉你10分，怎么样？要是觉得\n不划算就不要借了。”男生咬了咬牙说：“我借。”结果，在下一次测验中，他语文得了91分，扣掉10分，净剩81分。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十三、玻璃门&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　学校大厅的门自打安上的那天起就几乎没有一天不挨踢，学校想了很多办法，大门还是经常被踢破，修了补，补了修。&lt;/p&gt;&lt;p&gt;　　新校长刚上任，后勤主任就向校长说明了这种情况，并建议换成大铁门。校长笑着说：“放心吧，我已经订做了最坚固的门。”&lt;/p&gt;&lt;p&gt;　　很快，旧门被拆了下来，新门被装上去。装上以后，居然没有挨过一次踢。孩子走到门口，总是不由自主地放慢脚步。&lt;/p&gt;&lt;p&gt;　　原来，这是一道玻璃门。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十四、暗号&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　课堂上老师提问的时候，一个同学总是举手，可老师叫他回答问题时却答不上来，引得下面的同学窃笑不已。&lt;/p&gt;&lt;p&gt;　\n　课后老师问他为什么要这样，他说如果老师提问时他不举手，同学会在课下叫他傻瓜。于是，老师就和他约定，当他真会的时候就高高地举起左手，不会的时候就\n举起右手。渐渐地，这名同学越来越多地举起他骄傲的左手，越来越多、越来越好地回答出老师的课堂提问，这个原本极可能在太多的嘲笑中沉沦的孩子也由一个差\n生转变成了一个好学生。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十五、出乎意料的批评&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　在师范上学的某个晚上，我们几个在\n寝室闹着玩，把盛满水的塑料袋放在门的上面等一位同学进门。就在这时，班主任王老师去寝室找人，见门虚掩着就推门而进，“哗”的一声，一袋子水顺身而下，\n衣服全湿了。房间里的学生都吓得目瞪口呆，静等老师的训斥。谁知王老师却笑着说：“今天是不是泼水节？我怎么不知道啊！再说咱们这儿也不过这个节呀。”大家都笑了，那个往门上放水的同学也不好意思地低下了头。这时，王老师走过去摸着他的头说：“以后同学之间开玩笑要讲究一个‘度’，适可而止，但不要这样。”&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十六、它会哭的&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　著名教育家孙敬修遇到小学生攀折树木，便把耳朵凑过去，装出听什么的样子。孩子们好奇地问爷爷在听什么？他说：“在听小树苗哭泣。”“小树苗会哭吗？”\n“是呀，你们折了它，它会疼的，当然要哭了，等他们长大了还要为祖国建设服务呢。”孩子们一听，一个个红着脸低下了头。以后，再也没攀折过小树。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十七、美丽的歧视&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　英语课上，张老师让大伟背单词，当时他正在下面读一本武侠小说，回答不上来。张老师很生气，说：“大伟，你真没出息，你不仅糟蹋爹娘的钱还耗费自己的青\n春，你如果能考是大学，全世界就没有文盲了。”面对老师的歧视，大伟“噌”地跳离座位，跨到讲台上指着老师说：“你不要瞧不起人，我此生必定要上大学。”\n说完，大伟把武侠小说撕得粉碎。结果，第一次高考差了100多分的大伟，第二年只差了17分，到了第三年竟超出分数线80多分。&lt;/p&gt;&lt;p&gt;　　升入大学的大伟多想见到张老师，当着面告诉他自己已经考上了大学，而且是重点大学。然而一年以后，他收到了张老师的一封信：大伟，对于有的同学，一般的&lt;a textvalue=&quot;鼓励&quot; target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;鼓励&lt;/a&gt;是没有用的，关键是要用锋利的刀子去做他心灵的手术，很多时候，别人的歧视能使我们激发出心底最坚强的力量。我说的对吗？&lt;/p&gt;&lt;p&gt;　　此时此刻，大伟才明白是老师美丽的歧视挽救了他。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十八、撒谎&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　一次周末，和儿子约好，下班后他骑车去接我，可是按约定时间过了两个小时，儿子还没有回来。&lt;/p&gt;&lt;p&gt;　　等待中，正好遇到儿子的同事，他说儿子和几个人看电影去了，我只好耐心地等。&lt;/p&gt;&lt;p&gt;　　过了一会儿，儿子回来了，令我吃惊的是，他竟撒谎说路上车坏了。&lt;/p&gt;&lt;p&gt;　\n　“我可以等上10个小时，但我无法容忍你对我撒谎，儿子。”我气愤地转过身来，决定自己走回家去。儿子迎上去，想要解释什么。我接着说：“我很生气，不\n是对你，是对我自己。你看，我已经认识到，作为一个父亲我很失败，我现在要走回家去，并在这18公里的路上进行反省。”&lt;/p&gt;&lt;p&gt;　　在3个多小时的路途中，儿子始终推着车跟在我身后，也是从那以后，儿子再没有撒过谎。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;十九、凳子拿的&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　“老师，我的钱丢了，30多块呢。”张滑边说边掉眼泪，我一听，气就不打一处来，班里竟发生这样的事。&lt;/p&gt;&lt;p&gt;　　我气冲冲地走进教室，就在走向讲台的一霎那，突然意识到：如果此时去批评全班，效果一定不会好，毕竟拿钱的是一个人。倒不如……&lt;/p&gt;&lt;p&gt;　\n　于是，我扫视了一下全班同学，高声告诉大家：“同学们，不要慌，我知道谁拿了张滑的钱了。”同学们都惊讶地望着我。“是凳子，请大家对凳子说一句话。”\n聪明的孩子马上领会了我的意图。一个同学说：“凳子，你这样做不对，快还给人家吧。”一位说：“你是一时糊涂，改了还是好孩子。”……孩子一个个激动不\n已，只有刘虎红着脸，低着头，一句话也不说，此时，我心里已经明白了。&lt;/p&gt;&lt;p&gt;　　第二天，刘虎早早地来到学校，把30元钱交给了我。我告诉他：你真勇敢，这件事是我们俩之间的秘密，谁都不要告诉别人。他哭了。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;二十、子教父&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　\n　一个人特别不孝顺老人，总嫌自己的父亲年老多病，是个负担。有一天，他把父亲放进了一个竹筐中，与自己的儿子一起把老人抬到山中无人之处，放下后就往回\n走。儿子说：“爹，把那个竹筐拿回来。”此人大惑不解，儿子接着说：“等你老了好用来抬你呀！”这位父亲幡然悔悟，又回头把老人抬了回来，以后对老人也孝\n顺起来。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;二十一、当范文&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　李老师在批改一个女孩的作文时，发现是抄袭的。当时，他\n很想在的作文本上写上大大的“抄袭”二字。可静下来想想，这样做，她会改正自己的错误吗？考虑再三，李老师把她叫到办公室，微笑着对她说：“你的作文有进\n步，相信你一定花了不少时间，付出了很大的努力，老师想在全班同学面前表扬你，请你把作文好好改一改，然后在班上朗读，好吗？”小姑娘没说话，低着头走\n了。第二天，她拿着改好的作文来了，李老师发现她重写了作文，虽没抄袭的精彩，却是她自己写的，老师还是表扬了她。在班上朗读时，她念得十分自信，并赢得了掌声。从那以后，女孩对作文产生了浓厚的兴趣。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;二十二、编程高手&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　北大附中老师张思明曾遇到这样的一件事：班上有一个学生非常喜欢摆弄计算机，可以说达到了痴迷的境界和较高的编程水平，但由此耽误了不少功课。特别是他不爱学习化学公式，认为那是靠记忆的东西，有了计算机，学着些东西就没有什么意义了。结果他的化学考试很不理想。&lt;/p&gt;&lt;p&gt;　　张老师便去找他谈心，给他出了一道“计算机”难题：计算机能不能对输入的某个公式自动配平呢？这位同学一听立刻来了精神，回家以后就开始了研究。可他为了编写程序，就必须先把化学公式的配平原理搞清楚，不久，他在理解化学公式配平方法的基础上编好了程序。&lt;/p&gt;&lt;p&gt;　\n　张老师抓住他想证实自己编程实力的迫切心情，又接连提出了第二个和第三个问题：是不是书上任何一个化学公式用你的编程都能配平呢？你的编程除了适用简单\n的公式外，是不是还适用于比较复杂的公式？这两个问题不但促进该生对化学公式的复习和记忆，而且使对化学公式的学习更加深入。&lt;/p&gt;&lt;p&gt;　　面对学生的进步，张老师充分肯定了他的编程实力，鼓励他学好各门功课，为自己考入大学计算机系创造前提条件。从此，该生的学习更加自觉和主动。因为通过张老师的引导，他知道了两件事：一是自己能学好，二是自己必须学好。&lt;/p&gt;&lt;p&gt;　　&lt;strong&gt;二十三、欺骗&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　有一次，学校开家长会，姥姥代替了在外工作的父母去了学校。小顺一下午都惶惶不安，想：老师一定会对姥姥讲自己的成绩如何如何的一塌糊涂。&lt;/p&gt;&lt;p&gt;　　终于等到姥姥回来，小顺低着头只等姥姥数落。可是姥姥容光满面，看起来十分高兴的样子，他一脸的诧异。想不到老师竟对姥姥说他表现得很出色。&lt;/p&gt;&lt;p&gt;　　后来，小顺上了大学，暑假里他第一个去拜访了小学老师。谈起这件事时，只听这位老师忏悔地对他说：“那天，我说了你很多不好的话，可第二天，你的反应让我吃惊。从那以后，你的成绩直线上升，我一直都在纳闷儿……这时，小顺才明白是姥姥善意的欺骗挽救了他。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('81', '程序员励志小故事', '网络', '1', '157', '/Upload/article/56dd45e4223af.jpg', '2016-03-07 17:12:04', '在我们生活中，难免会碰到很多这样那样的事，有好事，也有坏事。我们的处事为人应该懂得随着自己的阅历越来越来得心应手。懂得怎么样去真诚对待朋 友，怎么去应付对手。但是做这些之前，首先我们就应该懂得认识自己。人首先要自明才能自傲。有了自明才能够认清朋友和对手。希望下面的一篇文摘能够对你有 所帮助。', '&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	一、“枯井”脱困&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　初涉职场，很多人一头撞进了陌生的环境，随之而来的困惑、焦虑还有彷徨和无奈，种种感觉一起涌上心头，各种滋味尽在其中。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　有一天，一个农夫的一头驴子，不小心掉进了一口枯井里，农夫绞尽脑汁想办法想救出驴子，但几个小时过去了，驴子还在井里痛苦的哀嚎着。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　最后，这位农夫决定放弃，他想这头驴子年纪大了，不值得大费周章的把它救上来，不过无论如何，这口井还是得填起来。于是农夫便请左邻右舍帮忙一起将井中的驴子埋了，以免除痛苦。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　农夫的邻居们人手一把铲子，开始将泥土铲进井中，当这头驴子了解到自己的处境时，刚开始哭得很凄惨。但出人意料的是，一会儿之后这头驴子就开始安静下\r\n来。农夫好奇的探头往井里一看，出现在眼前的景象令他目瞪口呆：当铲进井里的泥土落到驴子的背部的时候，驴子的反应令人称奇---它将泥土抖落到一旁，然\r\n后站到铲进的泥土堆上面，就这样，驴子将大家铲到它身上的泥土全数抖落在井底，然后再跳上去。很快的，随着脚下的泥土不断升高，这头驴子得意的上升到井\r\n口，最后在众人惊讶的表情中，快步的跑开了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　从这个预言故事中，可以看到：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　就像驴子的情况一样，在人的一生中，有时候难免会陷入“枯井”里，会有各式各样的“泥沙”倾倒到我们的身上，而从“枯井”脱困的秘诀就是将“泥沙”抖落掉，然后站在上面去！&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　事实上，我们在生活中所遭遇的种种困难挫折就是加诸在我们身上的“泥沙”；然而，换个角度，他们也是一块块垫脚石，只要我们锲而不舍的将它们抖落掉，然后站上去，那么即使掉落到再深得井，我们也能安然脱困。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　程序如人生，在学习&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;8_nwp&quot;&gt;程序开发&lt;/span&gt;的过程中总会遇到各种各样的技术难点和困惑，如果我们对此畏惧或逃避，难免会被“活埋”，而如果我们把这些问题一个个的抖落掉作为垫脚石，那么我们就会一步步的提升，从而最终达到我们的目标。&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	二、性格决定命运，气度影响格局&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　有三个水泥匠在砌墙，有人问他们在做什么？&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　第1个水泥匠&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;7_nwp&quot;&gt;闷闷不乐&lt;/span&gt;的回答说：“我在砌墙”。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　第2个水泥匠高兴的回答说：&amp;quot;我在盖一座摩天大楼&amp;quot;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　第3个水泥匠则兴奋而又豪迈万丈的说：&amp;quot;我在和我的团队伙伴们一起美化这座城市。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　若干年后，第1个&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;4_nwp&quot;&gt;水泥&lt;/span&gt;匠仍然是水泥，第2个水泥匠则成为一家&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;5_nwp&quot;&gt;建筑公司&lt;/span&gt;的总&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;6_nwp&quot;&gt;设计师&lt;/span&gt;；而第3个水泥匠成为了建筑公司的老总，带领着更多的人美化城市。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　这个小故事，告诉我们：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　3个本来同等层次的人，以为对自己价值的认可度对自己所作事情的认可度不同，有着截然不同的结果。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　这条结果同样适用于我们这些写代码的人，有人认为自己每天只是写程序，有人则认为是在完成一个完美的系统，而有的人想的是帮助客户解决问题、更好的提高业务能力和效率。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　俗话说：性格决定命运，气度影响格局。在显示生活中，我们改变不了过去，但可以改变现在；我们决定不了命运，但可以改变心态。&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	三、请闭上&amp;quot;鸟嘴&amp;quot;&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　一直小鸟正在飞往南方过冬的途中，天气太冷了，小鸟冻僵了，从天上掉了下来，跌在一大片的农田里，它躺在田里的时候，一只母牛走了过来，而且拉了一泡屎在它身上，冻僵的小鸟躺在牛屎堆里，发觉牛粪真是太温暖了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　牛粪让它慢慢的缓过劲儿来了，它躺在那里，又暖和又开心，不久久开始高兴的唱起歌来了，一只路过的猫听到了小鸟的歌声，走过来查看个究竟，顺着声音，猫发现了躲在牛粪中的小鸟，非常敏捷的将它刨了出来，并把它给吃了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　这个故事告诉我们，身处职场：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　1、不是每个在你身上拉屎的人都是你的敌人。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　2、不是每个把你从屎堆中拉出来的都是你的朋友。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　3、当你陷入深深的&amp;quot;屎堆&amp;quot;当中（深陷困境）的时候，请闭上你的&amp;quot;鸟嘴&amp;quot;!&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	四、技多不压身&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　在一个漆黑的晚上，老鼠首领带领着小老鼠外出觅食，在一家人的厨房内，垃圾桶中有很多剩余的饭菜，对于老鼠来说，就好像人类发现了宝藏。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　正当一大群老鼠在&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;3_nwp&quot;&gt;垃圾桶&lt;/span&gt;及\r\n附近范围内大餐一顿之际，突然传来一阵令它们肝胆俱裂的声音，那就是一只大花猫的叫声。它们震惊之余，便各自四处逃命，但大花猫毫不留情，穷追不舍，终于\r\n有2只小老鼠逃避不及，被大花猫捉到，正要讲它们吞噬之际，突然传来一连串凶恶的狗吠声，令大花猫措手不及，狼狈而逃，大花猫走后，老鼠首领施施然从垃圾\r\n桶后面走了出来说：”看，这年头，掌握一门外语是多么重要啊！“。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　这个故事告诉我们：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“多一门技艺，多一条出路。多一项才能，多一种选择。”不断学习是成功人士的终身箴言。&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;2_nwp&quot;&gt;程序员&lt;/span&gt;亦是如此，多掌握几门技术能在自己的职业发展过程中发挥意想不到的作用。&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	五、一切从实际出发&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　一天，一个名牌大学的高材生到一家犹太人开的公司去应聘。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“你有什么要求？”老板问。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“月薪10万元，一年有一个月公费出国，公司给我&lt;span style=&quot;width: auto; height: auto; float: none; font-size: 16px;&quot;&gt;租房子&lt;/span&gt;。”高材生说。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“我一个月给你薪水20万元，一年有2个月让你公费&lt;span style=&quot;font-size: 14px; width: auto; height: auto; float: none;&quot; id=&quot;0_nwp&quot;&gt;出国&lt;/span&gt;，还有，公司送你一栋房子。”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“这该不会是跟我开玩笑吧？”高材生惊讶不已。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　“你难道不是在跟我开玩笑嘛？”老板说。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　这个故事告诉我们：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　找工作应该从实际出发，根据自身能力要求更实际点的报酬。很多的时候，不要问公司能为你提供多少薪水，而是要想想你到底能为公司创造多少价值。&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	六、靠天靠地，不如靠自己&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　小蜗牛问妈妈：“为什么我们从生下来，就要背负这个又重又硬的壳呢？”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　妈妈：“因为我们的身体没有骨骼的支撑，只能爬，又爬不快。所以需要这个壳的保护！”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　小蜗牛：“毛虫妹妹没有骨头，也爬不快，为什么她缺不用背这个又硬又重的壳呢？”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　妈妈：“因为毛虫妹妹能变成蝴蝶，天空会保护她得啊。”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　小蜗牛：“可是蚯蚓弟弟也没骨头爬不快，也不会变成蝴蝶，他为什么不背这个又硬又重的壳呢？”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　妈妈：“因为蚯蚓弟弟会钻土，大地会保护他啊。”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　小蜗牛哭了起来：“我们好可怜啊，天空不保护，大地也不保护。”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　蜗牛妈妈安慰他说：“所以我们有壳啊！我们不靠天，也不靠地，我们靠自己！”&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　刚刚参加工作的人往往会有这样的心情：企业里面有许多人际矛盾，不少人在企业里面有“靠山”，而自己没有，只能靠自己打拼，心里有些失衡。有靠山的人，活的就是轻松自在，干的少，得到的多。我们卖力干活，却得不到回报。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;\r\n	　　然而，上面的故事告诉我们：我们不用期望依靠任何人，只能靠自己。别人只是外因，自己才是内涵。别人只能是锦上添花，而只有自己才能雪中送炭，只有靠自己的努力铸就的事业才会更加牢固。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('82', '编程的真实样子', '网络', '1', '45', '/Upload/article/56de664ab0466.png', '2016-03-08 13:42:34', '当 我跟人说我是一个Web程序员，他们会认为我是个天才。当我告诉他们我工作的公司是编写火狐浏览器的，他们认为我跟上帝一样伟大。我可以想象，其他的程序 员也会跟我一样受到这样的待遇。这些话我并不是要跟自己说，或说给我们圈内人听，我说这些是因为很多圈外人对我们的编程没有任何概念。', '&lt;p style=&quot;border: 0px; font-family: &amp;#39;Microsoft YaHei&amp;#39;, 微软雅黑, &amp;#39;Hiragino Sans GB&amp;#39;, STHeiti, &amp;#39;WenQuanYi Micro Hei&amp;#39;, SimSun, sans-serif, Lucida, Verdana, serif; font-size: 16px; margin: 0px 0px 24px; outline: 0px; padding: 0px; vertical-align: baseline; text-indent: 2em; color: rgb(43, 43, 43); line-height: 24px;&quot;&gt;当\n我跟人说我是一个Web程序员，他们会认为我是个天才。当我告诉他们我工作的公司是编写火狐浏览器的，他们认为我跟上帝一样伟大。我可以想象，其他的程序\n员也会跟我一样受到这样的待遇。这些话我并不是要跟自己说，或说给我们圈内人听，我说这些是因为很多圈外人对我们的编程没有任何概念。&lt;/p&gt;&lt;p style=&quot;border: 0px; font-family: &amp;#39;Microsoft YaHei&amp;#39;, 微软雅黑, &amp;#39;Hiragino Sans GB&amp;#39;, STHeiti, &amp;#39;WenQuanYi Micro Hei&amp;#39;, SimSun, sans-serif, Lucida, Verdana, serif; font-size: 16px; margin: 0px 0px 24px; outline: 0px; padding: 0px; vertical-align: baseline; text-indent: 2em; color: rgb(43, 43, 43); line-height: 24px;&quot;&gt;甚至在业内，一些程序员对另外一些程序员也是高山仰止。在我的眼中，Mozilla公司里很多其他程序员简直就是传奇，而事实上，很多时候，他们真是。就是这个原因，我们程序员中很多人只才会患上&lt;span style=&quot;font-family:inherit;color:#24890d&quot;&gt;&lt;span style=&quot;font-style: inherit; font-weight: inherit;&quot;&gt;骗子综合征&lt;/span&gt;&lt;/span&gt;，就是这个原因，我们才害怕加入OSS项目，害怕在开发大会上和明星程序员们站在一起。&lt;/p&gt;&lt;p style=&quot;border: 0px; font-family: &amp;#39;Microsoft YaHei&amp;#39;, 微软雅黑, &amp;#39;Hiragino Sans GB&amp;#39;, STHeiti, &amp;#39;WenQuanYi Micro Hei&amp;#39;, SimSun, sans-serif, Lucida, Verdana, serif; font-size: 16px; margin: 0px 0px 24px; outline: 0px; padding: 0px; vertical-align: baseline; text-indent: 2em; color: rgb(43, 43, 43); line-height: 24px;&quot;&gt;下面这个漫画展示了人们眼中的编程生活和实际编程生活的区别：&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457415591934959.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;border: 0px; font-family: &amp;#39;Microsoft YaHei&amp;#39;, 微软雅黑, &amp;#39;Hiragino Sans GB&amp;#39;, STHeiti, &amp;#39;WenQuanYi Micro Hei&amp;#39;, SimSun, sans-serif, Lucida, Verdana, serif; font-size: 16px; margin: 0px 0px 24px; outline: 0px; padding: 0px; vertical-align: baseline; text-indent: 2em; color: rgb(43, 43, 43); line-height: 24px;&quot;&gt;我们经常会在编程时犯愚蠢的错误。我们整天盯着屏幕看，寻找一行让我们的应用无法启动的代码。我们诅咒浏览器为什么不能正常显示页面，却发现是一行明显的错误造成的。每个程序员都有这种时刻，冥思苦索是每天的例行活动。&lt;/p&gt;&lt;p style=&quot;border: 0px; font-family: &amp;#39;Microsoft YaHei&amp;#39;, 微软雅黑, &amp;#39;Hiragino Sans GB&amp;#39;, STHeiti, &amp;#39;WenQuanYi Micro Hei&amp;#39;, SimSun, sans-serif, Lucida, Verdana, serif; font-size: 16px; margin: 0px 0px 24px; outline: 0px; padding: 0px; vertical-align: baseline; text-indent: 2em; color: rgb(43, 43, 43); line-height: 24px;&quot;&gt;这是程序员的真实生活。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('83', '一份关于解程序员的调查 ', 'PHPtalker', '1', '50', '/Upload/article/56de66c6f0669.png', '2016-03-08 13:44:38', '程序员周末都喜欢做什么？在公司加班？在家里加班？看电影？睡觉？程序员都怎么找女朋友？百分之八十的程序员表示，女朋友是啥，有好基友就够了。程序员都去哪些网站呢？嘿嘿嘿。。。', '&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, &amp;#39;冬青黑体简体中文 w3&amp;#39;, 宋体; line-height: 24px; background-color: rgb(255, 255, 255);&quot;&gt;程序员周末都喜欢做什么？在公司加班？在家里加班？看电影？睡觉？程序员都怎么找女朋友？百分之八十的程序员表示，女朋友是啥，有好基友就够了。程序员都去哪些网站呢？嘿嘿嘿。。。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457415840523598.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://www.onlyni.com&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457415841538586.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('84', '如果编程语言是女人 ', 'phptalker', '1', '52', '/Upload/article/56de675899460.png', '2016-03-08 13:47:04', 'PHP\r\n是你的豆蔻年华的心上人，她是情窦初开的你今年夏天傻乎乎的追求的目标。玩一玩可以，但千万不要投入过深，因为这个女孩有严重的问题。', '&lt;p&gt;&lt;a target=&quot;_self&quot; href=&quot;http://www.onlyni.ccom&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457415953206712.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;PHP&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;是你的豆蔻年华的心上人，她是情窦初开的你今年夏天傻乎乎的追求的目标。玩一玩可以，但千万不要投入过深，因为这个女孩有严重的问题。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Perl&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt; &lt;/strong&gt;是PHP的姐姐。她对你来说年龄稍微大了一点，但在90年代，她是相当受欢迎的。她和Larry \r\nWall(译注：Perl语言创始人)长期保持着亲密关系，因此她的审美一落千丈，如今她看起来是丑陋不堪。“无论你们怎么评论，我仍然爱\r\n她！”，Larry Wall说。没有第二个人会像他这样。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Ruby&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;是脚本家族中一个非常漂亮的孩子。第一眼看她，你的心魄就会被她的美丽摄走。她还很有有趣。起初她看起来有点慢，不怎么稳定，但近些年来她已经成熟了很多。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Python &lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;是Ruby的一个更懂事的姐姐。她优雅，新潮，成熟。她也许&lt;em&gt;太过&lt;/em&gt;优秀。很多小伙都会说“嘿，兄弟，你怎么可能不爱上Python呢！？”。没错，你喜欢Python。你把她当成了一个脾气和浪漫都退烧了的Ruby。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Java&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;是一个事业成功的女人。很多在她手下干过的人都感觉她的能力跟她的地位并不般配，她更多的是通过技巧打动了中层管理人员。你也许会认为她是很有智慧的人，你愿意跟随她。但你要准备好在数年里不断的听到“你用错了接口，你遗漏了一个分号”这样的责备。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;C++ &lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;是Java的表姐。她在很多地方跟Java类似，不同的是她成长于一个天真的年代，不认为需要使用“保护措施”。当然，“保护措施”是指自动内存管理。你以为我指的是什么？ &lt;img style=&quot;display: inline;&quot; src=&quot;/Upload/ueditor/img/1457415953409217.gif&quot; data-original=&quot;http://www.vaikan.com/wordpress/wp-includes/images/smilies/icon_smile.gif&quot; alt=&quot;:)&quot; class=&quot;wp-smiley lazy &quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt; &lt;/p&gt;&lt;p&gt;&lt;strong&gt;C &lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;是C++的妈妈。对一些头发花白的老程序员说起这个名称，会让他们眼睛一亮，产生无限回忆。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Objective C&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt; &lt;/strong&gt;C语言家族的另外一个成员。她加入了一个奇怪的教会，不愿意和任何教会之外的人约会。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Haskell, Clojure, Scheme &lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;以及她们的朋友们都是一些时髦的，附庸风雅的，很聪明的女孩，你也许在多年前和她们曾度过了一个很Happy的暑假。她们是第一次让你感到有压力的女孩。当然，你可能从来没有对她们很认真——尽管你总是在问自己“如果….会如何？”&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;也许你会拖延着不去认识&lt;strong&gt;C# &lt;/strong&gt;，因为她们家族不好的名声。但最近几年他们已经改邪归正了——他们会这样告诉你。\r\n一旦你加入我们，你就是我们的人了，你听到了没有？你需要一个数据库？她的哥哥MSSQL会罩着你。需要一个安身的地方？这有何难，她老爸甚至可以在\r\nAzure大道你给买一套公寓。什么？你觉得这样的关系过于亲密了？不，你别想离开我们。你已经是我们家族的一员了，&lt;em&gt;现在&lt;/em&gt;，听见了没有？&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Javascript &lt;/strong&gt;—— 嗨，这不是你的初吻的那个女孩吗，她甚至比PHP更早进入你的视线。我不知道她现在在干什么。我听说她的事业近几年来非常成功。旧时的人也可以赶上潮流变得很酷….（你发现她从头到脚穿的都是设计师jQuery的作品）….哇，有人从丑小鸭变成了美丽的天鹅…&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('85', 'PHP的学习规划', '网络', '20', '40', '/Upload/article/56de6a160a710.png', '2016-03-08 13:58:46', '想学习PHP吗？千万不要自己盲修瞎练，否则会走很多弯路，本文就是为了帮助大家少走弯路，减少找资料和学习方法的时间。\r\n一个人独立学习PHP可能有点难，但是这也未尝不可。学习PHP，大概只需要1-2个月的时间，这还是算上了你有几天没有学习。如果你是一个上班族，你只是在空余时间学习，可能时间还要长一点。', '&lt;p&gt;想学习PHP吗？千万不要自己盲修瞎练，否则会走很多弯路，本文就是为了帮助大家少走弯路，减少找资料和学习方法的时间。&lt;br/&gt;一个人独立学习PHP可能有点难，但是这也未尝不可。学习PHP，大概只需要1-2个月的时间，这还是算上了你有几天没有学习。如果你是一个上班族，你只是在空余时间学习，可能时间还要长一点。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.phptalker.com/data/attachment/portal/201411/15/145333k7p73ox59px8ud3q.png&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;/Upload/ueditor/img/1457416693225565.png&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;我用了几乎一年的时间才到达我现在的水平，中间走了很多弯路，我看了很多后来发现根本没用的视频和教材，因为我从没有看到过一个好的学习向导。这篇文章就是为了帮助大家少走弯路，减少找资料和学习方法的时间。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step1：2-4天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;学习HTML/CSS的入门知识，了解这些代码，对于初学者来说最简单的第一步，并且让你学习的内容立马看到成效。相反，一上来就学习语法，编写这样的代码实在无聊，因为你都可以用计算器得出结果。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step2：1-2天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;利用视频教程，PDF文档，学习PHP头几门课程，知道构建网站的一些基本工具，下面你就要学习更多PHP提供的功能了。另外，在这一阶段你要找到一款你喜欢的代码编辑器。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step3：2-4天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;这一步对于有一定编程基础的人来说很容易，但对于那些零基础的人就比前阶段的入门步骤难一些了。回到你之前学习的PHP课程，完成整个课程的内容，这一步虽然有些难，但不要灰心和放弃，因为在下一步我们会有大量的更酷的内容。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step4：6-9天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;学习完这些课程并完成相应的练习，你就基本已经是一个Web开发者了，如果你能做到这些你就应该很自豪了。你要培养这方面的兴趣，基本上所有人都足够聪明，可以学会Web开发，关键是兴趣和激情。在这个时候，你希望可以有一个更高级的代码编辑器，比如Netbeans。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step5：1天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;现在可以学习一些AJAX的知识了，现在不知道这是什么都没关系。&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step6：1天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;这时候应该学习面向对象的知识，让你的代码更整洁。它会增强代码的复用，把代码模块化。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step7：2-3天&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;用PHP做一个CMS系统，一旦你完成了它，再返回头，用面向对象的方式实现它。如果这个对你有困难，没有关系，随着你的学习一直做它，不要过分纠结于一个难点，然后就放弃了。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;Step8：7-9天&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;学习做一个登录系统，可以参考YouTube上的PHPAcademy课程。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;Step9：2-3天&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;利\r\n用JQuery把你的网站做的更有趣，互动性更强一些，JQuery是一个简单但非常重要的库，可以让你的网站更棒。所以，学习一些JQuery的指导课\r\n程，它是JavaScript的一个库，我这里没有让大家学习整个JavaScript，因为JavaScript和PHP很相似，在一开始你不会用到完\r\n整的JavaScript。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;Step10：3-5天&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;学习MVC框架的工作原理，我推荐CodeIgniter，它对于新手是很好的框架。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&lt;strong&gt;Step11：视情况而定&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;构建你的网站，这个需要看你想做的网站的复杂程度。记得你要使用面向对象的方法，使用MVC框架。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;Step12：2-3天&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;完成了网站之后，你要试着将它发布到网上。对于这点，你可以看看如何创建一个云服务器，我没有做过这个，但PHPAcademy上有这方面很好的指导，一定会帮到你。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;Step13：永远&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;学习永无止境，经常复习之前学习的内容，你依然可以从中学到新技巧，回忆起你忘记的内容。&lt;br/&gt;&lt;br/&gt;以上这些就是一个学习PHP的路径，希望可以帮到你&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('86', '为更好使用MySQL，你要了解的16个要点！', '传智播客', '21', '64', '/Upload/article/56de6ee0cc120.jpg', '2016-03-08 14:19:12', 'MySQL 可以很好的支持大数据量的存取，但是一般说来，数据库中的表越小，在它上面执行的查询也就会越快。因此，在创建表的时候，为了获得更好的性能，我们可以将 表中字段的宽度设得尽可能小。例如，在定义邮政编码这个字段时，如果将其设置为CHAR(255),显然给数据库增加了不必要的空间，', '&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;1、选取最适用的字段属性 &lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;MySQL\n可以很好的支持大数据量的存取，但是一般说来，数据库中的表越小，在它上面执行的查询也就会越快。因此，在创建表的时候，为了获得更好的性能，我们可以将\n表中字段的宽度设得尽可能小。例如，在定义邮政编码这个字段时，如果将其设置为CHAR(255),显然给数据库增加了不必要的空间，甚至使用\nVARCHAR这种类型也是多余的，因为CHAR(6)就可以很好的完成任务了。同样的，如果可以的话，我们应该使用MEDIUMINT而不是 \nBIGIN来定义整型字段。 &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;另外一个提高效率的方法是在可能的情况下，应该尽量把字段设置为NOT NULL，这样在将来执行查询的时候，数据库不用去比较NULL值。 &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;对于某些文本字段，例如“省份”或者“性别”，我们可以将它们定义为ENUM类型。因为在MySQL中，ENUM类型被当作数值型数据来处理，而数值型数据被处理起来的速度要比文本类型快得多。这样，我们又可以提高数据库的性能。 &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;2、使用连接（JOIN）来代替子查询(Sub-Queries) &lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;MySQL\n从4.1开始支持SQL的子查询。这个技术可以使用SELECT语句来创建一个单列的查询结果，然后把这个结果作为过滤条件用在另一个查询中。例如，我们\n要将客户基本信息表中没有任何订单的客户删除掉，就可以利用子查询先从销售信息表中将所有发出订单的客户ID取出来，然后将结果传递给主查询，如下所示： &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;DELETE FROM customerinfo WHERE CustomerID NOT in (SELECT CustomerID FROM salesinfo ) &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;使用子查询可以一次性的完成很多逻辑上需要多个步骤才能完成的SQL操作，同时也可\n以避免事务或者表锁死，并且写起来也很容易。但是，有些情况下，子查询可以被更有效率的连接（JOIN）.. \n替代。例如，假设我们要将所有没有订单记录的用户取出来，可以用下面这个查询完成： &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;SELECT * FROM customerinfo WHERE CustomerID NOT in (SELECT CustomerID FROM salesinfo ) &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果使用连接（JOIN）.. 来完成这个查询工作，速度将会快很多。尤其是当salesinfo表中对CustomerID建有索引的话，性能将会更好，查询如下： &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;SELECT * FROM customerinfo LEFT JOIN \nsalesinfoON customerinfo.CustomerID=salesinfo. CustomerID WHERE \nsalesinfo.CustomerID IS NULL &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;连接（JOIN）.. 之所以更有效率一些，是因为 MySQL不需要在内存中创建临时表来完成这个逻辑上的需要两个步骤的查询工作。 &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;3、使用联合(UNION)来代替手动创建的临时表 &lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;MySQL\n 从 4.0 的版本开始支持 UNION 查询，它可以把需要使用临时表的两条或更多的 SELECT \n查询合并的一个查询中。在客户端的查询会话结束的时候，临时表会被自动删除，从而保证数据库整齐、高效。使用 UNION \n来创建查询的时候，我们只需要用 UNION作为关键字把多个 SELECT 语句连接起来就可以了，要注意的是所有 SELECT \n语句中的字段数目要想同。下面的例子就演示了一个使用 UNION的查询。 &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;SELECT Name, Phone FROM client UNION SELECT Name, BirthDate FROM author &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;UNION &lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;SELECT Name, Supplier FROM product &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;&lt;strong&gt;4、当只要一行数据时使用LIMIT 1&lt;/strong&gt;当你查询表的有些时候，你已经知道结果只会有一条结果，单因为你可能需要去fetch游标，或是你也许会去检查返回的记录数。在这种情况下，加上LIMIT 1 可以增加性能。这样一样， MySQL数据库引擎会在找到一条数据后停止搜索，而不是继续往后查找下一条符合记录的数据。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;&lt;strong&gt;5、为搜索字段建索引&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;索引并不一定就是给主键或是唯一的字段。如果在你的表中，有某个字段你总要会经常用来做搜索，那么，请为其建立索引吧。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;&lt;strong&gt;6、在Join表的时候使用相当类型的列，并将其索引&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果你的应用程序有很多JOIN查询，你应该确认两个表中Join的字段是被建过索引的。这样，MySQL内部会启动为你优化Join的SQL语句的机制。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;而且，这些被用来Join的字段，应该是相同的类型的。例如：如果你要把DECIMAL字段和一个INT字段JOIN在一起，MYSQL就无法使用他们的索引。对于那些STRING类型，还需要有相同的字符集才行（两个表的字符集有可能不一样）&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;7、避免SELECT *&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;从数据库里读出越多的数据，那么查询就会变得越慢。并且，如果你的数据库服务器和WEB服务器是两台独立的服务器的话，这还会增加网络传输的负载。所以，你应该养成一个需要什么就取什么的好的习惯。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;8、永远为每张表设置一个ID作为主键&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;我们应该为数据库里的每张表都设置一个ID作为其主键，而最好的是一个INT型（推荐使用UNSIGNED），并设置上自动增长的AUTO INCREMENT标志。就\n算是你 users 表有一个主键叫 “email”的字段，你也别让它成为主键。使用 VARCHAR \n类型来当主键会使用得性能下降。另外，在你的程序中，你应该使用表的ID来构造你的数据结构。 \n而且，在MySQL数据引擎下，还有一些操作需要使用主键，在这些情况下，主键的性能和设置变得非常重要，比如，集群，分区……&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;9、使用 ENUM 而不是 VARCHAR ？&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;ENUM 类型是非常快和紧凑的。在实际上，其保存的是 TINYINT，但其外表上显示为字符串。这样一来，用这个字段来做一些选项列表变得相当的完美。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果你有一个字段，比如“性别”，“国家”，“民族”，“状态”或“部门”，你知道这些字段的取值是有限而且固定的，那么，你应该使用 ENUM 而不是 VARCHAR。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;10、尽可能的使用 NOT NULL&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;除非你有一个很特别的原因去使用 NULL 值，你应该总是让你的字段保持 NOT NULL。这看起来好像有点争议，请往下看。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;首先，问问你自己“Empty”和“NULL”有多大的区别（如果是INT，那就是0和NULL）？如果你觉得它们之间没有什么区别，那么你就不要使用NULL。（你知道吗？在 Oracle 里，NULL 和 Empty 的字符串是一样的！)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;不要以为 NULL 不需要空间，其需要额外的空间，并且，在你进行比较的时候，你的程序会更复杂。 当然，这里并不是说你就不能使用NULL了，现实情况是很复杂的，依然会有些情况下，你需要使用NULL值。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;11、把IP地址存成 UNSIGNED INT&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;很\n多程序员都会创建一个 VARCHAR(15) \n字段来存放字符串形式的IP而不是整形的IP。如果你用整形来存放，只需要4个字节，并且你可以有定长的字段。而且，这会为你带来查询上的优势，尤其是当\n你需要使用这样的WHERE条件：IP between ip1 and ip2。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;我们必需要使用UNSIGNED INT，因为 IP地址会使用整个32位的无符号整形&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;12、垂直分割&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;“垂直分割”是一种把数据库中的表按列变成几张表的方法，这样可以降低表的复杂度和字段的数目，从而达到优化的目的。（以前，在银行做过项目，见过一张表有100多个字段，很恐怖）&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;示例一：在Users表中有一个字段是家庭地址，这个字段是可选字段，相比起，而且\n你在数据库操作的时候除了个人信息外，你并不需要经常读取或是改写这个字段。那么，为什么不把他放到另外一张表中呢？ \n这样会让你的表有更好的性能，大家想想是不是，大量的时候，我对于用户表来说，只有用户ID，用户名，口令，用户角色等会被经常使用。小一点的表总是会有\n好的性能。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;示例二： 你有一个叫 “last_login” \n的字段，它会在每次用户登录时被更新。但是，每次更新时会导致该表的查询缓存被清空。所以，你可以把这个字段放到另一个表中，这样就不会影响你对用户 \nID，用户名，用户角色的不停地读取了，因为查询缓存会帮你增加很多性能。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;另外，你需要注意的是，这些被分出去的字段所形成的表，你不会经常性地去Join他们，不然的话，这样的性能会比不分割时还要差，而且，会是极数级的下降。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;13、拆分大的 DELETE 或 INSERT 语句&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果你需要在一个在线的网站上去执行一个大的 DELETE 或 INSERT 查询，你需要非常小心，要避免你的操作让你的整个网站停止相应。因为这两个操作是会锁表的，表一锁住了，别的操作都进不来了。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;Apache 会有很多的子进程或线程。所以，其工作起来相当有效率，而我们的服务器也不希望有太多的子进程，线程和数据库链接，这是极大的占服务器资源的事情，尤其是内存。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果你把你的表锁上一段时间，比如30秒钟，那么对于一个有很高访问量的站点来说，这30秒所积累的访问进程/线程，数据库链接，打开的文件数，可能不仅仅会让你泊WEB服务Crash，还可能会让你的整台服务器马上挂了。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;所以，如果你有一个大的处理，你定你一定把其拆分，使用 LIMIT 条件是一个好的方法。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;14、越小的列会越快&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;对于大多数的数据库引擎来说，硬盘操作可能是最重大的瓶颈。所以，把你的数据变得紧凑会对这种情况非常有帮助，因为这减少了对硬盘的访问。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;如果一个表只会有几列罢了（比如说字典表，配置表），那么，我们就没有理由使用 INT 来做主键，使用 MEDIUMINT, SMALLINT 或是更小的 TINYINT 会更经济一些。如果你不需要记录时间，使用 DATE 要比 DATETIME 好得多。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;当然，你也需要留够足够的扩展空间，不然，你日后来干这个事，你会死的很难看，&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;&lt;strong&gt;15、选择一个正确的存储引擎&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;在 MySQL 中有两个存储引擎 MyISAM 和 InnoDB，每个引擎都有利有弊！&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;MyISAM \n适合于一些需要大量查询的应用，但其对于有大量写操作并不是很好。甚至你只是需要update一个字段，整个表都会被锁起来，而别的进程，就算是读进程都\n无法操作直到读操作完成。另外，MyISAM 对于 SELECT COUNT(*) 这类的计算是超快无比的。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;InnoDB 的趋势会是一个非常复杂的存储引擎，对于一些小的应用，它会比 MyISAM 还慢。他是它支持“行锁” ，于是在写操作比较多的时候，会更优秀。并且，他还支持更多的高级应用，比如：事务。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;&lt;strong&gt;16、小心“永久链接”&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;“永\n久链接”的目的是用来减少重新创建MySQL链接的次数。当一个链接被创建了，它会永远处在连接的状态，就算是数据库操作已经结束了。而且，自从我们的 \nApache开始重用它的子进程后——也就是说，下一次的HTTP请求会重用Apache的子进程，并重用相同的 MySQL 链接。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;PHP手册：mysql_pconnect()&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;在理论上来说，这听起来非常的不错。但是从个人经验（也是大多数人的）上来说，这个功能制造出来的麻烦事更多。因为，你只有有限的链接数，内存问题，文件句柄数，等等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 12px;&quot;&gt;而且，Apache 运行在极端并行的环境中，会创建很多很多的了进程。这就是为什么这种“永久链接”的机制工作地不好的原因。在你决定要使用“永久链接”之前，你需要好好地考虑一下你的整个系统的架构。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('87', 'PHP高手干货分享：要大大提高PHP效率，不能不看的50个细节', '传智播客', '20', '67', '/Upload/article/56de70516d8d9.jpg', '2016-03-08 14:25:21', '用单引号代替双引号来包含字符串，这样做会更快一些。因为PHP会在双 引号包围的字符串中搜寻变量， 单引号则不会，注意：只有echo能这么做，它是一种可以把多个字符串当作参数的”函数”(译注：PHP手册中说echo是语言结构，不是真正的函数，故 把函数加上了双引号)。', '&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;1、用单引号代替双引号来包含字符串，这样做会更快一些。因为PHP会在双\r\n引号包围的字符串中搜寻变量， \r\n单引号则不会，注意：只有echo能这么做，它是一种可以把多个字符串当作参数的”函数”(译注：PHP手册中说echo是语言结构，不是真正的函数，故\r\n把函数加上了双引号)。&lt;/span&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;2、如果能将类的方法定义成static，就尽量定义成static，它的速度会提升将近4倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;3、$row[‘id’] 的速度是$row[id]的7倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;4、echo 比 print 快，并且使用echo的多重参数(译注：指用逗号而不是句点)代替字符串连接，比如echo $str1,$str2。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;5、在执行for循环之前确定最大循环数，不要每循环一次都计算最大值，最好运用foreach代替。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;6、注销那些不用的变量尤其是大数组，以便释放内存。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;7、尽量避免使用__get，__set，__autoload。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;8、require_once()代价昂贵。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;9、include文件时尽量使用绝对路径，因为它避免了PHP去include_path里查找文件的速度，解析操作系统路径所需的时间会更少。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;10、如果你想知道脚本开始执行(译注：即服务器端收到客户端请求)的时刻，使用&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;$_SERVER[‘REQUEST_TIME’]&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;要好于&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;time()&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;11、函数代替正则表达式完成相同功能。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;12、str_replace函数比preg_replace函数快，但strtr函数的效率是str_replace函数的四倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;13、如果一个字符串替换函数，可接受数组或字符作为参数，并且参数长度不太长，那么可以考虑额外写一段替换代码，使得每次传递参数是一个字符，而不是只写一行代码接受数组作为查询和替换的参数。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;14、使用选择分支语句(译注：即switch case)好于使用多个if，else if语句。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;15、用@屏蔽错误消息的做法非常低效，极其低效。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;16、打开apache的mod_deflate模块，可以提高网页的浏览速度。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;17、数据库连接当使用完毕时应关掉，不要用长连接。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;18、错误消息代价昂贵。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;19、在方法中递增局部变量，速度是最快的。几乎与在函数中调用局部变量的速度相当。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;20、递增一个全局变量要比递增一个局部变量慢2倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;21、递增一个对象属性(如：$this-&amp;gt;prop++)要比递增一个局部变量慢3倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;22、递增一个未预定义的局部变量要比递增一个预定义的局部变量慢9至10倍。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;23、仅定义一个局部变量而没在函数中调用它，同样会减慢速度(其程度相当于递增一个局部变量)。PHP大概会检查看是否存在全局变量。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;24、方法调用看来与类中定义的方法的数量无关，因为我(在测试方法之前和之后都)添加了10个方法，但性能上没有变化。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;25、派生类中的方法运行起来要快于在基类中定义的同样的方法。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;26、调用带有一个参数的空函数，其花费的时间相当于执行7至8次的局部变量递增操作。类似的方法调用所花费的时间接近于15次的局部变量递增操作。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;27、Apache解析一个PHP脚本的时间要比解析一个静态HTML页面慢2至10倍。尽量多用静态HTML页面，少用脚本。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;28、除非脚本可以缓存，否则每次调用时都会重新编译一次。引入一套PHP缓存机制通常可以提升25%至100%的性能，以免除编译开销。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;29、尽量做缓存，可使用memcached。memcached是一款高性能的内存对象缓存系统，可用来加速动态Web应用程序，减轻数据库负载。对运算码 (OP code)的缓存很有用，使得脚本不必为每个请求做重新编译。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;30、当操作字符串并需要检验其长度是否满足某种要求时，你想当然地会使用strlen()函数。\r\n此函数执行起来相当快，因为它不做任何计算， 只返回在zval \r\n结构(C的内置数据结构，用于存储PHP变量)中存储的已知字符串长度。但是，由于strlen()是函数，多多少少会有些慢，因为函数调用会经过诸多步\r\n \r\n骤，如字母小写化(译注：指函数名小写化，PHP不区分函数名大小写)、哈希查找，会跟随被调用的函数一起执行。在某些情况下，你可以使用isset()\r\n 技巧加速执行你的代码。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;(举例如下)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;if (strlen($foo) &amp;lt; 5) { echo “Foo is too short”$$ }&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;(与下面的技巧做比较)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;if (!isset($foo{5})) { echo “Foo is too short”$$ }&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;调用isset()恰巧比strlen()快，因为与后者不同的是，isset()作为一种语言结构，意味着它的执行不需要函数查找和字母小写化。也就是说，实际上在检验字符串长度的顶层代码中你没有花太多开销。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;31、当执行变量$i的递增或递减时，$i++会比++$i慢一些。这种差异是PHP特有的，并不\r\n适用于其他语言，所以请不要修改你的C或 \r\nJava代码并指望它们能立即变快，没用的。++$i更快是因为它只需要3条指令(opcodes)，$i++则需要4条指令。后置递增实际上会产生一个\r\n \r\n临时变量，这个临时变量随后被递增。而前置递增直接在原值上递增。这是最优化处理的一种，正如Zend的PHP优化器所作的那样。牢记这个优化处理不失为\r\n 一个好主意，因为并不是所有的指令优化器都会做同样的优化处理，并且存在大量没有装配指令优化器的互联网服务提供商(ISPs)和服务器。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;32、并不是事必面向对象(OOP)，面向对象往往开销很大，每个方法和对象调用都会消耗很多内存。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;33、并非要用类实现所有的数据结构，数组也很有用。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;34、不要把方法细分得过多，仔细想想你真正打算重用的是哪些代码?&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;35、当你需要时，你总能把代码分解成方法。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;36、尽量采用大量的PHP内置函数。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;37、如果在代码中存在大量耗时的函数，你可以考虑用C扩展的方式实现它们。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;38、评估检验(profile)你的代码。检验器会告诉你，代码的哪些部分消耗了多少时间。Xdebug调试器包含了检验程序，评估检验总体上可以显示出代码的瓶颈。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;39、mod_zip可作为Apache模块，用来即时压缩你的数据，并可让数据传输量降低80%。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;40、在可以用file_get_contents替代file、fopen、feof、\r\nfgets等系列方法的情况下，尽量用 \r\nfile_get_contents，因为他的效率高得多!但是要注意file_get_contents在打开一个URL文件时候的PHP版本问题；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;41、尽量的少进行文件操作，虽然PHP的文件操作效率也不低的；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;42、优化Select SQL语句，在可能的情况下尽量少的进行Insert、Update操作(在update上，我被恶批过)；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;43、尽可能的使用PHP内部函数(但是我却为了找个PHP里面不存在的函数，浪费了本可以写出一个自定义函数的时间，经验问题啊!)；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;44、循环内部不要声明变量，尤其是大变量：对象(这好像不只是PHP里面要注意的问题吧?)；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;45、多维数组尽量不要循环嵌套赋值；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;46、在可以用PHP内部字符串操作函数的情况下，不要用正则表达式；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;47、foreach效率更高，尽量用foreach代替while和for循环；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;48、用单引号替代双引号引用字符串；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;49、”用i+=1代替i=i+1。符合c/c++的习惯，效率还高”；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;50、对global变量，应该用完就unset()掉&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('88', '12个提问频率最高的PHP面试题', '传智播客', '20', '688', '/Upload/article/56de70ab59064.jpg', '2016-03-08 14:26:51', '本文为大家分一些被提问频率最高的12个PHP面试题，以及对应的常规回答。希望各位都能找到好工作。', '&lt;p&gt;&lt;span style=&quot;font-family:楷体, 楷体_GB2312&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:#ff0000&quot;&gt;本文为大家分一些被提问频率最高的12个PHP面试题，以及对应的常规回答。希望各位都能找到好工作。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family:仿宋, 仿宋_GB2312&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问1：请用简单的语言告诉我PHP 是什么？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：PHP全称：Hypertext Preprocessor，是一种用来开发动态网站的服务器脚本语言。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问2：什么是MVC？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：MVC由Model（模型）, View（视图）和Controller（控制器）组成，PHP \nMVC可以更高效地管理好3个不同层的PHP代码。 Model：数据信息存取层。 View：view层负责将应用的数据以特定的方式展现在界面上。 \nController：通常控制器负责从视图读取数据，控制用户输入，并向模型发送数据。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问3：在页面中引用CSS有几种方式？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;在页面中使用CSS有3中方式：引用外部CSS文件内部定义Style样式&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问4：PHP支持多继承吗？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：不可以。PHP类只能继承一个父类，并用关键字“extended”标识。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问5：请问PHP中echo和print有什么 区别？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：这两个看起来很相似，因为它们都是将一些值打印在屏幕上。但是echo和print的本质区别在于：echo用来输出字符串，显示多个值的时候可以用逗号隔开。只支持基本类型， print不仅可以打印字符串值， 而且可以打印函数的返回值。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问6：请问GET和POST方法有什么区 别？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：我们再网页上填写的表单信息都可以通过这两个方法将数据传递到服务器上，当我们使用GET方法是，所有的信息\n都会出现在URL地址中，并且使用GET方法最多只能传递1024个字符，所以如果在传输量小或者安全性不那么重要的情况下可以使用GET方法。说到\nPOST 方法，最多可以传输2MB字节的数据，而且可以根据需要调节。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问7：PHP中获取图像尺寸大小的方 法是什么？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;getimagesize()获取图片的尺寸Imagesx()获取图片的宽度Imagesy()获取图片的高度&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问8：PHP中的PEAR是什么？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：PEAR也就是为PHP扩展与应用库（PHP Extension and Application Repository），它是一个PHP扩展及应用的一个代码仓库。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问9：如何用PHP和MySQL上传视频？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#000&quot;&gt;答：我们可以在数据库中存放视频的地址，而不需要将真正的视频数据存在数据库中。可以将视频数据存放在服务器的指定文件夹下，上传的默认大小是 2MB，但是我们也可以php.&lt;/span&gt;&lt;span style=&quot;color:#2d64b3&quot;&gt;&lt;a class=&quot;gj_safe_a&quot; href=&quot;http://www.baidu.com/s?wd=ini%E6%96%87%E4%BB%B6&amp;ie=gbk&amp;tn=SE_hldp00990_u6vqbx10&quot; target=&quot;_blank&quot;&gt;ini文件&lt;/a&gt;&lt;/span&gt;&lt;span style=&quot;color:#000&quot;&gt;中修改max_file size选项来改变。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问10：PHP中的错误类型有哪些？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;答：PHP中遇到的错误类型大致有3 类。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#000&quot;&gt;1.提示：这都是一些非常正常的信息，而非重大的错误，有些甚至不会展示给用户。比如访问不存在的变量。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;2.警告：这是有点严重的错误，将会把警告信息展示给用户，但不会影响代码的输出，比如包含一些不存在的文件。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;3.错误：这是真正的严重错误，比如访问不存在的PHP类。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问11：如何在PHP中定义常量？&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;font-family:仿宋, 仿宋_GB2312&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;答：PHP中使用Define()来定义常量。define(“Newconstant”, 30)。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&lt;strong&gt;问12：如何不使用submit按钮来提交表单？&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;p&gt;&lt;span style=&quot;color:#000&quot;&gt;答：如果我们不想用submit按钮来提交表单，我们也可以用超链接来提交，我们可以这样写代码：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#000&quot;&gt;&amp;lt;a href=”javascript: document.myform.submit();”&amp;gt; Submit Me&amp;lt;/a&amp;gt;.&lt;/span&gt;&lt;/p&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('89', 'PHP知识点大分享，求鉴定！', '传智播客', '20', '56', '/Upload/article/56de739f2549a.jpg', '2016-03-08 14:39:27', '1. 变量如何定义？如何检查变量是否定义？如何删除一个变量？怎样检测变量是否设置？', '&lt;p&gt;&lt;strong&gt;1. &lt;span style=&quot;font-family:宋体&quot;&gt;变量如何定义？如何检查变量是否定义？如何删除一个变量？怎样检测变量是否设置？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$&lt;span style=&quot;font-family:宋体&quot;&gt;定义&amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;isset()// &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;检测变量是否设置&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;defined&lt;span style=&quot;font-family:宋体&quot;&gt;（）&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;// &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;检测常量是否设置&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;unset()//&lt;span style=&quot;font-family:宋体&quot;&gt;销毁指定的变量&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;empty()// &lt;span style=&quot;font-family:宋体&quot;&gt;检测变量是否为空&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2. &lt;span style=&quot;font-family:宋体&quot;&gt;什么是可变变量？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;一个变量的变量名可以动态的设置和使用。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$a = &amp;#39;hello&amp;#39; , $$a = &amp;#39;world&amp;#39;,&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;${$a}=hello world&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3. &lt;span style=&quot;font-family:宋体&quot;&gt;变量赋值方式有哪几种？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）直接赋值&amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）变量间赋值&amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）引用赋值&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4. &lt;span style=&quot;font-family:宋体&quot;&gt;引用和拷贝有什么区别？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;拷贝是将原来的变量内容复制下来，拷贝后的变量与原来的变量使用各自的内存，互不干扰。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;引用相当于是变量的别名，其实就是用不同的名字访问同一个变量内容。当改变其中一个变量的值时，另一个也跟着发生变化。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5. php&lt;span style=&quot;font-family:宋体&quot;&gt;中变量有哪些基本数据类型？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;php&lt;span style=&quot;font-family:宋体&quot;&gt;支持&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;8&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;种原始数据类型包括：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;四种标量类型（布尔型&lt;span style=&quot;font-family:Arial&quot;&gt;boolean&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，整型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;interger&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，浮点型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;float/double , &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;字符串&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;string&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;两种复合类型&lt;span style=&quot;font-family:Arial&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数组&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;array &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;， 对象&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;object)&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;两种特殊类型（资源&lt;span style=&quot;font-family:Arial&quot;&gt;resource&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;NULL&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6. &lt;span style=&quot;font-family:宋体&quot;&gt;其它类型转换为&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;boolean&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;类型时，哪些被认为是&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;false&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;？&lt;/span&gt;&amp;nbsp; &amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;布尔值&lt;span style=&quot;font-family:Arial&quot;&gt;false&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，整型值&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;0&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;， 浮点型值&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;0.0&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;， 空白字符串， 字符串&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;&amp;#39;0&amp;#39;, &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;空数组，特殊数据类型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;NULL, &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;没有设定的变量。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;empty()&lt;span style=&quot;font-family:宋体&quot;&gt;函数在哪些情况下返回&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;true&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;？&lt;/span&gt;&lt;/p&gt;&lt;p&gt;布尔值&lt;span style=&quot;font-family:Arial&quot;&gt;false&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，整型值&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;0&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;， 浮点型值&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;0.0&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;， 空白字符串， 字符串&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;&amp;#39;0&amp;#39;, array()&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;空数组，特殊数据类型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;NULL, &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;没有任何属性的对象，没有赋值的变量。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7. &lt;span style=&quot;font-family:宋体&quot;&gt;如果定义了一个变量&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;$a ,&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;但是没有赋初始值,&lt;/span&gt; 那么&lt;span style=&quot;font-family:Arial&quot;&gt;$a==0&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;吗？&lt;/span&gt; $a==false&lt;span style=&quot;font-family:宋体&quot;&gt;吗？&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;$a==&amp;#39;&amp;#39;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;吗？&lt;/span&gt;&amp;nbsp; &amp;nbsp;$a==NULL&lt;span style=&quot;font-family:宋体&quot;&gt;吗? &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;$a===NULL&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;吗？&lt;/span&gt;&lt;/strong&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;答：&lt;span style=&quot;font-family:Arial&quot;&gt;echo=&amp;gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;什么都没有 &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;, var_dump=&amp;gt;NULL&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;empty($b)==true&lt;span style=&quot;font-family:宋体&quot;&gt;吗？———————————— &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;echo=&amp;gt;1 , var_dump=&amp;gt;bool(true) &lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;此时如果输出&lt;span style=&quot;font-family:Arial&quot;&gt;$a++&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;是多少？———————&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;echo=&amp;gt;&lt;span style=&quot;font-family:宋体&quot;&gt;什么都没有 &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;, var_dump=&amp;gt;NULL&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;如果输出&lt;span style=&quot;font-family:Arial&quot;&gt;++$a&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;又是多少？—————————&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;echo=&amp;gt;1&amp;nbsp;&amp;nbsp;, var_dump=&amp;gt;int(1)&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8. &lt;span style=&quot;font-family:宋体&quot;&gt;字符串怎么转成整数，有几种方法？怎么实现？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;强制类型转换&lt;span style=&quot;font-family:Arial&quot;&gt;: (&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;整型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;字符串变量名&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;直接转换：&lt;span style=&quot;font-family:Arial&quot;&gt;settype(&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;字符串变量&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;,&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;整型&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;intval(&lt;span style=&quot;font-family:宋体&quot;&gt;字符串变量&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;9. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;标量数据和数组的最大区别是什么？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;一个标量只能存放一个数据，而数组可以存放多个数据。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;10. &lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;常量如何定义&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;? &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如何检测一个常量是否被定义？常量的值只能是哪些数据类型？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;define()//&lt;span style=&quot;font-family:宋体&quot;&gt;定义常量 &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;, defined()//&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;检查常量是否定义&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;常量的值只能是标量类型的数据。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;11. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;常量分为系统内置常量和自定义常量。请说出最常见的几个系统内置常量？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;__FILE__ , __LINE__ , PHP_OS , PHP_VERSION&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;12. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;如果定义了两个相同的常量，前者和后者哪个起作用？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;前者起作用，因为常量一旦定义就不能被重新定义或者取消定义。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;13. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;常量和变量有哪些区别？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）常量前没有&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;$&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;符号；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2&lt;span style=&quot;font-family:宋体&quot;&gt;）常量只能通过&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;define()&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;定义，而不能通过赋值语句定义；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3&lt;span style=&quot;font-family:宋体&quot;&gt;）常量可以在任何地方定义和访问，而变量有全局和局部之分；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;4&lt;span style=&quot;font-family:宋体&quot;&gt;）常量一旦定义就不能被重新定义或者取消定义，而变量而通过赋值方式重新定义；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;5&lt;span style=&quot;font-family:宋体&quot;&gt;）常量的值只能是标量数据，而变量的数据库类型有&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;8&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;种原始数据类型。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;14. &lt;strong&gt;PHP&lt;span style=&quot;font-family:宋体&quot;&gt;中常用的几个预定义的全局数组变量是哪些？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;有&lt;span style=&quot;font-family:Arial&quot;&gt;9&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;大预定义的内置数组变量：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$_POST, $_GET, $_REQUEST, $_SESSION, $_COOKIE, $_FILES&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;$_SERVER, $_ENV, $GLOBALS&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;15. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;在实际开发中，常量最常用于哪些地方？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;1&lt;span style=&quot;font-family:宋体&quot;&gt;）连接数据库的信息定义成常量，如数据库服务器的用户名、密码、数据库名、主机名；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2&lt;span style=&quot;font-family:宋体&quot;&gt;）将站点的部分路径定义成常量，如&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;web&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;绝对路径，&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;smarty&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的安装路径，&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;model&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;view&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;或者&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;controller&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的文件夹路径；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3&lt;span style=&quot;font-family:宋体&quot;&gt;）网站的公共信息，如网站名称，网站关键词等信息。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;16. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;函数的优越性有哪些？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;提高程序的可维护性&amp;nbsp;&amp;nbsp;提高软件的可靠性&amp;nbsp;&amp;nbsp;提高程序的可重用性&amp;nbsp;&amp;nbsp;提高程序的开发效率&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;17. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;如何定义一个函数？函数名区分大小写吗？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）使用 &lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;function &lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关键字；&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）函数命名规则和变量一样，以字母或者下划线开头，而不能以数字开头；&lt;/span&gt; 3&lt;span style=&quot;font-family:宋体&quot;&gt;）函数名不区分大小写；&lt;/span&gt; 4&lt;span style=&quot;font-family:宋体&quot;&gt;）函数名不可以使用已经声明过的或者系统自建的函数名称。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;18. &lt;span style=&quot;font-family:宋体&quot;&gt;&lt;strong&gt;什么是变量的能见度 或者 说是变量作用域？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;就是变量在程序中的可作用范围。根据变量的能见度，变量分为局部变量和全局变量。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;19. 什么是局部变量和全局变量？函数内是否可以直接调用全局变量？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;局部变量是函数内部定义的变量，其作用域是所在的函数。如果函数外还有一个跟局部变量名字一样的变量，程序会认为它们两个是完全不同的两个变量。当\r\n退出函数的时候，其中的局部变量就同时被清除。全局变量是定义在所有函数以外的变量，其作用域是整个php文件，但是在用户自定义的函数内部是无法使用\r\n的。如果一定要在用户自定义的函数内部使用全局变量，那么就需要使用global关键字声明。也就是说如果在函数内的变量前加上golbal来修饰，那么\r\n函数内部就可以访问到这个全局变量，不仅可以利用这个全局变量进行运算而且可以对这个全局变量进行重新赋值。全局变量还可以使用 \r\n$GLOBALS[&amp;#39;var&amp;#39;] 来调用。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;21. 什么是静态变量？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;如果一个函数内定义的变量前使用关键字static来声明，那么该变量就是静态变量。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;一般函数内的变量在函数调用结束后，其存储的数据将被清除，所占的内存空间也被释放。而使用静态变量时，该变量会在函数第一次被调用时被初始化，初\r\n始化后该变量也不会被清除，当再次调用该函数时，这个静态变量不再被初始化，而能保存上次函数执行完后的值。可以说静态变量在所有对该函数的调用之间共\r\n享。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;22. php中函数传递参数的方式有哪些？两者有什么区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;按值传递和按地址传递（或按引用传递）&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(1)按值传递:&amp;nbsp;&amp;nbsp;待传递的变量，与传递给函数之后的变量是存储在不同的空间中。所以函数体内对该变量值做的修改，不影响原本的变量值。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(2)按地址传递: 使用 &amp;amp; \r\n符号，表明该参数是以地址的方式传递值。并不会将主程序中的指定数值或目标变量传递给函数，而是把该数值或变量的内存储存区块地址导入函数之中，所以函数\r\n体内的该变量和主程序中的该变量在内存中是同一个。函数体做的修改，直接影响到函数体外部的该变量的值。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;23. 什么是递归函数？如何进行递归调用？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;递归函数其实就是调用自身的函数，但是必须满足以下两个条件：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1）在每一次调用自身时，必须是更接近于最终结果；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2）必须有一个确定的递归终止条件，不会造成死循环。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;举例说明：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;在实际工作中往往会在遍历文件夹的时候使用。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;如果有个例子是希望获取到目录windows下所有的文件，那么先遍历windows目录，如果发现其中还有文件夹，那么就会调用自身，继续往下寻找，依次类推，直到遍历到再也没有文件夹为止，这也就是意味着遍历出来了所有的文件。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;24. 判断一个函数是否存在？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;function_exists( string $function_name )&amp;nbsp;&amp;nbsp;如果存在，返回true, 不存在则返回 false。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;25. func()和@func()之间有什么区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;第二个函数调用失败不会报错，第一个会报错&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;26. include()和require()函数的用法和区别是什么？include_once()和require_once()呢？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;include和require出现错误后的错误级别不一样&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;include_once()和require_once()在加载之前要判断是否已经导入&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;27. 说出前置++和后置++的区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;前置++是先将变量增加1，然后在将值赋值给原来的变量；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;后置++是先返回变量的当前值，然后再将变量的当前值增加1.&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;28. 字符串运算符“.”与算术运算符“+”有什么区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;当“a”“b”之间使用.时认为是连字符。如果两者间是+的时候php会认为是一次运算。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1）如果+号两边的字符串是数字组成的，那么会将字符串自动转为整型；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2）如果+号两边是纯粹的字母，那么会输出0；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3）如果+号两边的字符串是以数字开头的，那么会截取字符串开头的数字，然后进行运算。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;29. 什么是三目（或三元）运算符？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;根据一个表达式的结果在另两个表达式中选择一个。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;例如: ($a==true) ? &amp;#39;good&amp;#39;:&amp;#39;bad&amp;#39;;&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;30. 控制流程语句有哪些？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1：三种程序结构&amp;nbsp; &amp;nbsp; 顺序结构、分支结构、循环结构&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2：分支： if/esle/esleif/&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;switch/case/default&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3: switch 需要注意的：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; case子句中的常量可以是整型、字符串型常量、 或者常量表达式，不允许是变量。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;同一个switch子句中，case的值不能相同，否则只能取到首次出现case中的值。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;4: 循环&amp;nbsp;&amp;nbsp;for while&amp;nbsp; &amp;nbsp;do...while&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; do...while 后面必须加入分号结尾。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; while 和 do...while 的区别&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;5：break 和 continue 的区别。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; break可以终止循环。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; continue没有break强大，只能终止本次循环而进入到下一次循环中。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;31. 数组的概念是什么？数组根据索引分为哪两种，如何区分？数组的赋值方式有哪两种？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;数组是一个可以存储一组或一系列数值的变量（复合型变量）&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;索引数组（索引值为数字，以0开始）和关联数组（以字符串作为索引值）&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;数组的赋值方式有哪两种？&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;数组的声明方式主要有两种。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1.通过array()函数声明数组；&lt;br/&gt;可以通过key=&amp;gt;value的方式分别定义索引和值，也可以不定义数组的索引下标，只给出数组的元素值。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2.直接为数组元素赋值,不需要调用array()函数。例如：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$arr[0] = 1;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$arr[1] = 2;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;特别注意：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;数组的下标如果是等价于整数的字符串值（但不能以0开头），则会被当作整数对待。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;例如： $array[3] 与&amp;nbsp;&amp;nbsp;$array[&amp;#39;3&amp;#39;] 引用的是相同的一个元素，而 $array[&amp;#39;03&amp;#39;] 引用的则就是另外的一个元素了。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;32.数组如何遍历？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;①for循环&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;②foreach循环&amp;nbsp;&amp;nbsp;，这是最常用的遍历方式。用法如下：foreach($arr as $key=&amp;gt;$value){}&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;③list each 和while 配合起来循环&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;33. foeach数组的时候指针是如何指向的？list()/each()/while()循环数组的时候指针如何指向的呢？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;当foreach开始执行的时候，数组内部的指针会自动指向第一个单元。因为foreach所操作的是指定数组的拷贝，而不是该数组本身。而\r\neach()一个数组后，数组指针将停留在数组中的下一个单元或者碰到数组结尾时停留在最后一个单元。如果要再次使用each()遍历数组，必须要使用\r\nreset().&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;reset()将数组的内部指针倒回到第一个单元并返回第一个数组单元的值。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;34. 如何计算数组长度（或者说计算数组中所有元素的个数）？字符串怎么取长度？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;count() -- 计算数组中的元素个数。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;可以使用count(数组名)或者count(数组名,1),如果有第二个参数，并且是数字1，则表示递归统计数组元素的个数。如果第二个参数是数字0，则等同于只有一个参数的count()函数。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;sizeof() -- count() 的别名（count — 计算数组中的单元数目或对象中的属性个数）&lt;br/&gt;字符串：strlen()— 获取字符串长度&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;mb_strlen()— Get string length&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;35. 数组中相关的常用函数有哪些？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1） count --（sizeof别名）— 计算数组中的单元数目或对象中的属性个数&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; 例如：int count ( mixed $var [, int $mode ] )&amp;nbsp;&amp;nbsp;$var 通常都是数组类型，任何其它类型都只有一个单元。&amp;nbsp;&amp;nbsp;$mode 默认值为0. 1为开启递归地对数组计数&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2） in_array ( mixed $needle , array $haystack [, bool $strict ] )\r\n — 检查数组中是否存在某个值。如果 needle 是字符串，则比较是区分大小写的。如果第三个参数 strict 的值为 TRUE 则 \r\nin_array() 函数还会检查 needle 的类型是否和 haystack 中的相同。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3） array_merge(array $array1 [, array $array2 [, array $... ]] ) 将一个或多个数组的单元合并起来，一个数组中的值附加在前一个数组的后面。返回作为结果的数组。 &lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; 特别注意：如果输入的数组中有相同的字符串键名，则该键名后面的值将覆盖前一个值。然而，如果数组包含数字键名，后面的值将不会覆盖原来的值，而是附加到后面. 如果只给了一个数组并且该数组是数字索引的，则键名会以连续方式重新索引!&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;4） 数组与字符串之间的转换&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; (1)explode ( string $separator , string $string [, int $limit ] )&amp;nbsp;&amp;nbsp;使用一个分隔字符来分隔一个字符串。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; (2)implode ( string $glue , array $arr )&amp;nbsp;&amp;nbsp;使用一个连接符将数组中的每个单元连接为一个字符串。join 为 implode 的别名&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;5） sort(array &amp;amp;$array [, int $sort_flags ]) — 对数组按照值排序，当本函数结束时数组单元将被从最低到最高重新安排。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;36. 数组合并函数array_merge()和数组加法运算$arr + $arr2 的区别是什么？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;array_merge()-&amp;gt;使用array_merge()，如果是关联数组合并，如果数组的键名相同，那么后面的值将覆盖前者；如果是\r\n数字索引数组合并，则不覆盖，而是后者附加到前者后面。&amp;quot;+&amp;quot;-&amp;gt;使用数组加法运算，与array_merge()不同，加法运算不管是关联数组还\r\n是数字索引数组，都是将相同键名的的值舍弃，也就是只保留首次出现该键名的元素，后来的具有相同键名的元素都不会被加进来。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;37. 字符串定义的时候单引号和双引号有什么区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;单引号加载速度比双引号加载速度快.&lt;br/&gt;&lt;strong&gt;38. echo(),print(),print_r()的区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(1)echo 是语法，Output one or more strings，没有返回值；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(2)print 是函数，不能输出数组和对象，Output a string，print有返回值；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(3)print_r 是函数，可以输出数组。print_r是个比较有意思的函数，可以输出stirng、int、float、&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;array、object等，输出array时会用结构表示，print_r输出成功时返回true；而且print_r可以通过\r\nprint_r($str,true)来，使print_r不输出而返回print_r处理后的值。此外，对于echo和print，基本以使用echo\r\n居多，因为其效率比print要高。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;39. 按照功能分类说说都有哪些字符串处理函数？这些函数的作用是什么？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;A. 字符串输出函数&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(1)echo $a,$b,$c......;&amp;nbsp;&amp;nbsp;是一个语言结构，而不是一个真正的函数。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(2)print($a)&amp;nbsp; &amp;nbsp; 本函数输出字符串。若成功则返回 1，失败返回 0&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(3)print_r($a)&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;(4)var_dump($a); 能输出类型、长度、值&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;B. 去除字符串首尾空格的函数： trim&amp;nbsp;&amp;nbsp;ltrim&amp;nbsp;&amp;nbsp;rtrim（别名：chop）&amp;nbsp; &amp;nbsp;使用第二个参数，还可以去除指定的字符。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;C. 转义字符串函数：addslashes()&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;D. 获取字符串长度的函数：strlen()&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;E. 截取字符串长度的的函数：substr()&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;F. 检索字符串函数： strstr() , strpos()&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;G. 替换字符串函数：str_replace()&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;40. 请给出以下问题的正确答案？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1).$arr = array(&amp;#39;james&amp;#39;, &amp;#39;tom&amp;#39;, &amp;#39;symfony&amp;#39;);请将$arr数组的值用’,’分割并合并成字符串输出？&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; echo implode(‘,’,$arr);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2).$str = ‘jack,james,tom,symfony’; 请将$str用’,’分割，并把分割后的值放到$arr数组中？&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; $arr = explode(‘,’,$str);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3).$arr = array(3,7,2,1,’d’,’abc’);&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;请将$arr按照从大到小的顺序排序，并保持其键值不变？&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; arsort($arr); print_r($arr);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;4).$mail = “&lt;a href=&quot;mailto:gaofei@163.com&quot; target=&quot;_blank&quot;&gt;gaofei@163.com&lt;/a&gt;”;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;请将此邮箱的域(163.com)取出来并打印，看最多能写出几种方法？&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; echo strstr($mail,&amp;#39;163&amp;#39;);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; echo substr($mail,7);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; $arr = explode(&amp;quot;@&amp;quot;,$mail); echo $arr[1];&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;5). 如果有一个字符串，该字符串是“123，234，345，”。请问如何切割掉这个字符串最后一个逗号？&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;6). 获取随机数的函数有哪几个？mt_rand() 和 rand()哪个执行速度快？&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;41.页面字符出现乱码，怎么解决?&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;1.首先考虑当前文件是不是设置了字符集。查看是不是meta标签中写了charset，如果是php页面还可以看看是不是&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;在header()函数中指定了charset；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;例如：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;meta http-equiv=&amp;quot;Content-Type&amp;quot; content=&amp;quot;text/html; charset=utf-8&amp;quot; /&amp;gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;header(“content-type:text/html;charset=utf-8”);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;2.如果设置了字符集（也就是charset），那么判断当前文件保存的编码格式是否跟页面设置的字符集保持一致，&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;两者必须保持统一；&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;3.如果涉及到从数据库提取数据，那么判断数据库查询时的字符集是否跟当前页面设置的字符集一致，两者必须统一，&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;例如：mysql_query(“set names utf8”)。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;42. 正则表达式是什么？php中有哪些常用的跟正则相关的函数？请写出一个email的正则，中国手机号码和座机号码的正则表达式？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;正则表达式是用于描述字符排列模式的一种语法规则。正则表达式也叫做模式表达式。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;网站开发中正则表达式最常用于表单提交信息前的客户端验证。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;比如验证用户名是否输入正确，密码输入是否符合要求，email、手机号码等信息的输入是否合法&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;在php中正则表达式主要用于字符串的分割、匹配、查找和替换操作。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;preg系列函数可以处理。具体有以下几个：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;string preg_quote ( string str [, string delimiter] )&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;转义正则表达式字符 正则表达式的特殊字符包括：. \\\\ + * ? [ ^ ] $ ( ) { } = ! &amp;lt; &amp;gt; | :。&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;preg_replace -- 执行正则表达式的搜索和替换&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;mixed preg_replace ( mixed pattern, mixed replacement, mixed subject [, int limit] &lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;preg_replace_callback -- 用回调函数执行正则表达式的搜索和替换&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;mixed preg_replace_callback ( mixed pattern, callback callback, mixed subject [, int limit] )&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;preg_split -- 用正则表达式分割字符串&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;array preg_split ( string pattern, string subject [, int limit [, int flags]] )&lt;br/&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;常用的正则表达式写法：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;中文：/^[\\u4E00-\\u9FA5]+$/&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;手机号码：/^(86)?0?1\\d{10}$/&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;EMAIL：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;/^[\\w-]+[\\w-.]?@[\\w-]+\\.{1}[A-Za-z]{2,5}$/&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;密码（安全级别中）：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;/^(\\d+[A-Za-z]\\w*|[A-Za-z]+\\d\\w*)$/&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;密码（安全级别高）：&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;/^(\\d+[a-zA-Z~!@#$%^&amp;amp;(){}][\\w~!@#$%^&amp;amp;(){}]*|[a-zA-Z~!@#$%^&amp;amp;(){}]+\\d[\\w~!@#$%^&amp;amp;(){}]*)$/&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;43. 请说出目前学过的返回是资源的函数？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;答：&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;fopen（打开文件）&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; imagecreatefromjpeg(png gif) — 从 JPEG 文件新建一图像 &lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; imagecreatetruecolor — 新建一个真彩色图像&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; imagecopymerge — 拷贝并合并图像的一部分&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; imagecopyresized&amp;nbsp;&amp;nbsp;— 拷贝部分图像并调整大小&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; mysql_connect&amp;nbsp;&amp;nbsp;— 打开一个到 MySQL 服务器的连接&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; mysql_query();只有这执行select的时候成功，才返回资源，失败返回FALSE&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;44. preg_replace()和 str_ireplace()两个函数在使用上有什么不同？preg_split()和split()函数如何使用？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; preg_replace — 执行正则表达式的搜索和替换&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; str_ireplace — str_replace() 的忽略大小写版本&amp;nbsp; &amp;nbsp;str_replace — 子字符串替换&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; preg_split — 用正则表达式分割字符串&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp; split — 用正则表达式将字符串分割到数组中&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;45. 获取当前时间戳的函数主要有哪些？用PHP打印出今天的时间，格式是2010-12-10 22:21:21？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;用PHP打印出前一天的时间格式是2010-12-10 22:21:21？如何把2010-12-25 10:30:25变成unix时间戳？&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;echo date (&amp;quot;Y-m-d H:i:s&amp;quot; ,strtotime(‘-1,days’));&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;date(&amp;#39;Y-m-d H:i:s&amp;#39;,time());&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$unix_time = strtotime(&amp;quot;2009-9-2 10:30:25&amp;quot;);//变成unix时间戳&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;echo date(&amp;quot;Y-m-d H:i:s&amp;quot;,$unix_time);//格式化为正常时间格式&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;46.&amp;nbsp; &amp;nbsp;在url中用get传值的时候，若中文出现乱码，应该用哪个函数对中文进行编码？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;用户在网站表单提交数据的时候，为了防止脚本攻击（比如用户输入&amp;lt;script&amp;gt;alert（111）；&amp;lt;/script&amp;gt;），php端接收数据的时候，应该如何处理？&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;使用urlencode()对中文进行编码，使用urldecode()来解码。&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;使用htmlspecialchars($_POST[‘title’])来过滤表单传参就可以避免脚本攻击。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;47. 说说mysql_fetch_row() 和mysql_fetch_assoc()和mysql_fetch_array之间有什么区别？&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;第一个是返回结果集中的一行作为索引数组，第二个是返回关联数组，而第三个既可以返回索引数组也可以返回关联数组，取决于它的第二个参数 MYSQL_BOTH MYSQL_NUM&amp;nbsp;&amp;nbsp;MYSQL_ASSOC 默认为MYSQL_BOTH&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$sql =”select * from table1”;&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;$result = mysql_query($sql);&lt;br/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;mysql_fetch_array($result, MYSQL_NUM);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('90', '神解释：向外行介绍程序员工作的复杂程度', '伯乐在线', '1', '75', '/Upload/article/56de86ea1082c.jpg', '2016-03-08 16:01:46', '「不就加个按钮么？怎么要做三四天？」类似的外行问话，经常在网上能看到。有程序员在 Quora 提问，该如何向外行解释自己工作的复杂度。', '&lt;blockquote&gt;&lt;p&gt;「不就加个按钮么？怎么要做三四天？」类似的外行问话，经常在网上能看到。有程序员在 Quora 提问，该如何向外行解释自己工作的复杂度。&lt;/p&gt;&lt;p&gt;下面是&amp;nbsp;Channing Walton 的用泡茶的例子来解释，非常形象。&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;请他们描述泡出一杯茶需要哪些步骤，他们会这么说：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;烧水&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;把茶叶放到茶壶里&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;水烧开后倒入茶壶&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;等待5分钟&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;把茶倒进杯子&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;加牛奶&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;喝&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;现在，有趣的开始了。你要开始问这样的问题:&lt;/p&gt;&lt;h3&gt;烧水?&lt;/h3&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;水哪来的？&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;热水壶在哪里？&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;你怎么把水倒进热水壶？&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;你怎么知道热水壶壶里要倒多少水？&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;如果没有水/热水壶/电怎么办呢?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;假如加水传感器失效怎么办?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;假如煮水传感器失效怎么办？&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;h3&gt;茶叶放到茶壶里?&lt;/h3&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;茶壶在哪里，如果没有茶壶怎么办？烧水之前我们应该考虑到这些问题吗?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;茶叶在哪里，要用哪一种茶叶？我们是否应该先问清楚，或许如果没有对应的茶叶，我们甚至都不应该开始泡茶?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;关于加水和传感器也可以有类似的问题要问&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;h3&gt;倒开水?&lt;/h3&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;你确定水已经开了么？你怎么能确保“倒水”的机器从热水壶那收到“烧水完成”的信号呢?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;你如何确保倒水的机器知道热水壶在哪里?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;如果热水壶在倒水的过程翻了怎么办呢?&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;等等&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;诸如此类——这样的问题你可以问几个小时。他们会厌烦说，“问到这么细节真是太蠢了”，你但可一笑“的确很蠢”。&lt;/p&gt;&lt;p&gt;ps. 最后，告诉他们，客户其实真正想要的是浓缩咖啡而不是日本茶道——当然，你肯定知道了，这不是显而易见的需求吗?&lt;/p&gt;&lt;hr/&gt;&lt;p&gt;小编最后想到了一张趣图。&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;pic&quot; alt=&quot;&quot; src=&quot;/Upload/ueditor/img/1457424055291732.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;（左：外行眼中的项目；右：开发眼中的项目）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('91', 'PHP简单实现“相关文章推荐”功能的方法', '简明魔法', '20', '59', '/Upload/article/56de8bca33492.jpg', '2016-03-08 16:22:34', '这篇文章主要介绍了PHP简单实现“相关文章推荐”功能的方法,方法简单功能实用,需要的朋友可以参考下', '&lt;p&gt;这篇文章主要介绍了PHP简单实现“相关文章推荐”功能的方法,方法简单功能实用,需要的朋友可以参考下&lt;/p&gt;&lt;p&gt;通常在做内容网站的时候，需要在每一篇文章中出现与该文章相关的文章列表。对于大多数人来说，使用的方法通常是：建立一个关键词列表，判断每篇文章包含有那些关键词，最后根据关键词找出与某篇文章最相关的文章。对于内容比较复杂的网站，确定关键列表词显然会比较麻烦。&lt;/p&gt;&lt;p&gt;本文介绍了与以往方法不同的similar_text（php4,php5）函数来方便的达到我们的预期要求。具体的思路是：&lt;span style=&quot;color: #ff0000&quot;&gt;从文章列表中取出所有的文章标题，将所有的文章标题都同当前标题对比，将对比结果生成一个数组，按照相似度的大小由大到标题，利用similar_text将这些文章标题同原文章标题做对比，按标题的相似程度重新排列标题，就得到了与原文章相似的文章列表&lt;/span&gt;。&lt;/p&gt;&lt;p&gt;这个思路用到的关键函数是：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;int similar_text ( string &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$first&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;, string &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$second&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[, float &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$percent&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;] )&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;它返回的是两个字根串的相同字节数。&lt;/p&gt;&lt;p&gt;按照这个思路，我们建立如下的函数，这个函数的功能是把$arr_title数组按照同$title相似的的顺序重新排列数组。&lt;/p&gt;&lt;table class=&quot;sortEnabled&quot; data-sort=&quot;sortEnabled&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter selectTdClass&quot; width=&quot;19&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;13&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;14&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;15&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;16&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;17&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;18&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;19&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;20&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;21&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;22&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;23&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;24&lt;/div&gt;&lt;div class=&quot;line number25 index24 alt2&quot;&gt;25&lt;/div&gt;&lt;div class=&quot;line number26 index25 alt1&quot;&gt;26&lt;/div&gt;&lt;div class=&quot;line number27 index26 alt2&quot;&gt;27&lt;/div&gt;&lt;div class=&quot;line number28 index27 alt1&quot;&gt;28&lt;/div&gt;&lt;div class=&quot;line number29 index28 alt2&quot;&gt;29&lt;/div&gt;&lt;div class=&quot;line number30 index29 alt1&quot;&gt;30&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code selectTdClass&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$demo_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;简明现代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$demo_arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;简单易懂的现代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;简单明了的现代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;简明扼要的古代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;不简单的现代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;很难懂的现代魔法&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$new_array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;= getSimilar(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$demo_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$demo_arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//print_r($new_array); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;与[$demo_title]最相关的前三个文章是：&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;for&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$j&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=0; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$j&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;=2; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$j&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;++) &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;{ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$j&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;+1).&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;:&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$new_array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$j&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;].&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//$title当前标题，$arrayTitle为需要查找的数组 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;functiongetSimilar(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;{ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_len&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;count&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;for&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=0; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;=(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_len&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;-1); &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;++) &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;{ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//取得两个字符串相似的字节数 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_similar&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;] = similar_text(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$i&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;],&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;arsort(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_similar&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;//按照相似的字节数由高到低排序 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;reset(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_similar&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;//将指针移到数组的第一单元 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$index&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;= 0; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_similaras&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$old_index&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$similar&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;{ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number25 index24 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$new_title_array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$index&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;] = &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$arr_title&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$old_index&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;]; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number26 index25 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$index&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;++; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number27 index26 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number28 index27 alt1&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;return&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$new_title_array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number29 index28 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number30 index29 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;程序运行结果：&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;xhtml plain&quot;&gt;与[简明现代魔法]最相关的前三个文章是： &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;xhtml plain&quot;&gt;1:简单明了的现代魔法 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;xhtml plain&quot;&gt;2:简单易懂的现代魔法 &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;xhtml plain&quot;&gt;3:简明扼要的古代魔法&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;有些需要&lt;strong&gt;注意的地方&lt;/strong&gt;：&lt;/p&gt;&lt;p&gt;关于similar_text速度，有人做过这个一个测试，结果是：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;The speed issues for similar_text seem to be only an issue for long sections of text (&amp;gt;20000 chars).&lt;/p&gt;&lt;p&gt;I found a huge performance improvement in my application by just \ntesting if the string to be tested was less than 20000 chars before \ncalling similar_text.&lt;/p&gt;&lt;p&gt;20000+ took 3-5 secs to process, anything else (10000 and below) took\n a fraction of a second. Fortunately for me, there was only a handful of\n instances with &amp;gt;20000 chars which I couldn&amp;#39;t get a comparison % for.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;如果要直接使用正文作对比速度可能会比较慢。&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;据说这个函数用于英文的效果不太好（感兴趣的读者可以自行尝试）。&lt;span style=&quot;color: #0000ff&quot;&gt;用于英文时可以将英文句子用空格分开成多个单词后再写一个类似于similar_text的函数。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #0000ff&quot;&gt;另外，如果句子中含有比较多“的”、“了”等非关键词字符时，得到的结果可能会不太理想。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('92', '有意思的技术文章php介绍篇', 'jb51', '20', '86', '/Upload/article/56e2909966200.jpg', '2016-03-11 17:32:09', '为了让PHP初学者少走一些弯路.所以突然神经恍惚.决定写下此文章.仅供PHP初学者参考.如有错误.还望指出.不甚感激.', '&lt;p&gt;为了让PHP初学者少走一些弯路.所以突然神经恍惚.决定写下此文章.仅供PHP初学者参考.如有错误.还望指出.不甚感激.&lt;/p&gt;&lt;p&gt;身为一名中级PHPer菜鸟..无聊了就爱在各个PHP论坛瞎转.看到了好多PHP初学者都问到了很多相同的问题.而且我学PHP的时候也都遇到\r\n过.为了 让PHP初学者少走一些弯路.所以突然神经恍惚.决定写下此文章.仅供PHP初学者参考.如有错误.还望指出.不甚感激. &lt;br/&gt;PHP其实是一种很简单易学的语言.如果要精通PHP多则三年.少则一年就足够了.但是为什么三年之后我们照样是菜鸟? &lt;br/&gt;不知道从什么开始.学习PHP我们不得不学习数据库.学习架构.学习面向对象.学习前端.学习linux.学习协议甚至美工等直接导致了现在PHPer都是最累的一种程序员. &lt;br/&gt;最累就不算什么了.往往PHP会被认为是下等的程序.拿着最低级的工资.却要掌握web方面几乎全部的知识.这实在是不公平. &lt;br/&gt;可喜的是 我们逐渐在国内看到了转变.国内越来越多的公司从Java或者C#等转到了PHP.所以各位PHPer一定要有自己的梦想.你们都会很牛B的.请坚信. &lt;br/&gt;扯远了.回到我要说的重点上.下面几个问题包含着我遇到的问题.也许我说的不一定对或者你不一定同意我的看法.十分愿意接受你的意见.总之我们只是想让PHPer都雄起:-) &lt;br/&gt;&lt;strong&gt;1.编码的问题 &lt;br/&gt;&lt;/strong&gt;这不光是PHP..只要是程序上.就会遇到.至少我和我身边的人都遇到了.而且在Web方面.往往编码问题更是非常难解决.非常难调试.有时候程序出了莫名其妙的问题,很难想到是编码出了问题等等. &lt;br/&gt;这些问题总是困扰着我们.所以我建议PHP初学者.一定要编码统一.并且强烈建议统一为UTF-8.中文不建议使用GBK或者GB2312等.因为在AJAX中传输的时候是不认识这种编码的. &lt;br/&gt;如果要统一编码,就要在编码可能出现的任何地方注意一下. &lt;br/&gt;&lt;br/&gt;1) header头的编码 &lt;br/&gt;个人建议在你的PHP中都应该加上下面这句话(特殊header除外) &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut63055&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;header(&amp;quot;Content-type: text/html;charset=utf-8&amp;quot;); &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;这样可以避免一些问题.比如我们可以看到有些网站submit后就是一个alert然后跳转.但是他们没有设置编码.如果他的编码跟浏览器的默认编码不一致.就会出现乱码了. &lt;br/&gt;你是单纯的HTML.一定要记得把meta的编码标签放到title标签前面. 如果你不理解就记住把编码的信息放到head标签的第一行就可以了.最终的效果是这样的 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut54769&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;head&amp;gt; &lt;br/&gt;&amp;lt;meta http-equiv=&amp;quot;Content-Type&amp;quot; content=&amp;quot;text/html; charset=utf-8&amp;quot; /&amp;gt; &lt;br/&gt;... &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;2) 文件的编码 &lt;br/&gt;我们保存文件的时候,选择编码一定要统一.如果按照上面的.我们就应该选择UTF-8. &lt;br/&gt;一般的编辑器都有此文件的编码信息.如果你发现不是UTF-8.请马上更改. &lt;br/&gt;比如我们在Dreamweaver中打开了一个模板文件.发现右下角显示 GB2312(简体中文).此时不要做任何更改.马上CTRL+J 然后选择编码更改成UTF-8模式并保存. &lt;br/&gt;文件的编码不一定是PHP文件的.也得保证你的JavaScript文件与css文件都为UTF-8编码. &lt;br/&gt;另外还有一个重要的一点.就是BOM..这个东东PHP是不太欢迎的.所以我们在保存的时候要选择UTF-8无BOM格式.如果你用的编辑器是UE.那么请你着重注意这一点. &lt;br/&gt;3) 数据库的编码 &lt;br/&gt;这个我就不想多说了.网上有太多的文章.如果你是MySQL记得每次连接后进行SET NAMES = ‘utf8′就可以了.并且那个utf8中间是没有横线的. &lt;br/&gt;&lt;strong&gt;2.MVC的问题 &lt;br/&gt;&lt;/strong&gt;程序员理论上来说应该是个完美主义者.他们不光想着把程序写的运行就可以了.而且还要优雅. &lt;br/&gt;但是话说回来.把程序按照MVC做了以后我们的代码就优雅了吗? 答案当然是否定的. &lt;br/&gt;学习架构是一个理解的过程.当你真正体会到这个架构的好处的时候.才会理解其真正的含义.切勿盲目. &lt;br/&gt;有时候我们会做这样的打算.用这个月的时间去学习MVC.好吧.我想你应该这样去做.你学会给自己定目标了. &lt;br/&gt;但是一个月后我们也许并不能真正的理解MVC..但是至少我们会根据他的模式他的基本.换句话说也许我们没有学会葵花宝典.但是我们应该会用辟邪剑法了. &lt;br/&gt;&lt;strong&gt;3.面向对象的问题 &lt;br/&gt;&lt;/strong&gt;关于PHP面向对象的问题.一直都纠缠不清.有的人不觉得PHP面向对象是个好的东西.有的人甚至不认为面向对象是个好的玩意,比如Linus. &lt;br/&gt;我们不必去纠缠这个东西到底好不好.至少我们应该知道从面向对象诞生到现在.经过了这么多年的演变.运用到各种项目甚至语言中.我们有理由相信存在即有其道理. &lt;br/&gt;实践是检验真理的唯一标准.这么多年面向对象越发展形势越好.看来我们学习就变得是种必须了. &lt;br/&gt;与一些架构的知识类似.面向对象也不是那么好学的.面向对象是种理念.但是具体到某种语言的时候.其意义也会发生一些微妙的变化. &lt;br/&gt;PHP的面向对象很灵活.再加上其特有的魔术方法.会造就出一种特别形势的面向对象.也许这跟你平时看Java的面向对象不太一样. &lt;br/&gt;不一定把你需要的方法放到一个类中就是面向对象.当你真正体会到面向对象给你的编码带来快捷.带来方便.你就对面向对象有更深的一层理解了. &lt;br/&gt;正所谓欲速则不达.切勿心急.你想几天之内理解相对论那纯粹是扯淡. &lt;br/&gt;&lt;strong&gt;4.算法的问题 &lt;br/&gt;&lt;/strong&gt;似乎PHP与算法总是相距甚远的.PHP大多数算法都是基于数组实现的.而我们又知道PHP的数组的特性就注定当数组变得非常大的时候.效率会直线下降. &lt;br/&gt;其实PHP就是个做网站的.大家不要也不必给她带什么高帽子.在Web方面尤其是在PHP所服务的前端的实际情况下会涉及到算法的地方会非常少. &lt;br/&gt;PHP的算法面试题无非就是对数组或者对字符串的操作..动动脑子加上手册.那基本就没问题了. &lt;br/&gt;那你也许会问.我想学习PHP.那我还学习算法吗? &lt;br/&gt;我觉得你需要学.并且更需要学数据结构.这门功课就像是一门内功.会在潜移默化中影响你的编码. &lt;br/&gt;这时候再配合上我们前面的辟邪剑法.恭喜你 你已经学会葵花宝典了! &lt;br/&gt;&lt;strong&gt;5.框架的问题 &lt;br/&gt;&lt;/strong&gt;PHP的框架是各种语言中最多的.用不用框架.选什么框架这又成为一个争议的话题.. &lt;br/&gt;我的建议是非常推荐学习至少一款框架.这可以加深你对PHP的认识.我们学习框架不是说要学这个框架怎么用.怎么做项目. &lt;br/&gt;我们要去看它的源码.看看他是怎么实现的.这时候你就可以选择自己开发一个小型的框架.不求做的多好.不求别人多少项目去运用. &lt;br/&gt;我们只是为了学习而已. &lt;br/&gt;&lt;strong&gt;6.JavaScript的问题 &lt;br/&gt;&lt;/strong&gt;可以说我看过这么多语言.最神奇就莫过于JavaScript了.平时Java一直宣称自己一切皆对象.其实我更觉得JavaScript才是一切皆对象. &lt;br/&gt;JavaScript好学吗? 好学!其实在平时Web运用时候.一般的JavaScript操作就是DOM操作. &lt;br/&gt;JavaScript难学吗? 难学!如果我们被其他语言的面向对象的思维束缚了.就很难理解JavaScript的面向对象.并且JavaScript里面概念也非常多. &lt;br/&gt;prototype arguments call apply callee caller 闭包等概念就让我们不知所措.再加上诡异的JavaScript的代码风格与其难调试的特征直接导致我们这些菜鸟避而远之. &lt;br/&gt;曾经我转发过一段实现document.ready的代码.其诡异程度不得不佩服. &lt;br/&gt;个人觉得看一些JavaScript框架的源码就是最好的学习方法.曾经在写我的PHP框架的时候.由于使用了统一入口+自己定义的URL规则.所以再使用普通属性为get的form的时候就会获取不到参数. &lt;br/&gt;这时候我就想到了jQuery.form中的formSerialize方法.摘选出来并做了一些修改.就可以拿来使用了. &lt;br/&gt;&lt;strong&gt;7.CSS问题 &lt;br/&gt;&lt;/strong&gt;我之所与把CSS跟JavaScript分开来说.我觉得CSS的学习更有特点. &lt;br/&gt;其实要我说CSS是最简单的东西了.我们只是知道了一些基本的CSS属性.写页面绝对不成问题. &lt;br/&gt;CSS的主要特征在于必须动手.你看N本CSS的书不如实际动手去写一个页面. &lt;br/&gt;CSS另外一个特点就是得多试.也许这个属性不能达到你的效果.那你就换一个.这也是最笨最有效的办法:-) &lt;br/&gt;当然 这只是初级CSS的要求. 会了这些之后 我们还得兼容浏览器. 其实这个也不难 无非就是几个浏览器同时开着一个属性一个属性的试. &lt;br/&gt;也可以熟记一些hack 比如IE6认识_ IE都认识*等.如果你觉得这样写CSS不够标准.那么把hack放到不同的css文件中.比如ie6的就叫ie6.css &lt;br/&gt;然后再页面上用下面的代码就可以了. &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut32742&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;!--[if lt IE 6]&amp;gt; &lt;br/&gt;&amp;lt;link href=&amp;quot;css/ie6.css&amp;quot; rel=&amp;quot;stylesheet&amp;quot; type=&amp;quot;text/css&amp;quot; /&amp;gt; &lt;br/&gt;&amp;lt;![endif]--&amp;gt; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;更高级的当然是用户的体验与设计了.这个我也不懂就不说了@_@ 希望达人补充. &lt;br/&gt;&lt;strong&gt;8.linux的问题 &lt;br/&gt;&lt;/strong&gt;个人强烈建议每人的电脑上都安装一个linux系统.如果你觉得太折腾 可以选择wubi方式安装Ubuntu.对于懒人这个是最简单有效的方法了. &lt;br/&gt;你无聊的时候可以下载一份PHP源码自己编译下试试.切忌apt-get.因为往往服务器版本不是rhel就是centos.那里是没有这个命令的. &lt;br/&gt;而且apt-get到的往往不是最新的版本.并且又不能自己定制. &lt;br/&gt;linux总会出现这样活着那样莫名其妙的问题.比如环境稍微不一样就编译不通过.服务起不来. &lt;br/&gt;这时候不要着急.看错误提示 看日志.Google下自己尝试解决. &lt;br/&gt;编译完不要觉得就完了 试试不同的配置.而且修改一下源码再编译试试. &lt;br/&gt;比如我们下载了一份nginx的源码.然后修改下header头等一些地方. 就可以编译一份属于自己的HTTP Web Server了. &lt;br/&gt;当然 你如果叫BWS或者GWS也行:-) &lt;br/&gt;还有个问题 linux需要背命令吗? 这个是无可厚非的. 熟记一些常用的命令可以提高速度.这点我需要反思.经常一些常用的命令还需要看下参数.. &lt;br/&gt;另外一个就是Shell的问题. Shell其实不难.但是语法在我看来比较丑陋@_@. 看看wiki就可以学习大半了..主要还得是联系. 我们可以看到一个现象.一些公司在招聘高级PHPer的时候这条也列为必须条件了. &lt;br/&gt;其实网上linux的资料非常多. 大多数你遇到的问题别人都会遇到.所以善于用Google. 并且熟读英语. &lt;br/&gt;&lt;strong&gt;9.语言的问题 &lt;br/&gt;&lt;/strong&gt;这个的问题就太多口水战了.最终谁也没有战胜谁.我们不必去参与这些无聊的事情.这些都是年轻粪青要做的. &lt;br/&gt;你再牛B你有Linus牛B吗? 他喷C++的喷了这么多年.C++怎么样了? &lt;br/&gt;所以我们不必纠缠这些.做好自己做的就OK了. &lt;br/&gt;既然你选择了PHP.就不要犹豫. &lt;br/&gt;也许我们在做项目的时候PHP显得不够用了.那我们可以用Java或者C来做个中间件.这又何尝不可. &lt;br/&gt;一种语言发挥她的最大最用才是真理. &lt;br/&gt;&lt;strong&gt;10.态度的问题&lt;/strong&gt; &lt;br/&gt;态度一定要谦虚谨慎. 这是作为一个程序员应该有的美德.切勿自我膨胀. &lt;br/&gt;你学的越多 你就会发现自己不足就越多. &lt;br/&gt;等你牛B了你可以说一些狂妄的话.但是现在我们都不牛B.至少如果你看到我写的这篇文章.你应该还不太牛B. 我说了这篇文章是面向初学者的:-) &lt;br/&gt;&lt;strong&gt;11.女人的问题 &lt;br/&gt;&lt;/strong&gt;呃..这个自己解决吧@_@&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('93', 'PHP编程开发怎么提高编程效率 提高PHP编程技术', 'jb51', '20', '64', '/Upload/article/56e291110ab51.jpg', '2016-03-11 17:34:09', '用单引号代替双引号来包含字符串，这样做会更快一些。因为PHP会在双引号包围的字符串中搜寻变量，单引号则 不会，注意：只有echo能这么做，它是一种可以把多个字符串当作参数的“函数”(译注：PHP手册中说echo是语言结构，不是真正的函数，故把函数加 上了双引号)。', '&lt;p&gt;这篇文章主要介绍了PHP编程开发怎么提高编程效率(牢记这20个点)的相关资料,需要的朋友可以参考下&lt;/p&gt;&lt;p&gt;用单引号代替双引号来包含字符串，这样做会更快一些。因为PHP会在双引号包围的字符串中搜寻变量，单引号则 \r\n不会，注意：只有echo能这么做，它是一种可以把多个字符串当作参数的“函数”(译注：PHP手册中说echo是语言结构，不是真正的函数，故把函数加\r\n 上了双引号)。&lt;/p&gt;&lt;p&gt;1、如果能将类的方法定义成static，就尽量定义成static，它的速度会提升将近4倍。&lt;/p&gt;&lt;p&gt;2、$row[&amp;#39;id&amp;#39;] 的速度是$row[id]的7倍。&lt;/p&gt;&lt;p&gt;3、echo 比 print 快，并且使用echo的多重参数(译注：指用逗号而不是句点)代替字符串连接，比如echo $str1,$str2。&lt;/p&gt;&lt;p&gt;4、在执行for循环之前确定最大循环数，不要每循环一次都计算最大值，最好运用foreach代替。&lt;/p&gt;&lt;p&gt;5、注销那些不用的变量尤其是大数组，以便释放内存。&lt;/p&gt;&lt;p&gt;6、尽量避免使用__get，__set，__autoload。&lt;/p&gt;&lt;p&gt;7、require_once()代价昂贵。&lt;/p&gt;&lt;p&gt;8、include文件时尽量使用绝对路径，因为它避免了PHP去include_path里查找文件的速度，解析操作系统路径所需的时间会更少。&lt;/p&gt;&lt;p&gt;9、如果你想知道脚本开始执行(译注：即服务器端收到客户端请求)的时刻，使用$_SERVER[‘REQUEST_TIME&amp;#39;]要好于time()。&lt;/p&gt;&lt;p&gt;10、函数代替正则表达式完成相同功能。&lt;/p&gt;&lt;p&gt;11、str_replace函数比preg_replace函数快，但strtr函数的效率是str_replace函数的四倍。&lt;/p&gt;&lt;p&gt;12、如果一个字符串替换函数，可接受数组或字符作为参数，并且参数长度不太长，那么可以考虑额外写一段替换代码，使得每次传递参数是一个字符，而不是只写一行代码接受数组作为查询和替换的参数。&lt;/p&gt;&lt;p&gt;13、使用选择分支语句(译注：即switch case)好于使用多个if，else if语句。&lt;/p&gt;&lt;p&gt;14、用@屏蔽错误消息的做法非常低效，极其低效。&lt;/p&gt;&lt;p&gt;15、打开apache的mod_deflate模块，可以提高网页的浏览速度。&lt;/p&gt;&lt;p&gt;16、数据库连接当使用完毕时应关掉，不要用长连接。&lt;/p&gt;&lt;p&gt;17、错误消息代价昂贵。&lt;/p&gt;&lt;p&gt;18、在方法中递增局部变量，速度是最快的。几乎与在函数中调用局部变量的速度相当。&lt;/p&gt;&lt;p&gt;19、递增一个全局变量要比递增一个局部变量慢2倍。&lt;/p&gt;&lt;p&gt;20、递增一个对象属性(如：$this-&amp;gt;prop++)要比递增一个局部变量慢3倍。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #ff0000&quot;&gt;下面给大家介绍提高PHP编程技术。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;我决定在这里给大家讲述一些能够提高大家PHP代码效果的注意事项：&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、PHP标签&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我知道有些人写PHP代码的时候喜欢用缩略标签—&amp;lt;? \r\n?&amp;gt;，但是这可不是个好习惯，因为缩略标签在有些服务器上不能被正确识别，而标准的PHP标签的使用使你可以在任何一台服务器上准确编译你的PHP\r\n代码。可能有一天你需要把你的代码安装在那些不支持缩略标签的服务器上，于是你将不得不花费一个小时或者更多的时间老实的坐下来升级你的PHP代码&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、PHP代码的调试&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;有时我们运行PHP代码的时候会碰到问题并且我们不知道这问题究竟出在哪儿。而PHP中专门有个error_reporting() 函数,它可以告诉你你的代码中的每一处错误。如果你希望它显示页面的所有可能出现的错误信息，可以将下面这句代码放在文件的第二行：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;--------------------------------------------------------------------------------&lt;br/&gt;error_reporting(E_ALL);&lt;br/&gt;--------------------------------------------------------------------------------&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、PHP代码的调试（补充）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;如果你完成一个有1200行PHP代码的文件，并在浏览器中浏览它时，显示你的代码的错误出现在文件的第561行。此时你有个简单的方法来找到该行，按照以下步骤：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;——新建一记事本&lt;br/&gt;——把你的PHP代码复制进去&lt;br/&gt;——“编辑”-&amp;gt;“转到”&lt;br/&gt;——输入“561”，回车&lt;br/&gt;——你的鼠标就停留在561行了&lt;br/&gt;——在该行附近看看是否存在错误&lt;br/&gt;——修正错误，重新上传代码到你的空间，很可能将正常运行。如果还有什么错误，则重复以上步骤。&lt;br/&gt;彷徨一小生&amp;nbsp; 补充：现在大都用editplus之类的软件，这个方法过时了哦&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、使用注释&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;如果你的PHP代码有1200行，那么要搞明白它要做什么是一件很困难的事，解决这个问题的办法就是为你的代码加上注释。&lt;br/&gt;PHP的注释不同于HTML中的&amp;lt;!-- HTML 注释 --&amp;gt; ，因为它将不被输出（以为着它们甚至不会被在“查看源文件”时看到）&lt;br/&gt;有三中方法在PHP中添加注释：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;--------------------------------------------------------------------------------&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// 你的注释// &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;# 你的注释&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;/*你的注释 */&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;--------------------------------------------------------------------------------&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;你可以按照你所希望的来装饰它们，你是唯一使用它们的人。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、PHP代码的缩进&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我个人其实不喜欢缩进PHP代码，但是它确实使代码易于阅读。当我不得不进行缩进时，我是用tab笺来进行的，如下：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// Settings // &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$var1&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;This&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// Showing Variables // &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$var1&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;== &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;This&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;){ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;You said This&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;else&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;{ &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;You said That&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;-------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;6、修正你的PHP文件包含方式&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我确信这里的大部分人在一个文件中都会需要包含一个或两个其它的文件。有没有想过如果并不存在你所需要的那个文件怎么办？浏览你网页的人会否因此觉得你不够专业？&lt;br/&gt;在我所写的PHP代码中，我在包含另外一个文件之前都会先确定它存在，如下例：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(!&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;file_exists&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;layout.inc.php&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)){&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;exit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Error : LayOut File Missing&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);}&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;else&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;{&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;include_once&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;layout.inc.php&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;7、数据库查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;有时你的PHP代码中包含着于数据库的连接，你可能会遇到一些小麻烦，大部分容易出现数据库问题的人都是以这样的形式来写代码的：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;mysql_query(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;INSERT INTO tableName (&amp;#39;id&amp;#39;,&amp;#39;name&amp;#39;) VALUES(&amp;#39;1&amp;#39;,&amp;#39;Mike&amp;#39;)&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;。。运行之后他发现数据并没有插入到数据库中，我们可以这样来解决这个问题：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;mysql_query(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;INSERT INTO tableName (&amp;#39;id&amp;#39;,&amp;#39;name&amp;#39;) VALUES(&amp;#39;1&amp;#39;,&amp;#39;Mike&amp;#39;)&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;or&lt;/code&gt; &lt;code class=&quot;php functions&quot;&gt;exit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;MySQL Error : &amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;. mysql_error()); &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;strong&gt;8、缩略类似IF-THEN的语句&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;假如你接收一个注册页面传递的数据，而你想保证所有信息都已填写，你可能会用包含很多IF-THEN格式的语句，象下面这个：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(!&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$_POST&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[name]){&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;exit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Sorry, but you did not fill-in all of the requested fields.&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(!&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$_POST&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[email]){&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;exit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Sorry, but you did not fill-in all of the requested fields.&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;而实际上你可以通过合并这两行的IF-THEN语句使它只有一行：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;if&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;((!&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$_POST&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[name]) || (!&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$_POST&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[email])){&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;exit&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Sorry, but you did not fill-in all of the requested fields.&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);} &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;||和OR，&amp;amp;&amp;amp;和AND分别具有相同的意义&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;9、使用echo还是print？&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;大部分人会说“echo和print是相同的”，我同意这个观点。不过echo的运行速度要比print快很多，并且比print少一个字母。echo命令比print出现得晚一些（我这样认为），显然你知道该如何选择了。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;10、不时输入一大段HTML语言&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;我相信很多人都有解决这个的办法，但是我还是想和你说说解决这个的一些办法。&lt;br/&gt;⑴、输入PHP的结束标签，然后就可以随意的输入HTML代码，然后再PHP的开始标签（我不喜欢这样做，因为它看起来很不专业）。&lt;br/&gt;⑵、在每句HTML代码中都加入反斜线（这样是可行的，但是你一直要这样—每一句都要）。&lt;br/&gt;⑶、使用echo或者print命令，仅此而已（推荐）：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://www.jb51.net/article/74563.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;?php &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;// Showing a huge chunk of HTML at a time // &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;&amp;lt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;END&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;font face=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Verdana&amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;color=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Orange&amp;quot;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;size=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;3&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;gt;Large, Orange Text in Font Size 3&amp;lt;/font&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;br&amp;gt;&amp;lt;br&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;More HTML down here.. &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;br&amp;gt;&amp;lt;br&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;lt;div align=&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;Center&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;&amp;gt;Centered text&amp;lt;/div&amp;gt; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;php functions&quot;&gt;END&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;; &lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;--------------------------------------------------------------------------------&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实际上我还有很多其它的关于修饰PHP代码的东西想说，但是就这样吧，我不想继续烦大家了。&lt;br/&gt;希望对大家有所帮助。&lt;/p&gt;&lt;p&gt;以上内容是小编给大家介绍的PHP编程开发怎么提高编程效率 提高php编程技术的全部叙述，希望大家喜欢。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('94', '合格的PHP程序员必备技能', 'jb51', '20', '56', '/Upload/article/56e2914b7a586.jpg', '2016-03-11 17:35:07', '要想成为一个合格的PHP程序员需要掌握许多技能，这篇文章就向大家介绍了一个合格的PHP程序员必备技能，想要真正了解php程序的朋友一定仔细阅读，判断自己具备多少个技能。', '&lt;p&gt;要想成为一个合格的PHP程序员需要掌握许多技能，这篇文章就向大家介绍了一个合格的PHP程序员必备技能，想要真正了解php程序的朋友一定仔细阅读，判断自己具备多少个技能。&lt;/p&gt;&lt;p&gt;作为PHP的爱好者，如果你想加入PHP程序的世界，一定要做好充分的准备。&lt;/p&gt;&lt;p&gt;如果想进入大的企业进行底层开发的话必须对互联网各方面的技术原理了解的很清楚，例如apache实现原理。语言方面既然是php开发自然对 \r\nc/c++要求比较高。往往需要自己写php扩展。使用mysql自然想很多常见的，性能瓶颈要能有很好的解决方案。mysql \r\n插件编写，apache模块编写。联系起来结合点还是要会c。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;倘若是做中间层和前端工作则要求对css，javascript要求比较高。当然对web的一系列实现原理也是要非常熟悉的。&lt;br/&gt;对新技术要比较敏感，才能证明你有很好的学习能力和使用能力。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;努力提高自己的英文口语，和阅读能力。往往很多新技术都是老外想出来的。等有了中文版也许这个技术已经不是那么新了。&lt;br/&gt;相信我们很多人都只是了解到中间的皮毛而已，对于技术原理。这类型的东西了解甚少。值得注意的一点，正则这个东西在他们严重也是很重要的。&lt;br/&gt;想成为一位合格的php程序员（高级）,必须符合如下条件。&lt;br/&gt;&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;1、&lt;strong&gt;文件目录处理函数&lt;/strong&gt;包80％以上的函数的功能的灵活运用。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;2、&lt;strong&gt;日期时间函数&lt;/strong&gt;中的80％以上的函数的功能的灵活运用&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;3、&lt;strong&gt;数学函数库&lt;/strong&gt;中的100％的内容。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;4、&lt;strong&gt;网络库&lt;/strong&gt;中的60％以上的内容，对各个函数的功能比较熟悉。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;5、&lt;strong&gt;字符串处理函数&lt;/strong&gt;下的60％以上的内容，特别是各种处理函数。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;6、&lt;strong&gt;正则表达式函数&lt;/strong&gt;下的90%以上的内容，特别是各种正则处理&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;7、一些安全库下的40％以上的内容，如果对于安全没有接触的话根本就不可能掌握PHP&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;8、&lt;strong&gt;XML处理&lt;/strong&gt;，熟悉SAX、DOM以及JDOM的优缺点并且能够使用其中的一种完成XML的解析及内容处理。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;9、&lt;strong&gt;图形图像函数库&lt;/strong&gt;下的80%以上的内容，特别是一些图像生成和处理&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;10、MySQL 数据库函数下的90%以上的内容，特别是处理各种数据的函数&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;11、数组处理函数下的90%以上的内容，特别是各种操作处理函数&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;12、其它PEAR，PECL，和一些扩展类库中的80%以上的内容，特别是一些常用的类的处理&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;13、针对不同的需求，查找不同的函数库。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;14、&lt;strong&gt;测试：&lt;/strong&gt;必须熟悉使用phpunit编写测试用例完成代码的自动测试。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;15、&lt;strong&gt;管理&lt;/strong&gt;：必须熟悉使用xinc, phing等完成工程管理的常用任务，例如工程编译、生成phpdoc、生成、版本控制、自动测试。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;16、&lt;strong&gt;排错：&lt;/strong&gt;应该可以根据异常信息比较快速的定位问题的原因和大致位置。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;17、&lt;strong&gt;思想：&lt;/strong&gt;必须掌握OOP的主要要求，这样使用PHP开发的系统才能是真正的PHP系统。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;18、&lt;strong&gt;规范：&lt;/strong&gt;编写的代码必须符合流行的编码规范，这样程序的可读性才比较好。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;19、&lt;strong&gt;博学：&lt;/strong&gt;掌握OOA、OOD、YiiFramework 、Zendframework、cakephp、symfony、模板技术等流行技术，掌握软件架构设计思想、搜索引擎优化、缓存系统设计、网站负载均衡、系统性能调优等实用技术。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;20、多去看别人写的代码，php优秀的框架很多，很多人在认知上就有一个错误。认为自己能用一个框架开发一个系统了就认为自己已经熟悉这个\r\n框架了，其实会使用他并没有什么一个框架基本都有用户手册，一看基本都会使用了，要想自己技术上面提升，建议多看看这些框架的实现原理（设计模式）。&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;希望通过对PHP程序员必备技能的整理，能够帮助到你，更好的探索php程序世界。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('95', 'php程序员应具有的7种能力小结', 'jb51', '20', '70', '/Upload/article/56e291870dc52.jpg', '2016-03-11 17:36:07', 'php程序员应具有什么样的能力，才能更好的完成工作，才会有更好的发展方向呢？在中国我想您不会写一辈子代码的，那样不可能，过了黄金期，您又怎么办呢？看了本文后，希望对您有所帮助', '&lt;p&gt;php程序员应具有什么样的能力，才能更好的完成工作，才会有更好的发展方向呢？在中国我想您不会写一辈子代码的，那样不可能，过了黄金期，您又怎么办呢？看了本文后，希望对您有所帮助&lt;/p&gt;&lt;p&gt;&lt;strong&gt;一，php能力&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;1，了解阶段，您能写一些代码，因为那是在手册和google的帮助下，您才完成的。变量乱定义，N多函数不知道，做起事来很慢，想到什么写什么，代码写的比较乱，后期维护很麻烦。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，熟悉阶段，经常查函数，手册估计也看过一，二遍了，常用的函数基本上您都了解了。后 \r\n期维护给您带来了不少痛苦，您开始发现自己的代码有很多不足，开始思考如果改进自己的代码，如何站在项目的角度来规划自己的代码，而不是想到什么写什么，\r\n \r\n知道如何来减少冗余代码，使您的代码清晰，知道什么样的代码写出来让人看着舒服，基本的代码规范，已经形成。为了提高自己，会特意的去一些技术性的论坛，\r\n 学习研究。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;3，很熟悉阶段，本来我想写精通的，到现在我也不知道精通是到什么程度，也没有听到有人 \r\n说自己精通PHP的，所以就用很熟悉了。这个阶段，我想您已经从面向过程进入了面向对象。个人觉得面向对象的最大好处就是，能使整个项目功能化，模块化，\r\n \r\n后期维护，改版，升级就很方便了。没有面向对象的时候，不也一样开发吗.这个时期，您已经研究过了一种或者几种框架，结合自己的实际项目经验，在脑子里已\r\n 经能形成自己的一个框架，这个框架是最适合你的。并且能够将这个框架运用到实际的开发中去，以提高自己的开发效率。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;如果您刚写代码的时候，就有人能约束你按OOP的思想去写代码的话，那您就遇到贵人了。当不好的代码习惯养成时，在想改就不那么容易了。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;二，数据库能力&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;用php来做项目的话，用mysql是最多的了，其次是pgsql。因为他们二个是免费的。哈哈，以mysql为例&lt;br/&gt;&amp;nbsp;&lt;br/&gt;1，了解阶段，知道mysql是什么，能写一些简单的sql语句，能设计简单的表，知道如何使用数据库管理工具（如：phpmyadmin）&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，熟悉阶段，知道如何才能写出高效率的sql语句，了解索引原理，知道如何创建索引， \r\n会写一些储存过程，触发器等，能通过各种手段来分析，测试数据库，例如：利用mysqlslap来进行压力测试，通来explain来分析sql语句，通\r\n 过开启慢查询来分析哪些sql语句真正影响mysql的运行，能利用dbdesigner4,mysql \r\nworkbench为设计数据库，能在命令状态下，查询，分析mysql环境变量，来分析mysql的运行状态等等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;3，很熟悉阶段，对于各有种存储引擎的原理非常熟悉，知道通过修改配置文件来，使存储引 \r\n擎达到最优化，知道如何来优化数据库的最大连接数，知道怎么样来优化mysql的I/o瓶颈，为了项目的需要，向mysql数据库增加存储引擎或者插件，\r\n 知道如何搭建数据库集群，并监控数据库的运行状态等等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;三，html，css能力&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;php是脚本语言，我们用php大多数情况下是用来做网站的，慨然是网站，那肯定是离不开html,css&lt;br/&gt;&amp;nbsp;&lt;br/&gt;1，了解阶段，知道html标签是干什么用的，通过网络和手册能自主的写一些html，知道css是怎么回事，能在html中写一些简单的style等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，熟悉阶段，能利用css来能设计一些简单的布局，可以将css单独的写成文件，熟悉css的语法规则，以及继承性等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;3，很熟悉阶段，能够设计出很好的CSS，并且管理好这些CSS文件，尽量减少冗余代码。知道如何写出有利于搜索引擎搜索的代码，例如：title,h1,h2权重比较高的。等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;对于php程序员来说，并不一定要你去设计页面，但是给你一个页面，你要知道如何来修改CSS文件，html就不要说了肯定要掌握的。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;四，js能力&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;如果提高用户体验，是一个网站能留住人的重要标志。这个就要用到JS了&lt;br/&gt;&amp;nbsp;&lt;br/&gt;1，了解阶段，了解JS的基本语法，知道如何去调试这些程序，能写一些简单function等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，熟悉阶段，对JS的语法，函数，正则等已经熟悉了，能利用js来写一些特效，并且发 \r\n现用JS写特效，是比较累人的一件事，开始尝试jquery,prototype，并对jquery,prototype基本语法有所解，个人反对不学 \r\nJS，直接入手jquery,prototype这样的JS框架。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;3，很熟悉阶段，在框架的帮助下，能熟练的用OOP的思想的来写代码，而不是一个个 \r\nfunction累加，熟练运用jquery,prototype的ajax，或者是网上一些ajax框架，如（ajaxrequest），不在直接写 \r\nactive控件了。能够利用网络资源，来完成各种特效。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;对于大型公司来说一般都是有js程序员的，小公司基本上没有，要么交给程序员来做，要么 \r\n交给美工来做。美工一般都不是程序员，也没有编程基础，所以学JS比较吃力，但是学jquery比较容易的，因为css对html进行控制的方法，和 \r\njquery对html的控制方法基本上差不多（css，jquery的相同之处），所以有好多公司把特效交给美工来做。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;五，apache等能力&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;个人觉得，到目录为止，跑php的话用apache的人还是最多，前段时间好多网站在吵NGINX有多么多么的好，能比apache好10倍，我觉得还是亲自尝试一下比较好。以apache为例&lt;br/&gt;&amp;nbsp;&lt;br/&gt;1，了解阶段，不管是linux下，还是windows下，能够安装配置apache，知道如何添加php添模，如果面试官问你，apache为什么能解释php代码，你怎么回答呢。对apache的基本配置有所了解，对于启动中遇到的问题能够解决等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，熟悉阶段，知道如何向apache中添加新的模块，如果如何进行url重写，防盗链，进行IP限制等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;3，很熟悉阶段，知道如何利用apache来缓存图片，能利用apache来做负载均衡，并且知道利用ab命令来进行压力，通过工具对日志分析，经过分析来对apache进行优化，知道如何搭建多个虚拟主机；对apahce的常用模块都有实际操作经验等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;对apache进行监控和维护，一般是运维人员或者是项目经理来做的，个人觉得最好还是了解一点，因为这样您才不会那么容易被忽悠，对于自己将来的转型也是非常有必要的。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;六，linux系统&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;为什么要掌握linux系统呢？用php写的网站大多数运行在linux或者 \r\nfreebsd下的，掌握linux系统对自己将来的发展还是比较有好处的。借此感谢一下我的好友汪洋，是他将我带进linux世界的，进来后我才发现这\r\n \r\n里的世界很精彩，现在我基本上不用windows了，偶尔打游戏的时候会进一下，在linux下，不用担心中毒的问题，linux下的病毒很少，也不用担\r\n 心，XX和XXX扫描你的硬盘了。哈哈&lt;br/&gt;&amp;nbsp;&lt;br/&gt;1，熟悉阶段，会装linux系统，对系统的常用命令能够熟练运用等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;2，运用阶段，在linux系统下，能够安装配置apache,php,mysql,svn,memcache,squid，lvs等一些web项目必要的工具，能够通过日志分析其状态等。对shell要有所了解，并能够写一些简单的shell脚本等&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&lt;strong&gt;七，勾通能力&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;这一点非常重要，并且被越来越多的人所忽视，其实做程序员挺杯具的，根电脑打交道的时间 \r\n是最多，也许是因为这样吧，勾通的时候，是比较费劲的，也有可能是被程序的严谨性束缚了大脑，说出来的话，太专业，可能其他人听不懂的。所以平时多和他人\r\n 交流，特别是根非技术人员多勾通，多站在对方的角度来思想问题，这样的话，我想勾通起来会容易很多。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('96', 'PHP程序员基本要求和必备技能', 'jb51', '20', '68', '/Upload/article/56e291cee6b23.jpg', '2016-03-11 17:37:18', '昨天在图书馆看书，看到《电脑报》上的一篇文章，题目为《30岁不是程序员的终点》，（详情可参看《电脑报》2010年第19期，G14版数字职场 的文章）。大致内容是采访一位30岁的PHP高级程序员，他讲了一些关于PHP程序员必备知识，感觉很是受用，于是做了些笔记：', '&lt;p&gt;昨天在图书馆看书，看到《电脑报》上的一篇文章，题目为《30岁不是程序员的终点》，（详情可参看《电脑报》2010年第19期，G14版数字职场\n的文章）。大致内容是采访一位30岁的PHP高级程序员，他讲了一些关于PHP程序员必备知识，感觉很是受用，于是做了些笔记：&lt;/p&gt;&lt;p&gt;一名PHP程序员的基本要求：&lt;/p&gt;&lt;p&gt;1、精通html、CSS、JS等网站前端技术。&lt;/p&gt;&lt;p&gt;2、掌握PHP编程技术。&lt;/p&gt;&lt;p&gt;3、了解Linux及Apache、MySql&lt;/p&gt;&lt;p&gt;4、知道基本的网络原理，了解TCP/IP协议。&lt;/p&gt;&lt;p&gt;5、了解基本的软件工程知识和软件设计技术。&lt;/p&gt;&lt;p&gt;必须具备的基础知识：&lt;/p&gt;&lt;p&gt;1、LAMP技术(加强)：Linux+Apache+Mysql+PHP,是PHP网站最普遍的架构之一，也是效率最好的架构之一。&lt;/p&gt;&lt;p&gt;2、对潜在的安全漏洞有深刻的理解。如：SQL注入漏洞、字符编码循环、跨站脚本攻击(XSS)、跨站请求伪造(CSRF)。(好陌生 – -！)&lt;/p&gt;&lt;p&gt;3、掌握MVC模式：Model(模式)、View(视图）、Controller（控制器）。&lt;/p&gt;&lt;p&gt;4、其他：面向对象编程（OOP）、PHP缓存技术、PHP相关开发框架、软件重构技术、软件设计模式。&lt;/p&gt;&lt;p&gt;必须掌握的工具：&lt;/p&gt;&lt;p&gt;1、PHPMyAdmin（安装完LAMP以后第一个安装的工具）。&lt;/p&gt;&lt;p&gt;2、PHPDocumenter，会从PHP中找到所有的逻辑结构。&lt;/p&gt;&lt;p&gt;3、Zend，专业PHP集成开发环境，是php程序员首选IDE工具。&lt;/p&gt;&lt;p&gt;一些资源：PHPChina，《PHP与MySql Web应用开发核心技术》。&lt;/p&gt;&lt;p&gt;声明：这些只是本人的读书笔记，肯定和原文有出入，大家还是看原文来的更好些。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('97', 'PHP常用的缓存技术汇总', 'j51', '20', '76', '/Upload/article/56e2920b9441d.jpg', '2016-03-11 17:38:19', '这篇文章主要介绍了PHP常用的缓存技术汇总，下面所说的都是在项目中最常用的PHP缓存技术，新手同学收藏起来好好研究吧', '&lt;p&gt;这篇文章主要介绍了PHP常用的缓存技术汇总，下面所说的都是在项目中最常用的PHP缓存技术，新手同学收藏起来好好研究吧&lt;/p&gt;&lt;p&gt;&lt;strong&gt;一、数据缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;这里所说的数据缓存是指数据库查询缓存，每次访问页面的时候,都会先检测相应的缓存数据是否存在，如果不存在，就连接数据库，得到数据，并把查询结果序列化后保存到文件中，以后同样的查询结果就直接从缓存表或文件中获得。&lt;/p&gt;&lt;p&gt;用的最广的例子看Discuz的搜索功能，把结果ID缓存到一个表中，下次搜索相同关键字时先搜索缓存表。&lt;br/&gt;举个常用的方法，多表关联的时候，把附表中的内容生成数组保存到主表的一个字段中，需要的时候数组分解一下，这样的好处是只读一个表，坏处就是两个数据同步会多不少步骤，数据库永远是瓶颈，用硬盘换速度，是这个的关键点。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;二、页面缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;每次访问页面的时候，都会先检测相应的缓存页面文件是否存在，如果不存在，就连接数据库，得到数据，显示页面并同时生成缓存页面文件，这样下次访问的时候页面文件就发挥作用了。(模板引擎和网上常见的一些缓存类通常有此功能)。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;三、时间触发缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;检查文件是否存在并且时间戳小于设置的过期时间,如果文件修改的时间戳比当前时间戳减去过期时间戳大，那么就用缓存，否则更新缓存。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;四、内容触发缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;当插入数据或更新数据时，强制更新缓存。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;五、静态缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;这里所说的静态缓存是指静态化，直接生成HTML或XML等文本文件，有更新的时候重生成一次，适合于不太变化的页面，这就不说了。&lt;br/&gt;以上内容是代码级的解决方案，我直接CP别的框架，也懒得改，内容都差不多，很容易就做到，而且会几种方式一起用，但下面的内容是服务器端的缓存方案，非代码级的，要有多方的合作才能做到。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;六、内存缓存&lt;br/&gt;&lt;br/&gt;&lt;/strong&gt;Memcached是高性能的，分布式的内存对象缓存系统，用于在动态应用中减少数据库负载，提升访问速度。&lt;br/&gt;这里说下Memcached的例子：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut49396&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;?php&lt;br/&gt;$memcache = new Memcache;&lt;br/&gt;$memcache-&amp;gt;connect(&amp;#39;localhost&amp;#39;, 11211) or die (&amp;quot;Could not connect&amp;quot;);&lt;br/&gt;$version = $memcache-&amp;gt;getVersion();&lt;br/&gt;echo &amp;quot;Server&amp;#39;s version: &amp;quot;.$version.&amp;quot;\\n&amp;quot;;&lt;br/&gt;$tmp_object = new stdClass;&lt;br/&gt;$tmp_object-&amp;gt;str_attr = &amp;#39;test&amp;#39;;&lt;br/&gt;$tmp_object-&amp;gt;int_attr = 123;&lt;br/&gt;$memcache-&amp;gt;set(&amp;#39;key&amp;#39;, $tmp_object, false, 10) or die (&amp;quot;Failed to save data at the server&amp;quot;);&lt;br/&gt;echo &amp;quot;Store data in the cache (data will expire in 10 seconds)\\n&amp;quot;;&lt;br/&gt;$get_result = $memcache-&amp;gt;get(&amp;#39;key&amp;#39;);&lt;br/&gt;echo &amp;quot;Data from the cache:\\n&amp;quot;;&lt;br/&gt;var_dump($get_result);&lt;br/&gt;&lt;/p&gt;&lt;p&gt;读库的例子:&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut41110&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;?php&lt;br/&gt;$sql = &amp;#39;SELECT * FROM users&amp;#39;;&lt;br/&gt;$key = md5($sql); //memcached 对象标识符&lt;br/&gt;if ( !($datas = $mc-&amp;gt;get($key)) ) {&lt;br/&gt;// 在 memcached 中未获取到缓存数据，则使用数据库查询获取记录集。&lt;br/&gt;echo &amp;quot;n&amp;quot;.str_pad(&amp;#39;Read datas from MySQL.&amp;#39;, 60, &amp;#39;_&amp;#39;).&amp;quot;n&amp;quot;;&lt;br/&gt;$conn = mysql_connect(&amp;#39;localhost&amp;#39;, &amp;#39;test&amp;#39;, &amp;#39;test&amp;#39;);&lt;br/&gt;mysql_select_db(&amp;#39;test&amp;#39;);&lt;br/&gt;$result = mysql_query($sql);&lt;br/&gt;while ($row = mysql_fetch_object($result))&lt;br/&gt;$datas[] = $row;&lt;br/&gt;// 将数据库中获取到的结果集数据保存到 memcached 中，以供下次访问时使用。&lt;br/&gt;$mc-&amp;gt;add($key, $datas);&lt;br/&gt;} else {&lt;br/&gt;echo &amp;quot;n&amp;quot;.str_pad(&amp;#39;Read datas from memcached.&amp;#39;, 60, &amp;#39;_&amp;#39;).&amp;quot;n&amp;quot;;&lt;br/&gt;}&lt;br/&gt;var_dump($datas);&lt;/p&gt;&lt;p&gt;&lt;strong&gt;七、PHP的缓冲器、加速器&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;有eaccelerator， apc， phpa，xcache，这个这个就不说了吧，搜索一堆一堆的，自己看啦，知道有这玩意就OK。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;八、MYSQL缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;这也算非代码级的，经典的数据库就是用的这种方式，看下面的运行时间，0.09xxx之类的&lt;br/&gt;我贴段根据蓝色那家伙修改后部分my.ini吧，2G的MYISAM表可以在0.05S左右，据说他前后改了有快一年。&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut19084&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;[client]&lt;br/&gt;……&lt;br/&gt;default-character-set=gbk&lt;br/&gt;default-storage-engine=MYISAM&lt;br/&gt;max_connections=600&lt;br/&gt;max_connect_errors=500&lt;br/&gt;back_log=200&lt;br/&gt;interactive_timeout=7200&lt;br/&gt;query_cache_size=64M&lt;br/&gt;……&lt;br/&gt;table_cache=512&lt;br/&gt;……&lt;br/&gt;myisam_max_sort_file_size=100G&lt;br/&gt;myisam_max_extra_sort_file_size=100G&lt;br/&gt;myisam_sort_buffer_size=128M&lt;br/&gt;key_buffer_size=1024M&lt;br/&gt;read_buffer_size=512M&lt;br/&gt;……&lt;br/&gt;thread_concurrency=8&lt;/p&gt;&lt;p&gt;&lt;strong&gt;九、基于反向代理的Web缓存&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;如Nginx，SQUID，mod_proxy(apache2以上又分为mod_proxy和mod_cache)&lt;br/&gt;NGINX的例子：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut58605&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;nginx.conf&amp;gt;&lt;br/&gt;#user nobody;&lt;br/&gt;worker_processes 4;&lt;br/&gt;error_log logs/error.log crit;&lt;br/&gt;pid logs/nginx.pid;&lt;br/&gt;worker_rlimit_nofile 10240;&lt;br/&gt;events {&lt;br/&gt;use epoll;&lt;br/&gt;worker_connections 51200;&lt;br/&gt;}&lt;br/&gt;http {&lt;br/&gt;include mime.types;&lt;br/&gt;default_type application/octet-stream;&lt;br/&gt;sendfile on;&lt;br/&gt;keepalive_timeout 65;&lt;br/&gt;tcp_nodelay on;&lt;br/&gt;# server pool&lt;br/&gt;upstream bspfrontsvr {&lt;br/&gt;server 10.10.10.224:80 weight=1;&lt;br/&gt;server 10.10.10.221:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspimgsvr {&lt;br/&gt;server 10.10.10.201:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspstylesvr {&lt;br/&gt;server 10.10.10.202:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bsphelpsvr {&lt;br/&gt;server 10.10.10.204:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspwsisvr {&lt;br/&gt;server 10.10.10.203:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspadminsvr {&lt;br/&gt;server 10.10.10.222:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspbuyersvr {&lt;br/&gt;server 10.10.10.223:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspsellersvr {&lt;br/&gt;server 10.10.10.225:80 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bsploginsvr {&lt;br/&gt;server 10.10.10.220:443 weight=1;&lt;br/&gt;}&lt;br/&gt;upstream bspregistersvr {&lt;br/&gt;server 10.10.10.220:80 weight=1;&lt;br/&gt;}&lt;br/&gt;log_format test_com &amp;#39;$remote_addr - $remote_user [$time_local] &amp;quot;$request&amp;quot; &amp;#39;&lt;br/&gt;&amp;#39;$status $body_bytes_sent &amp;quot;$http_referer&amp;quot; &amp;quot;$http_user_agent&amp;quot; &amp;#39;;&lt;br/&gt;#--------------------------------------------------------------------&lt;br/&gt;#img.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name img.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspimgsvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/img.log test_com;&lt;br/&gt;}&lt;br/&gt;#style.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name style.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspstylesvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/style.log test_com;&lt;br/&gt;}&lt;br/&gt;#help.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name help.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bsphelpsvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/help.log test_com;&lt;br/&gt;}&lt;br/&gt;#admin.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name admin.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspadminsvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/admin.log test_com;&lt;br/&gt;}&lt;br/&gt;#buyer.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name buyer.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspbuyersvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/buyer.log test_com;&lt;br/&gt;}&lt;br/&gt;#seller.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name seller.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspsellersvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/seller.log test_com;&lt;br/&gt;}&lt;br/&gt;#wsi.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name wsi.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspwsisvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/wsi.log test_com;&lt;br/&gt;}&lt;br/&gt;#www.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name www.test.com *.test.com;&lt;br/&gt;location ~ ^/NginxStatus/ {&lt;br/&gt;stub_status on;&lt;br/&gt;access_log off;&lt;br/&gt;}&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspfrontsvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/www.log test_com;&lt;br/&gt;error_page 500 502 503 504 /50x.html;&lt;br/&gt;location = /50x.html {&lt;br/&gt;root html;&lt;br/&gt;}&lt;br/&gt;}&lt;br/&gt;#login.test.com&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:443;&lt;br/&gt;server_name login.test.com;&lt;br/&gt;ssl on;&lt;br/&gt;ssl_certificate cert.pem;&lt;br/&gt;ssl_certificate_key cert.key;&lt;br/&gt;ssl_session_timeout 5m;&lt;br/&gt;ssl_protocols SSLv2 SSLv3 TLSv1;&lt;br/&gt;ssl_ciphers ALL:!ADH:!EXPORT56:RC4+RSA:+HIGH:+MEDIUM:+LOW:+SSLv2:+EXP;&lt;br/&gt;ssl_prefer_server_ciphers on;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass https://bsploginsvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/login.log test_com;&lt;br/&gt;}&lt;br/&gt;#login.test.com for register&lt;br/&gt;server {&lt;br/&gt;listen 10.10.10.230:80;&lt;br/&gt;server_name login.test.com;&lt;br/&gt;location / {&lt;br/&gt;proxy_pass http://bspregistersvr;&lt;br/&gt;include proxy_setting.conf;&lt;br/&gt;}&lt;br/&gt;access_log logs/register.log test_com;&lt;br/&gt;}&lt;br/&gt;}&lt;br/&gt;&amp;lt;conf/proxy_setting.conf&amp;gt;&lt;br/&gt;proxy_redirect off;&lt;br/&gt;proxy_set_header Host $host;&lt;br/&gt;proxy_set_header X-Real-IP $remote_addr;&lt;br/&gt;proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;&lt;br/&gt;client_max_body_size 10m;&lt;br/&gt;client_body_buffer_size 128k;&lt;br/&gt;proxy_connect_timeout 90;&lt;br/&gt;proxy_send_timeout 90;&lt;br/&gt;proxy_read_timeout 90;&lt;br/&gt;proxy_buffer_size 4k;&lt;br/&gt;proxy_buffers 4 32k;&lt;br/&gt;proxy_busy_buffers_size 64k;&lt;br/&gt;proxy_temp_file_write_size 64k;&lt;/p&gt;&lt;p&gt;apache mod_proxy的例子:&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut40042&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;VirtualHost *&amp;gt;&lt;br/&gt;ServerName jb51.net&lt;br/&gt;ServerAdmin admin@zxsv.com&lt;br/&gt;# reverse proxy setting&lt;br/&gt;ProxyPass / http://jb51.net:8080/&lt;br/&gt;ProxyPassReverse / http://jb51.net:8080/&lt;br/&gt;# cache dir root&lt;br/&gt;CacheRoot &amp;quot;/var/www/proxy&amp;quot;&lt;br/&gt;# max cache storage&lt;br/&gt;CacheSize 50000000&lt;br/&gt;# hour: every 4 hour&lt;br/&gt;CacheGcInterval 4&lt;br/&gt;# max page expire time: hour&lt;br/&gt;CacheMaxExpire 240&lt;br/&gt;# Expire time = (now - last_modified) * CacheLastModifiedFactor&lt;br/&gt;CacheLastModifiedFactor 0.1&lt;br/&gt;# defalt expire tag: hour&lt;br/&gt;CacheDefaultExpire 1&lt;br/&gt;# force complete after precent of content retrived: 60-90%&lt;br/&gt;CacheForceCompletion 80&lt;br/&gt;CustomLog /usr/local/apache/logs/jb51_net_access_log combined&lt;br/&gt;&amp;lt;/VirtualHost&amp;gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;十、DNS轮询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;BIND是一款开放源码的DNS服务器软件，这个要说起来就大了，自己搜索去，大家知道有这个东西就行了。&lt;/p&gt;&lt;p&gt;我知道的有chinacache等大站就是这样做的，说简单点就是多服务器啦，把同一个页面或文件缓存到不同的服务器上，按南北自动解析到相关的服务器中。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('99', '常用SQL查询语句大全', 'it', '21', '47', '/Upload/article/56e7707c83714.jpg', '2016-03-15 10:16:28', '1. 查看表结构\nSQL&gt;DESC emp;\n2. 查询所有列\nSQL&gt;SELECT * FROM emp;', '&lt;p&gt;常用sql查询语句如下所示：&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #0000ff&quot;&gt;一、简单查询语句&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1. 查看表结构&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;DESC emp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2. 查询所有列&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3. 查询指定列&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT empmo, ename, mgr FROM emp;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT DISTINCT mgr FROM emp; 只显示结果不同的项&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4. 查询指定行&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE job=&amp;#39;CLERK&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5. 使用算术表达式&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename, sal*13+nvl(comm,0)&amp;nbsp; FROM emp;&lt;/p&gt;&lt;p&gt;nvl(comm,1)的意思是，如果comm中有值，则nvl(comm,1)=comm; comm中无值，则nvl(comm,1)=0。&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename, sal*13+nvl(comm,0) year_sal FROM emp; （year_sal为别名，可按别名排序）&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE hiredate&amp;gt;&amp;#39;01-1月-82&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6. 使用like操作符（%，_）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;%表示一个或多个字符，_表示一个字符，[charlist]表示字符列中的任何单一字符，[^charlist]或者[!charlist]不在字符列中的任何单一字符。&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE ename like &amp;#39;S__T%&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7. 在where条件中使用In&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE job IN (&amp;#39;CLERK&amp;#39;,&amp;#39;ANALYST&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8. 查询字段内容为空/非空的语句&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE mgr IS/IS NOT NULL;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;9. 使用逻辑操作符号&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE (sal&amp;gt;500 or job=&amp;#39;MANAGE&amp;#39;) and ename like &amp;#39;J%&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;10. 将查询结果按字段的值进行排序&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp ORDER BY deptno, sal DESC; (按部门升序，并按薪酬降序)&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: #0000ff&quot;&gt;二、复杂查询&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1. 数据分组(max,min,avg,sum,count)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT MAX(sal),MIN(age),AVG(sal),SUM(sal) from emp;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp where sal=(SELECT MAX(sal) from emp));&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELEC COUNT(*) FROM emp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2. group by（用于对查询结果的分组统计） 和 having子句（用于限制分组显示结果）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT deptno,MAX(sal),AVG(sal) FROM emp GROUP BY deptno;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT deptno, job, AVG(sal),MIN(sal) FROM emp group by deptno,job having AVG(sal)&amp;lt;2000;&lt;/p&gt;&lt;p&gt;对于数据分组的总结：&lt;/p&gt;&lt;p&gt;a. 分组函数只能出现在选择列表、having、order by子句中（不能出现在where中）&lt;/p&gt;&lt;p&gt;b. 如果select语句中同时包含有group by, having, order by，那么它们的顺序是group by, having, order by。&lt;/p&gt;&lt;p&gt;c. 在选择列中如果有列、表达式和分组函数，那么这些列和表达式必须出现在group by子句中，否则就是会出错。&lt;/p&gt;&lt;p&gt;使用group by不是使用having的前提条件。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3. 多表查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT e.name,e.sal,d.dname FROM emp e, dept d WHERE e.deptno=d.deptno order by d.deptno;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT e.ename,e.sal,s.grade FROM emp e,salgrade s WHER e.sal BETWEEN s.losal AND s.hisal;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4. 自连接（指同一张表的连接查询）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT er.ename, ee.ename mgr_name from emp er, emp ee where er.mgr=ee.empno;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5. 子查询（嵌入到其他sql语句中的select语句，也叫嵌套查询）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.1 单行子查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename FROM emp WHERE deptno=(SELECT deptno FROM emp \nwhere ename=&amp;#39;SMITH&amp;#39;);查询表中与smith同部门的人员名字。因为返回结果只有一行，所以用“=”连接子查询语句&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.2 多行子查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename,job,sal,deptno from emp WHERE job IN (SELECT \nDISTINCT job FROM emp WHERE \ndeptno=10);查询表中与部门号为10的工作相同的员工的姓名、工作、薪水、部门号。因为返回结果有多行，所以用“IN”连接子查询语句。&lt;/p&gt;&lt;p&gt;in与exists的区别： exists() \n后面的子查询被称做相关子查询，它是不返回列表的值的。只是返回一个ture或false的结果，其运行方式是先运行主查询一次，再去子查询里查询与其对\n 应的结果。如果是ture则输出，反之则不输出。再根据主查询中的每一行去子查询里去查询。in()后面的子查询，是返回结果集的，换句话说执行次序和\n exists()不一样。子查询先产生结果集，然后主查询再去结果集里去找符合要求的字段列表去。符合要求的输出，反之则不输出。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.3 使用ALL&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename,sal,deptno FROM emp WHERE sal&amp;gt; ALL (SELECT sal\n FROM emp WHERE deptno=30);或SQL&amp;gt;SELECT ename,sal,deptno FROM emp \nWHERE sal&amp;gt; (SELECT MAX(sal) FROM emp WHERE \ndeptno=30);查询工资比部门号为30号的所有员工工资都高的员工的姓名、薪水和部门号。以上两个语句在功能上是一样的，但执行效率上，函数会高\n 得多。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.4 使用ANY&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename,sal,deptno FROM emp WHERE sal&amp;gt; ANY (SELECT sal\n FROM emp WHERE deptno=30);或SQL&amp;gt;SELECT ename,sal,deptno FROM emp \nWHERE sal&amp;gt; (SELECT MIN(sal) FROM emp WHERE \ndeptno=30);查询工资比部门号为30号的任意一个员工工资高（只要比某一员工工资高即可）的员工的姓名、薪水和部门号。以上两个语句在功能上是\n 一样的，但执行效率上，函数会高得多。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.5 多列子查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT * FROM emp WHERE (job, deptno)=(SELECT job, deptno FROM emp WHERE ename=&amp;#39;SMITH&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.6 在from子句中使用子查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;SQL&amp;gt;SELECT emp.deptno,emp.ename,emp.sal,t_avgsal.avgsal FROM \nemp,(SELECT emp.deptno,avg(emp.sal) avgsal FROM emp GROUP BY emp.deptno)\n t_avgsal where emp.deptno=t_avgsal.deptno AND \nemp.sal&amp;gt;t_avgsal.avgsal ORDER BY emp.deptno;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.7 分页查询&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;数据库的每行数据都有一个对应的行号，称为rownum.&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT a2.* FROM (SELECT a1.*, ROWNUM rn FROM (SELECT * FROM emp ORDER BY sal) a1 WHERE ROWNUM&amp;lt;=10) a2 WHERE rn&amp;gt;=6;&lt;/p&gt;&lt;p&gt;指定查询列、查询结果排序等，都只需要修改最里层的子查询即可。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.8 用查询结果创建新表&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;CREATE TABLE mytable (id,name,sal,job,deptno) AS SELECT empno,ename,sal,job,deptno FROM emp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.9 合并查询（union 并集, intersect 交集, union all 并集+交集, minus差集)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;SELECT ename, sal, job FROM emp WHERE sal&amp;gt;2500 \nUNION(INTERSECT/UNION ALL/MINUS) SELECT ename, sal, job FROM emp WHERE \njob=&amp;#39;MANAGER&amp;#39;;&lt;/p&gt;&lt;p&gt;合并查询的执行效率远高于and,or等逻辑查询。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;5.10 使用子查询插入数据&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;CREATE TABLE myEmp(empID number(4), name varchar2(20), sal number(6), job varchar2(10), dept number(2)); 先建一张空表；&lt;/p&gt;&lt;p&gt;SQL&amp;gt;INSERT INTO myEmp(empID, name, sal, job, dept) SELECT empno, \nename, sal, job, deptno FROM emp WHERE deptno=10; \n再将emp表中部门号为10的数据插入到新表myEmp中，实现数据的批量查询。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.11 使用了查询更新表中的数据&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;SQL&amp;gt;UPDATE emp SET(job, sal, comm)=(SELECT job, sal, comm FROM emp where ename=&amp;#39;SMITH&amp;#39;) WHERE ename=&amp;#39;SCOTT&amp;#39;;&lt;/p&gt;&lt;p&gt;以上所述就是本文给大家介绍的日常收集常用SQL查询语句大全，希望大家喜欢。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('100', 'mysql_fetch_row,mysql_fetch_array,mysql_fetch_assoc的区别', 'jb51', '21', '49', '/Upload/article/56e78230ea04e.jpg', '2016-03-15 11:32:00', '一直以来，有很多初学者搞不懂这些Mysql中从查询结果集中取得数据的函数之间有什么区别，今天我就来秀一把，在秀之前先给大家一段PHP实例\r\n复制代码 代码如下:', '&lt;p&gt;一直以来，有很多初学者搞不懂这些Mysql中从查询结果集中取得数据的函数之间有什么区别，今天我就来秀一把，在秀之前先给大家一段PHP实例&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut39330&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&amp;lt;?php &lt;br/&gt;$link=mysql_connect(&amp;#39;localhost&amp;#39;,&amp;#39;root&amp;#39;,”); &lt;br/&gt;mysql_select_db(&amp;#39;abc&amp;#39;,$link); &lt;br/&gt;$sql = “select * from book”; &lt;br/&gt;$result = mysql_query($sql); &lt;br/&gt;while($row = mysql_fetch_row($result)) &lt;br/&gt;{ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].&amp;#39;&amp;lt;br&amp;gt;&amp;#39;; &lt;br/&gt;} &lt;br/&gt;$result = mysql_query($sql); &lt;br/&gt;while($row = mysql_fetch_array($result)) &lt;br/&gt;{ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].&amp;#39;&amp;lt;br&amp;gt;&amp;#39;; &lt;br/&gt;} &lt;br/&gt;$result = mysql_query($sql); &lt;br/&gt;while($row = mysql_fetch_object($result)) &lt;br/&gt;{ &lt;br/&gt;echo $row-&amp;gt;cid.&amp;#39;::&amp;#39;.$row-&amp;gt;title.”&amp;lt;br&amp;gt;”; &lt;br/&gt;} &lt;br/&gt;$result = mysql_query($sql); &lt;br/&gt;while($row = mysql_fetch_assoc($result)) &lt;br/&gt;{ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].&amp;#39;&amp;lt;br&amp;gt;&amp;#39;; &lt;br/&gt;} &lt;br/&gt;?&amp;gt; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;分析： &lt;br/&gt;mysql_fetch_row，\r\n这个函数是从结果集中取一行作为枚举数据，从和指定的结果标识关联的结果集中取得一行数据并作为数组返回。每个结果的列储存在一个数组的单元中，偏移量从\r\n 0 开始。 注意，这里是从0开始偏移，也就是说不能用字段名字来取值，只能用索引来取值，所以如下代码是取不到值的： &lt;br/&gt;while($row = mysql_fetch_row($res)){ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].”; &lt;br/&gt;} //这里的$row[&amp;#39;cid&amp;#39;] 取不到值。 &lt;br/&gt;mysql_fetch_array，从结果集中取得一行作为关联数组，或数字数组，或二者兼有，除了将数据以数字索引方式储存在数组中之外，还可以将数据作为关联索引储存，用字段名作为键名。 也就是说他得到的结果像数组一样，可以用key或者索引来取值，所以 &lt;br/&gt;while($row = mysql_fetch_array($res)){ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].”; &lt;br/&gt;}//这里$row[&amp;#39;cid&amp;#39;],$row[1]都能得到相应的值。 &lt;br/&gt;mysql_fetch_object,顾名思义，从结果集中取得一行作为对象，并将字段名字做为属性。所以只有这样才能取到值 &lt;br/&gt;while($row = mysql_fetch_object($res)){ &lt;br/&gt;echo $row-&amp;gt;cid.&amp;#39;::&amp;#39;.$row-&amp;gt;title.”&amp;quot;; &lt;br/&gt;} &lt;br/&gt;mysql_fetch_assoc，从结果集中取得一行作为关联数组，也就是说这个函数不能像mysql_fetch_row那样用索引来取值，只能用字段名字来取，所以 &lt;br/&gt;while($row = mysql_fetch_assoc($res)){ &lt;br/&gt;echo $row[&amp;#39;cid&amp;#39;].&amp;#39;::&amp;#39;.$row[1].”; &lt;br/&gt;} //$row[1]这样是取不到值的 &lt;br/&gt;补充一点： &lt;br/&gt;mysql_fetch_array函数是这样定义的：array mysql_fetch_array ( resource result [, int result_type])，返回根据从结果集取得的行生成的数组，如果没有更多行则返回 FALSE。 &lt;br/&gt;mysql_fetch_array() 中可选的第二个参数 result_type 是一个常量，可以接受以下值：MYSQL_ASSOC，MYSQL_NUM 和 MYSQL_BOTH。其中： &lt;br/&gt;1、mysql_fetch_assoc($result)==mysql_fetch_array($result,MYSQL_ASSOC); &lt;br/&gt;2、mysql_fetch_row($result)==mysql_fetch_array($result,MYSQL_NUM); &lt;br/&gt;所\r\n以mysql_fetch_array()函数在某种程度上可以算是mysql_fetch_row()与 \r\nmysql_fetch_assoc()的集合。另外，mysql_fetch_array()另外还有MYSQL_BOTH参数，将得到一个同时包含关\r\n 联和数字索引的数组。 &lt;br/&gt;在来说句 $row = $db-&amp;gt;fetch_array($query)； &lt;br/&gt;$db是人数据库操作\r\n类，$db-&amp;gt;fetch_array($query)，fetch_array($query)是那个db类里的方法，$row = \r\n$db-&amp;gt;fetch_array($query)这句的意思是从记录集$query中得到数据库中的一行记录。 &lt;br/&gt;不用类可这样实现 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a style=&quot;CURSOR: pointer&quot; class=&quot;copybut&quot; id=&quot;copybut31044&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;p&gt;&lt;br/&gt;$conn=@mysql_connect($host,$user,$pass); &lt;br/&gt;@mysql_select_db($database,$conn); &lt;br/&gt;$query=mysql_query($sql); &lt;br/&gt;while($row=mysql_fetch_array($query)){ &lt;br/&gt;$rows[]=$row; &lt;br/&gt;}&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('101', '浅谈域名后缀对于SEO有影响', 'it', '1', '42', '/Upload/article/56e7ccf445733.jpg', '2016-03-15 16:51:00', '静态路径是要优于动态路径的，为什么呢？静态路径短，层次明了，利于收录。静态地址参数少，蜘蛛也更容易爬取。', '&lt;p&gt;二级域名比栏目页哪个更好呢？&lt;br/&gt;　　静态路径是要优于动态路径的，为什么呢？静态路径短，层次明了，利于收录。静态地址参数少，蜘蛛也更容易爬取。&lt;br/&gt;　　基本上来说，除特殊域名和免费域名外域名后缀对于SEO来说是没有影响的，也就是不管你应用.com或.net或者.cn,包括.cc都是一样的，他\r\n本身并没有特殊的权重值。但是，有很多人喜欢去注册一些免费域名，这些免费域名就如二级域名一样是不受搜索引擎欢迎的，而且收录时间要远远长于等级域名的\r\n收录时间。&lt;br/&gt;　　1.多音节拼音域名是首先，利于网站推广&lt;br/&gt;　　一、域名对SEO的影响：&lt;br/&gt;　　在我国，懂拼音的人比懂英文的多，就连百度这样的网站也钟情于拼音域名，因为英文比拼音更难进行口碑传播。即使你懂英文，有些组合类的个性英文也容易\r\n读错音，往往会出现别人想介绍网站时都不知道怎么样叫网站名的尴尬情形。有的人喜欢用简写的域名，简写是没有用的，我们要选取完全匹配词。最好的域名是，\r\n用户说一次就可以记得住，下次还能找得到。比如校内、海内、百度、淘宝等等。域名尽可能的包含关键词，但一定要精短。百度是能够轻松识别域名当中包含的关\r\n键词或者拼音。&lt;br/&gt;　　2.域名后缀对于SEO有影响&lt;br/&gt;　　当内容不多的时候，最好不要用二级域名，子目录的信用度要高于二级域名，二级域名的权重是独立计算的，优化的时候会增加人力成本。&lt;br/&gt;　　二、路径对SEO的影响：&lt;br/&gt;　　1.路径对于用户是有引导作用的。&lt;br/&gt;　　最好采用完全匹配，且完全匹配不能大于4个汉字的全拼。&lt;br/&gt;　　2.静态路径与动态路径哪个更有利于排名？&lt;br/&gt;　　3.一个网站里面要采用统一的路径&lt;/p&gt;', '0');
INSERT INTO `ni_article` VALUES ('102', '尤果赵伊彤妩媚多姿令男人欲罢不能', '网络', '22', '56', '/Upload/article/56e7ff1847eab.jpg', '2016-03-15 20:07:22', '尤果赵伊彤妩媚多姿令男人欲罢不能', '&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h2&gt;尤果赵伊彤妩媚多姿令男人欲罢不能&lt;/h2&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/1.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图1)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图1)&quot; src=&quot;/Upload/ueditor/img/1458043569122955.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/2.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图2)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图2)&quot; src=&quot;/Upload/ueditor/img/1458043569168184.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/3.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图3)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图3)&quot; src=&quot;/Upload/ueditor/img/1458043569866500.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/4.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图4)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图4)&quot; src=&quot;/Upload/ueditor/img/1458043570347615.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/5.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图5)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图5)&quot; src=&quot;/Upload/ueditor/img/1458043570804505.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/6.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图6)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图6)&quot; src=&quot;/Upload/ueditor/img/1458043570107476.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/7.jpg&quot; title=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图7)&quot;&gt;&lt;img alt=&quot;尤果赵伊彤妩媚多姿令男人欲罢不能(图7)&quot; src=&quot;/Upload/ueditor/img/1458043570381193.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://img1.mm131.com/pic/2306/8.jpg&quot; title=&quot;尤果赵伊