INSERT INTO `ni_article` VALUES ('116', '再见，漳师 &lt;木棉篇&gt; ', '达子', '17', '56', '/Upload/article/56e8b84c4b37e.jpg', '2016-03-16 09:35:08', '最是人间留不住 朱颜辞镜花辞树\n璀璨不过一季，韶华不过四年\n且行且珍惜', '&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt;最是人间留不住&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt; &lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt;朱颜辞镜花辞树&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt;璀璨不过一季，韶华不过四年&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt; &lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt;且行且珍惜&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;font-weight:bold&quot;&gt;&amp;nbsp;...&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma;color:aqua&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin:0&quot;&gt;&lt;img data-src=&quot;http://r.photo.store.qq.com/psb?/V14Rn7Yq084Tl5/OvrvB3otfwqeU9RnXPBOxH5N979XVhdhuU0XI4p1Ty8!/o/dOhO*l8IGgAA&amp;amp;ek=1&amp;amp;kp=1&amp;amp;pt=0&amp;amp;bo=IAMWAugDmwIBAGI!&amp;amp;su=118131697&amp;amp;sce=0-12-12&amp;amp;rf=2-9&quot; data-img-idx=&quot;0&quot; alt=&quot;图片&quot; src=&quot;http://r.photo.store.qq.com/psb?/V14Rn7Yq084Tl5/OvrvB3otfwqeU9RnXPBOxH5N979XVhdhuU0XI4p1Ty8%21/o/dOhO*l8IGgAA&amp;ek=1&amp;kp=1&amp;pt=0&amp;bo=IAMWAugDmwIBAGI%21&amp;su=118131697&amp;sce=0-12-12&amp;rf=2-9&quot; style=&quot;width:800px;height:534px&quot; width=&quot;-20&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin:0&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin:0&quot;&gt;&lt;img data-src=&quot;http://r.photo.store.qq.com/psb?/V14Rn7Yq084Tl5/*HYIv0ipxNylpptrbuVUCbn1fRIIoqO9nacHint9xHw!/o/dILc*F8xGgAA&amp;amp;ek=1&amp;amp;kp=1&amp;amp;pt=0&amp;amp;bo=6APPAegDzwEBACc!&amp;amp;su=16018417&amp;amp;sce=0-12-12&amp;amp;rf=2-9&quot; data-img-idx=&quot;1&quot; 